//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼 클릭시 처리
//---------------------------------------------------------------------------
scwin.lfn_Search = function(e) { 
	//console.log("[/CBSC068M.xml] [lfn_Search()] ==> [조회버튼 클릭시 처리] [TEST_01]" ); 

	util.eventAsyncCall(this, scwin.lfn_Search_Sync, arguments);		// 비동기 방식 호출 
}

//---------------------------------------------------------------------------
// function 명 : lfn_Search_Sync
// 내용        : 비동기 방식 호출 처리
//---------------------------------------------------------------------------
scwin.lfn_Search_Sync = function(e) {
	//console.log("[/CBSC068M.xml] [lfn_Search()] ==> [조회 버튼 클릭 Event 처리]" );

    if (scwin.chkEssnInpt() == false) {
        return;
    }

    //세금우대 조회전에 가입설계 동의 여부 체크 확인 로직 추가 (2014.03)
    if (scwin.lfn_getCUSAA42Info(msk_gsCrtorCd.getValue()) == "Y") {
        wprom.alert(msk_gsCrtorCd.getValue() + "님의 가입설계 동의여부를 확인하세요");
        return;
    }

    if (cmb_sTaxPrfrFlag.getValue() == "6") {	 // 과세종류가 연금저축일 경우  
    	//console.log("[/CBSC068M.xml] [lfn_Search()] ==> [01. 과세종류가 연금저축일 경우] [TES_03_1]" );

 		div1.setSrc("/ui/cbsc/CBSC148S.xml") ;   // 과세종류가 연금저축일 경우 
 		var nCrtorCdFlag = "";		// 주빈번호, 사업자번호

		wconv.clear(Ds_TaxPrfrLmit);
		wconv.clear(Ds_SendTax);

   		if (wconv.length(msk_gsCrtorCd.getValue()) == 10) {
           	nCrtorCdFlag = "2"; 	//사업자번호 2
		} else if (wconv.length(msk_gsCrtorCd.getValue()) == 13) {
	    	nCrtorCdFlag = "1";	 	//주빈번호 1
		} 

		var MethodObj = new callService("com.inswave.longterm.pay.ptm.task.GEG430Task", "selectTaxPrfrLmit");  	// 02. 과세종류가 연금저축일 경우조회 Obj 
		MethodObj.setId("selectTaxPrfrLmit");			// ID 셋팅
		MethodObj.setParam("sCrtorCd", msk_gsCrtorCd.getValue());				// Param 셋팅(계약자코드)
		MethodObj.setParam("sTaxPrfrFlag", cmb_sTaxPrfrFlag.getValue());		// Param 셋팅(과세종류 콤보)
//		MethodObj.setParam("sTermNo", "5000035"); 				// Param 셋팅(입력지점)  				// TEST @@@ ===> 
//		MethodObj.setParam("sTrmnUserID", "2140026");		// Param 셋팅(단말기 조작자 ID) 	// TEST @@@ ===>  
		MethodObj.setParam("sTermNo", util.getGdsValue("gUserCenterCode")); 	// Param 셋팅(입력지점)   
		MethodObj.setParam("sTrmnUserID", util.getGdsValue("gUserID"));			// Param 셋팅(단말기 조작자 ID)  
		MethodObj.setParam("sTrmnUserNm", ""); 									// Param 셋팅(단말기 조작자 성명) 
		MethodObj.setParam("nCrtorCd", msk_gsCrtorCd.getValue());			// Param 셋팅(계약자코드)
		MethodObj.setParam("nSvKind", "36");											// Param 셋팅(계조회 저축 종류약자코드)
		MethodObj.setParam("nCrtorCdFlag", nCrtorCdFlag);						// Param 셋팅(주빈번호, 사업자번호) 
		MethodObj.setUrl("/contract/CBSC068MController.do");  					// 서버단 호출 URL
		MethodObj.setXPath("//hashtable/data[@hashkey='sendDoc']/TAX0021");		// XPath
		MethodObj.addXPath("//*/vector/data/GEG430");	// XPath 
		MethodObj.addXPath("//WARNING");	// XPath( 
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.send();		// 최종 전송 
		//console.log("[/CBSC068M.xml] [lfn_selectMediCover()] ==> [01. 과세종류가 연금저축일 경우, 전송_완료] [TEST_91]");  

		//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_SendTax.setJSON(_tranSync.getDsJson("TAX0021"));		// DS 셋팅(조회 결과)  
		Ds_TaxPrfrLmit.setJSON(_tranSync.getDsJson("GEG430"));		// DS 셋팅(조회 결과)  
		Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  	// Warning DS 셋팅(조회 결과) 
		//console.log("[/CBSC068M.xml] [lfn_selectMediCover()] ==> [I/F 조회 완료] [02. 연금저축일 경우] [Ds_TaxPrfrLmit(계약정보Ds), 갯수]"+ Ds_TaxPrfrLmit.getRowCount() ); 
		//console.log("[/CBSC068M.xml] [lfn_selectMediCover()] ==> [I/F 조회 완료] [02. 연금저축일 경우] [Ds_SendTax(계약상세정보Ds), 갯수]"+ Ds_SendTax.getRowCount() ); 	

    } else { 	 // 과세종류가 연금저축이 아닐 경우
    	//console.log("[/CBSC068M.xml] [lfn_Search()] ==> [02. 과세종류가 연금저축이 아닐 경우] [TES_03_2]" );

		div1.setSrc("/ui/cbsc/CBSC147S.xml") ;   // 과세종류가 연금저축이 아닐 경우 
        var nCrtorCdFlag = "";		// 주빈번호, 사업자번호

		wconv.clear(Ds_TaxPrfrLmit);
		wconv.clear(Ds_SendTax);

   		if (wconv.length(msk_gsCrtorCd.getValue()) == 10) {
           	nCrtorCdFlag = "2"; 	//사업자번호 2
		} else if (wconv.length(msk_gsCrtorCd.getValue()) == 13) {
	    	nCrtorCdFlag = "1";	 	//주빈번호 1
		}

	   	var MethodObj = new callService("com.inswave.longterm.pay.ptm.task.GEG430Task", "selectTaxPrfrLmit");  	// 01. 과세종류가 연금저축이 아닐 경우조회 Obj 
		MethodObj.setId("selectTaxPrfrLmit");			// ID 셋팅
		MethodObj.setParam("sCrtorCd", msk_gsCrtorCd.getValue());				// Param 셋팅(계약자코드)
		MethodObj.setParam("sTaxPrfrFlag", cmb_sTaxPrfrFlag.getValue());		// Param 셋팅(과세종류 콤보)
//		MethodObj.setParam("sTermNo", "5000035"); 				// Param 셋팅(입력지점)  				// TEST @@@ ===> 
//		MethodObj.setParam("sTrmnUserID", "2140026");		// Param 셋팅(단말기 조작자 ID) 	// TEST @@@ ===>  
		MethodObj.setParam("sTermNo", util.getGdsValue("gUserCenterCode")); 	// Param 셋팅(입력지점)   
		MethodObj.setParam("sTrmnUserID", util.getGdsValue("gUserID"));			// Param 셋팅(단말기 조작자 ID)  
		MethodObj.setParam("sTrmnUserNm", ""); 									// Param 셋팅(단말기 조작자 성명) 
		MethodObj.setParam("nCrtorCd", msk_gsCrtorCd.getValue());			// Param 셋팅(계약자코드)
		MethodObj.setParam("nCrtorCdFlag", nCrtorCdFlag);						// Param 셋팅(주빈번호, 사업자번호) 
		MethodObj.setUrl("/contract/CBSC068MController.do");  					// 서버단 호출 URL
		MethodObj.setXPath("//hashtable/data[@hashkey='sendDoc']/TAX0010");		// XPath
		MethodObj.addXPath("//*/vector/data/GEG430");	// XPath 
		MethodObj.addXPath("//WARNING");	// XPath( 
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.send();		// 최종 전송 
		//console.log("[/CBSC068M.xml] [lfn_selectMediCover()] ==> [01. 과세종류가 연금저축이 아닐 경우, 전송_완료] [TEST_91]");  

		//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_SendTax.setJSON(_tranSync.getDsJson("TAX0010"));		// DS 셋팅(조회 결과)  
		Ds_TaxPrfrLmit.setJSON(_tranSync.getDsJson("GEG430"));		// DS 셋팅(조회 결과)  
		Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  	// Warning DS 셋팅(조회 결과) 
		//console.log("[/CBSC068M.xml] [lfn_selectMediCover()] ==> [I/F 조회 완료] [01. 연금저축이 아닐 경우] [Ds_TaxPrfrLmit(계약정보Ds), 갯수]"+ Ds_TaxPrfrLmit.getRowCount() ); 
		//console.log("[/CBSC068M.xml] [lfn_selectMediCover()] ==> [I/F 조회 완료] [01. 연금저축이 아닐 경우] [Ds_SendTax(계약상세정보Ds), 갯수]"+ Ds_SendTax.getRowCount() ); 	
   } 

	if (wconv.length(Ds_Warning.getCellData(0, "msg")) > 0) {
        wprom.alert(Ds_Warning.getCellData(0, "msg"));
    } else {  
        if ((Ds_TaxPrfrLmit.getRowCount() == 0) && (Ds_SendTax.getRowCount() == 0)) {
            wprom.alert("해당 데이터가 없습니다."); 

        } else {  
  			Ds_SendTax.setCellData(0, "sCrtorName", wconv.trim(wconv.replace(Ds_SendTax.getCellData(0, "sCrtorName"), "\u3000", ""))); 
           	Ds_SendTax.setRowPosition(0);	 // Row 위치 0번째로 지정

            if (cmb_sTaxPrfrFlag.getValue() == "6") {	 // 과세종류가 연금저축일 경우  
            	scwin.getComCodeS_div2();		 // 공통코드 조회
            } else {
            	scwin.getComCodeS_div();		 // 공통코드 조회
            }

       		// gfn_LogCreditInfoVt("", "", "S", msk_gsCrtorCd.getValue());		    //개인정보 로그처리(AS-IS)
       		gfn_LogCreditInfoVt(Ds_SendTax, "", "S", msk_gsCrtorCd.getValue());		//개인정보 로그처리(TO-BE): Ds_SendTax 추가(수정: 2017.11.02, by 진태만 
        }
    } 
};

//---------------------------------------------------------------------------
// function 명 : btn_search_OnClick
// 내용        : 계약자코드(고객) 팝업  선택시 Event처리
//---------------------------------------------------------------------------
scwin.btn_search_OnClick = function(obj) {
	console.log("[/CBSC068M.xml] [btn_search_OnClick()] ==> [계약자코드(고객) 팝업] [TEST_01]" );  

	var promise = Promise.resolve();

	var indata={
 				  "sPsnCorpFlag": "1"
 				 ,"sCdNameFlag"	: "Cd"
 				 ,"sSerchKey"	: msk_gsCrtorCd.getValue()
			};  

    promise = promise.then(function(result) { 
		var popInfo = {
				"width"     : 660,
				"height"    : 552, 
				"indata"    : indata,
				"callback"  : "",
				"title"     : "고객검색"
			};
			return wprom.popupOpen("/ui/cbso/CBSO004P.xml", popInfo);	// 고객 검색 팝업 처리
    });

    promise = promise.then(function(result) { 
        var pObj = wprom.getReturnData();		

		if(wconv.length(pObj)>0) {
			var resultArr = pObj.split("^");		
			msk_gsCrtorCd.setValue(resultArr[0]);		// 계약자코드 
		}			 
    });  
};

