	if(Ds_SendTax.getRowCount() > 0) {   //Ds_SendTax가 존재하면  
		var sArg = "";
		var sSvKind = "";

		if(cmb_sTaxPrfrFlag.getValue() == "2") { 	// 과세종류가 비과세 저축일 경우
			sSvKind = "58";
		}

		if(cmb_sTaxPrfrFlag.getValue() == "6") {	// 과세종류가 연금일 경우
			sSvKind = "36";
		}

		var oDsJson=[{"PrigId":"CBSC068M", "nCrtorCd": msk_gsCrtorCd.getValue()
								, "nSvKind": sSvKind 
		 					}];  		// 필드 Json에 담기(gDs_LinkArg 함수 사용)
  		//console.log("[/CBSC068M.xml] [btn_FinaOrgnInsSpec_OnClick()] ==>  [msk_gsCrtorCd.getValue()]"+ msk_gsCrtorCd.getValue() +"[과세종류]"+ cmb_sTaxPrfrFlag.getValue() +"[sSvKind]"+ sSvKind );

       	window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");  

			var popInfo = {"width":807,
						   "height":608, 
						   "oDsJson"    : oDsJson,	 	// 자식으로 보낼 부모 Ds OBJECT 리스트   // 수정(추가) 2017.10.31, by 진태만   
						   "title"     : "세금우대전금융기관 가입내역 조회"
						  };
			 //var Rst = Dialog("pay_ptm::G440_Pop.xml", gDs_LinkArg,780, 580);
			return wprom.popupOpen("/ui/cbsc/CBSC067P.xml", popInfo);	// 세금우대전금융기관 가입내역 조회 팝업 
	} 

 	//console.log("[/CBSC068M.xml] [btn_FinaOrgnInsSpec_OnClick()] ==>  [T_end]" );
};

