//---------------------------------------------------------------------------
// function 명 : lfn_cmb_linkMenu_OnChanged
// 내용        : 하단 화면이동 
//---------------------------------------------------------------------------
scwin.lfn_cmb_linkMenu_OnChanged = function(obj, strCode, strText, nOldIndex, nNewIndex) {
	console.log("[/CBSC068M.xml] [lfn_cmb_linkMenu_OnChanged()] ==> [하단 화면이동] [TEST_01]"); 

    var sCrNo = Ds_TaxPrfrLmit.getCellData(nRow, "sCrNo");
    gds.gDs_LinkArg.removeAll();
    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
    /*수작업 변환 개발자 가이드(테스트 중)
    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
    util.insertColumn(gds.gDs_LinkArg, [
        [
            "sCrNo",
            "sCrNo",
            "text"
        ]
    ]);
    util.insertColumn(gds.gDs_LinkArg, [
        [
            "sScrnId",
            "sScrnId",
            "text"
        ]
    ]);
    gds.gDs_LinkArg.setCellData(0, "sCrNo", sCrNo);
    gds.gDs_LinkArg.setCellData(0, "sScrnId", "G430");
    gfn_OpenLinkPage(strCode);
};

//---------------------------------------------------------------------------
// function 명 : lfn_getCUSAA42Info()
// 내용        : 가입설계 동의여부 확인 
//---------------------------------------------------------------------------
scwin.lfn_getCUSAA42Info = function(insrdID) {
	//console.log("[/CBSC068M.xml] [lfn_getCUSAA42Info()] ==> [가입설계 동의여부 확인]" );

	//신용정보 동의 23조 동의여부 확인	 
    wconv.clear(Ds_Search42);

    var RejectYN = "";    // 가입설계 동의 여부 체크 

   	var MethodObj = new callService("com.inswave.customer.task.CustTask", "getCUSAA42Info");  	// 01. 가입설계 동의 여부 조회 Obj  
	MethodObj.setId("getCUSAA42Info");		// ID 셋팅
	MethodObj.setParam("sCustNo", insrdID);				// Param 셋팅 
	MethodObj.setParam("sProductType", "3");			// Param 셋팅 
	MethodObj.setUrl("/contract/CBSC068MController.do");  		// 서버단 호출 URL
	MethodObj.setXPath("//*/CUSAA42");		// XPath 
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	//console.log("[/CBSC068M.xml] [btn_search_OnClick()] ==> [01. 가입설계 동의 여부 조회, 전송_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_Search42.setJSON(_tranSync.getDsJson("CUSAA42"));		// DS 셋팅(조회 결과)  
	//console.log("[/CBSC068M.xml] [btn_search_OnClick()] ==> [DS_갯수]"+ Ds_Search42.getRowCount() +"[Ds_Search42]"+ WebSquare.xml.serialize(Ds_Search42.getAllXML()) ); 

    if (Ds_Search42.getCellData(0, "result") > 0) {
        /*if(  DsWs_Search42.GetColumn(0, "sAgmReq") == "Y" ){	//2016.09.05(N1609-00035):기가입자는 유효기간 지나도 세금우대한도 조회 시 설계동의 필요하지않음
        	RejectYN = "Y";//유효일자가 지난경우 다시 받기. 해당 칼럼이 다 체크해줌
        }*/
    } else {
        RejectYN = "Y"; //가입동의 거부
    }
	//console.log("[/CBSC068M.xml] [btn_search_OnClick()] ==> [가입설계 동의여부 확인] [RejectYN]"+ RejectYN );

    return RejectYN;
};

