scwin.setCrtorCd = function(sCrtorCd, sTaxPrfrFlag, sScanObjtYn) {
	//console.log("[/CBSC068M.xml] [setCrtorCd()] ==> [계약번호받았을때 처리] [TEST_01]"); 

    scwin.getComCodeS(); //공통코드 조회

    msk_gsCrtorCd.setValue(sCrtorCd);
    cmb_sTaxPrfrFlag.setValue(sTaxPrfrFlag);
    sScanObjtYnGlb = sScanObjtYn;

    scwin.lfn_Search();
};

//**************************************************************************************************
// Grid 칼럼 expression  처리 함수
//**************************************************************************************************
scwin.lfn_getColumnValue = function(data, formattedData, rowIndex, colIndex) { 
	//console.log("[/CBSC068M.xml] [lfn_getColumnValue()] ==> [TEST_01]" ); 
 	//console.log("[/CBSC068M.xml] [lfn_getColumnValue()] ==> [TEST_02] [grd_TaxPrfrLmit.getColumnID(colIndex)]"+ grd_TaxPrfrLmit.getColumnID(colIndex) +"[rowIndex]"+ rowIndex +"[colIndex]"+ colIndex  ); 

	if (grd_TaxPrfrLmit.getColumnID(colIndex)  == "sInsurTerm") {  		// Grid 칼럼이 '보험기간' 이면  
		//console.log("[/CBSC068M.xml] [lfn_getColumnValue2()] ==> [Grid 칼럼이 '보험기간' 이면] [TEST_01]" );  
		var sInsurStdt = Ds_TaxPrfrLmit.getCellData(rowIndex, "sInsurStdt");   // 보험기간From
		var sInsurEndDate = Ds_TaxPrfrLmit.getCellData(rowIndex, "sInsurEndDate");   // 보험기간To 
		formattedData = wconv.substr(sInsurStdt, 0, 4) +"-"+ wconv.substr(sInsurStdt, 4, 2) +"-"+  wconv.substr(sInsurStdt, 6, 2)
		 		 +" ~ "+ wconv.substr(sInsurEndDate, 0, 4) +"-"+ wconv.substr(sInsurEndDate, 4, 2) +"-"+  wconv.substr(sInsurEndDate, 6, 2);	// 보험기간 
	 	//console.log("[/CBSC068M.xml] [lfn_getColumnValue2()] ==> [보험기간] [formattedData]"+ formattedData +"[sInsurStdt]"+ sInsurStdt +"[sInsurEndDate]"+ sInsurEndDate  ); 
	 }

	return formattedData;
};
