             //sTmp += "^" + nXcptPftbpChngSeqPop;  
         } 
        sPopupFlag = "";

    	var rtnJSON = {};  	//parent접근불가. returnParam으로 변경

     	rtnJSON = {	"edt_sB110PopValue" : sTmp,   
