//---------------------------------------------------------------------------
// function 명 : lfn_IVR_CallBack
// 내용        :  IVR 콜백함수
//---------------------------------------------------------------------------
scwin.lfn_IVR_CallBack = function(id, nRow, val) { 
    if (wconv.length(val) > 0) {
        edt_Cd.setValue(val);
        Ds_cd.setCellData(0, "sCd", val);
    }
};

scwin.cmb_sAddrFlagCd_OnChanged = function(obj, strCode, strText, nOldIndex, nNewIndex) {
    //edt_add.setValue("");
    //console.log("[/CBSC069P.xml] [cmb_sAddrFlagCd_OnChanged()] ==> [TEST_01] [edt_add]"+ edt_add.getValue() );

    if (cmb_sAddrFlagCd.getValue() == sMailFlagCd) {
        edt_add.setValue(sAdrs1Pop + " " + sAdrsAddPop);
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_A070CallBack
// 내용        :  (수익자 팝업에서 해당 함수 호출하여 값 세팅) 
//---------------------------------------------------------------------------
scwin.lfn_A070_CallBack = function() {
    switch (fAgntFlag1.getValue()) {
        case "ed_Mangi":	// 만기 수익자 이면
            if (wconv.length(edt_sA070PopValue.getValue()) != 0) {
                var resultArr = wconv.split(edt_sA070PopValue.getValue(), "^", true);
                if (wconv.length(resultArr[0]) == 0) {} else {
                    edt_add.setValue(resultArr[0]);
                    cmb_sAddrFlagCd.setValue(resultArr[1]);
                    //  Ds_EndPftbp.SetColumn(0, "sEndPftbpMailAddFlag", resultArr[3]);
                    //  Ds_EndPftbp.SetColumn(0, "nEndPftbpMailChngSeq", resultArr[4]);
                    sEndPftbpMailAddFlagPop = resultArr[3];
                    nEndPftbpMailChngSeqPop = resultArr[4];
                    console.log(sEndPftbpMailAddFlagPop);
                    console.log(nEndPftbpMailChngSeqPop);
                    /*    trace(resultArr[3]);
                                        trace(Ds_EndPftbp.GetColumn(0, "sEndPftbpMailAddFlag"));
                                        trace(Ds_EndPftbp.GetColumn(0, "nEndPftbpMailChngSeq"));*/
                }
            }
            break;
        case "ed_Proxy":	// 만기 수익자 이면
            if (wconv.length(edt_sA070PopValue.getValue()) != 0) {
                var resultArr = wconv.split(edt_sA070PopValue.getValue(), "^", true);
                if (wconv.length(resultArr[0]) == 0) {} else {
                    edt_add.setValue(resultArr[0]);
                    cmb_sAddrFlagCd.setValue(resultArr[1]);
                    sAsmtAgntMailAddFlagPop = resultArr[3];
                    nAsmtAgntMailChngSeqPop = resultArr[4];
                }
            }
            break;
        case "ed_Dead":	
            if (wconv.length(edt_sA070PopValue.getValue()) != 0) {
                var resultArr = wconv.split(edt_sA070PopValue.getValue(), "^", true);
                edt_add.setValue(resultArr[0]);
                cmb_sAddrFlagCd.setValue(resultArr[1]);
                sPftbpMailAddFlagPop = resultArr[3];
                nPftbpMailChngSeqPop = resultArr[4];
            }
            break;
        case "ed_Xcpt":
            if (wconv.length(edt_sA070PopValue.getValue()) != 0) {
                var resultArr = wconv.split(edt_sA070PopValue.getValue(), "^", true);
                edt_add.setValue(resultArr[0]);
                cmb_sAddrFlagCd.setValue(resultArr[1]);
                sXcptPftbpMailAddFlagPop = resultArr[3];
                nXcptPftbpMailChngSeqPop = resultArr[4];
                break;
            }
    }
};

//---------------------------------------------------------------------------
// function 명 : 
// 내용        : 스마트폰앱고객 버튼클릭시 처리
//--------------------------------------------------------------------------- 
scwin.btn_SearchCust_onclick = function(e) {

	//console.log("[/CBSC069P.xml] [btn_SearchCust_OnClick()] ==> [대면고객정보 버튼 클릭] [TEST_end]" );

	//wprom.alert("추후 개발 예정 입니다.[대면고객정보]");
  	//return;  

    var promise = Promise.resolve();
    var arg = {};
    arg.sUserID=util.getGdsValue("gUserID");
    promise = promise.then(function(rtnVal) {
    	var popInfo = {
				"width"     : 635,
				"height"    : 390,  
				"title"     : "대면채널조회",
				indata : arg
    	};
        return wprom.popupOpen("/ui/cbso/CBSO009P.xml", popInfo);
    });
    promise = promise.then(function(result) {
        /*wprom.popupOpen(가이드참조)*/

        var args = result.split( "^");
        if(args[0]=='X') return;

        if (wconv.bound(args) > 2) {
            edt_Cd.setValue(wconv.trim(args[2]));
        }
    });

    return promise;
};

//---------------------------------------------------------------------------
// function 명 : lfn_TellCheck
// 내용        : 핸드폰/전화번호에 따라 자리수 잘라서 배열에 담음
//---------------------------------------------------------------------------
scwin.lfn_TellCheck = function(val) {

    var sVal = wconv.substr(val, 0, 3);
    var hpNumTmpArr = Array(3);
    if (wconv.substr(val, 0, 2) == "02") {
        if (val.length == 10) {
            hpNumTmpArr[0] = wconv.substr(val, 0, 2);
            hpNumTmpArr[1] = wconv.substr(val, 2, 4);
            hpNumTmpArr[2] = wconv.substr(val, 6, 4);
        } else {
            hpNumTmpArr[0] = wconv.substr(val, 0, 2);
            hpNumTmpArr[1] = wconv.substr(val, 2, 3);
            hpNumTmpArr[2] = wconv.substr(val, 5, 4);
        }
    } else {
        if (val.length == 11) {
            hpNumTmpArr[0] = wconv.substr(val, 0, 3);
            hpNumTmpArr[1] = wconv.substr(val, 3, 4);
            hpNumTmpArr[2] = wconv.substr(val, 7, 4);
        } else {
            hpNumTmpArr[0] = wconv.substr(val, 0, 3);
            hpNumTmpArr[1] = wconv.substr(val, 3, 3);
            hpNumTmpArr[2] = wconv.substr(val, 6, 4);
        }
    }
    return hpNumTmpArr;
};
