	var gsSysDate;				// global 현재일자
	var gUserID; 				// global 사용자 ID
	var gUserEmpName; 	    	// global 부서명

	var gPlanNo;				// 호출화면 indata[sPlanNo]  
	var gGb;					// 호출화면 indata[sGb]  
	var gFATCAFlag;				// 호출화면 indata[sFATCAFlag]  

	var gPaymCyclCd;			// 호출화면 indata["sPaymCyclCd"] = parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd"); 
	var gBussPrem;				// 호출화면 indata["nBussPrem"] = parent.Ds_LTIDA00.getCellData(0, "nBussPrem"); 
	var gBunsMetdDocuClsfCd; 	// 호출화면 indata["sBunsMetdDocuClsfCd"] = parent.Ds_LTIDA00.getCellData(0,"sBunsMetdDocuClsfCd");


//---------------------------------------------------------------------------
// function 명 : lfn_OnLoadCompleted
// 내용        : 폼로드할때 처리할 내용 
//---------------------------------------------------------------------------
	scwin.init = function() {
wprom.setPopupInit(750,290, "실소유자 및 실명확인");	
		// 실명확인버튼 hidden처리 
		btn_RlnmCnfm.visible(false);

	    //Parent에서 넘어온 값을 설정한다.
		var indata = $w.getParameter("param");
		var args;
		try {
			args = JSON.parse(indata);
		} catch(e) {

		}

		// 호출화면에서 넘어오는 값 저장
		if(!util.isNull(args)){
	    	gPlanNo 			= args.sPlanNo;
			gGb					= args.sGb;		 
			gFATCAFlag			= args.sFATCAFlag;	
			gPaymCyclCd 		= args.sPaymCyclCd;			// 호출화면 indata["sPaymCyclCd"] = parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd"); 
			gBussPrem   		= args.nBussPrem;			// 호출화면 indata["nBussPrem"] = parent.Ds_LTIDA00.getCellData(0, "nBussPrem"); 
			gBunsMetdDocuClsfCd = args.sBunsMetdDocuClsfCd; // 호출화면 indata["sRlnmCnfmRgcr"] = parent.Ds_LTIDA00.getCellData(0,"sBunsMetdDocuClsfCd");			
	    }
		gfn_getServerTime();//서버시간
		gsSysDate = wconv.substr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8);

		gUserID 		= util.getGdsValue("gUserID");    					// global 사용자 ID
		gUserEmpName 	= util.getGdsValue("gUserEmpName");    				// global 부서명

		scwin.lfn_init();
	};

//---------------------------------------------------------------------------
// function 명 : lfn_init()
// 내용        : 화면초기화
//---------------------------------------------------------------------------
	scwin.lfn_init = function() {			
		wconv.clear(Ds_LTIDA64);
		util.appendRow(Ds_LTIDA64);

		if(gFATCAFlag == 1){
			btn_save.setDisabled(true);
		}else{
		}

		scwin.lfn_Search();


	};

//**********************************************************
// 본인정보 조회서비스  Call
//**********************************************************
	scwin.lfn_Search = function() {
		wconv.clear(DsWs_Warning);

		if (gGb == "2") {
			wconv.clear(DsWs_LTIEA64);
	    	wconv.clear(DsWs_LTIEA00);
	    } else {
		    wconv.clear(DsWs_LTIDA64);
		    wconv.clear(DsWs_LTIDA00);
		}

	    wconv.clear(DsWs_LTIDA03);

		var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida64", "", "");
 	    MethodObj.setId("getLtida64");

		// parent 넘겨주는 인자로 조회 입력데이터 세팅  	    
	    MethodObj.setParam("sPlanNo", gPlanNo);
	    MethodObj.setParam("sGb", gGb);
		MethodObj.setUrl("/contract/CBSC071PController.do");
		//DsWs_LTIDA64
		MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA64DocRtn']/LTIDA64");
		//DsWs_LTIDA03
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA03DocRtn']/LTIDA03");
		//DsWs_LTIDA00
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA00DocRtn']/LTIDA00");  
		//DsWs_LTIEA00
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA00DocRtn']/LTIEA00");		
		//DsWs_LTIEA64
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA64DocRtn']/LTIEA64");
		//DsWs_Warning
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning", "Y");
		if (DsWs_Warning.getRowCount() != 0) {
			wprom.alert(DsWs_Warning.getCellData(0, "msg"));
			return;
		} 
		else {
			util.jSonDsCreate(_tranSync.getDsJson("LTIDA64"), "DsWs_LTIDA64", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIDA03"), "DsWs_LTIDA03", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_LTIDA00", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LTIEA00", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIEA64"), "DsWs_LTIEA64", "Y");
	    }

		var PlanNo00;
		var PlanNo64;

		if(gGb == "2")	{		
			PlanNo00 = DsWs_LTIEA00.getCellData(0,"sPlanNo");
			PlanNo64 = DsWs_LTIEA64.getCellData(0,"sPlanNo");
		} else{
			PlanNo00 = DsWs_LTIDA00.getCellData(0,"sPlanNo");
	//		sPlanProgRankCd = DsWs_LTIDA00.getCellData(0,"sPlanProgRankCd");
			PlanNo64 = DsWs_LTIDA64.getCellData(0,"sPlanNo");
		}

		if(wconv.length(PlanNo64) != 0){

			if(gGb == "2")	{
				wconv.clear(Ds_LTIDA64);
				util.copyM(Ds_LTIDA64, DsWs_LTIEA64.getAllJSON());
				util.setCompBindRefresh(Ds_LTIDA64.getID());
				util.setRowPosition(Ds_LTIDA64, 0);
			}
			else {
				util.copyM(Ds_LTIDA64, DsWs_LTIDA64.getAllJSON());
				util.setCompBindRefresh(Ds_LTIDA64.getID());
				util.setRowPosition(Ds_LTIDA64, 0);
			}
			edt_RealName.setValue(Ds_LTIDA64.getCellData(0, "sRealOwnerNm"));
			edt_RealCd.setValue(Ds_LTIDA64.getCellData(0, "sRealOwnerCd"));
			edt_RealNatnl.setValue(Ds_LTIDA64.getCellData(0, "sRealOwnerNatnl"));
	    }
	    if((DsWs_LTIDA00.getCellData(0, "sRealOwnerYn")) == "N"){
	         sel_RealOwner.setValue("2");
	    }else{
	        sel_RealOwner.setValue("1");
	    }

	    if(util.isNull(DsWs_LTIDA03.getCellData(0, "sRlnmCnfmRgcr")) || DsWs_LTIDA03.getCellData(0, "sRlnmCnfmRgcr") == ""){

	    }else{
	        sel_RlnmCnfmRgcr.setValue(DsWs_LTIDA03.getCellData(0, "sRlnmCnfmRgcr"));
	    }

		// 실소유자여부
		scwin.sel_RealOwner_onviewchange();
		// 실명확인 기준 세팅 
		scwin.setRealOwnerSetting();	

		var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB330Task", "getInsPlan", "", "");
 	    MethodObj.setId("getInsPlan");

		// parent 넘겨주는 인자로 조회 입력데이터 세팅  	    
	    MethodObj.setParam("sPlanNo", gPlanNo); // parent.Ds_LTIDA00.GetColumn(0, "sPlanNo"));//설계번호
		MethodObj.setUrl("/contract/CBSC071PController.do");
		MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA03Doc']/LTIDA03");  
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.jSonDsCreate(_tranSync.getDsJson("LTIDA03"), "DsWs_LTIDA03_1", "Y");
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning", "Y");
		if (DsWs_Warning.getRowCount() != 0) {
			//wprom.alert(DsWs_Warning.getCellData(0, "msg"));
			//return;
		}

		scwin.setRlnmCnfmRgcr();

	    return;
	};	

	//**********************************************************
	// 실명확인표 선택에 따른 에디트박스 값 세팅
	//**********************************************************	
	scwin.setRlnmCnfmRgcr = function() {
		var sRlnmCnfmRgcr = sel_RlnmCnfmRgcr.getValue();
		switch(sRlnmCnfmRgcr) {
			// 선택
			case "00":
				edt_RvMatt1.setDisabled(true);
				edt_RvMatt2.setDisabled(true);
				break;
			// 주민등록번호
			case "01" :
//				Stat_RvMatt1.setStyle( propertyName , value )
			    Stat_RvMatt1.setValue("발급일자");
			    edt_RvMatt1.setDisabled(false);
			    Stat_RvMatt2.setValue("");
			    edt_RvMatt2.setDisabled(true);
			    break;
			// 운전면허증    
			case "02" :
			    Stat_RvMatt1.setValue("면허번호");
			    edt_RvMatt1.setDisabled(false);
			    Stat_RvMatt2.setValue("");
			    edt_RvMatt2.setDisabled(true);
				break;
			// 여권	
			case "03" :
			    Stat_RvMatt1.setValue("여권기재명");
			    edt_RvMatt1.setDisabled(false);
			    Stat_RvMatt2.setValue("여권번호");
			    edt_RvMatt2.setDisabled(false);
			    break;
			// 외국인등록증    
			case "04" :
			// 재외국인(외국국적동포)
			case "05" :
			// 국내거소신고증
			case "06" :
			    Stat_RvMatt1.setValue("등록번호");
			    edt_RvMatt1.setDisabled(false);
			    Stat_RvMatt2.setValue("발급일자");
			    edt_RvMatt2.setDisabled(false);
			    break;
			// 그외			    
			case "07" :
			case "08" :
			case "09" :
			case "10" :
			case "11" :
			case "12" :
			    Stat_RvMatt1.setValue("발급기관");
			    edt_RvMatt1.setDisabled(false);
			    Stat_RvMatt2.Text = "발급(신청일자)";
			    edt_RvMatt2.setDisabled(false);
			    break;			
			default:
				sel_RlnmCnfmRgcr.setValue("00");
				edt_RvMatt1.setDisabled(true);
				edt_RvMatt2.setDisabled(true);
				break;		
		}
	};

	//**********************************************************
	// 실명확인 기준 셋팅
	//**********************************************************	
	scwin.setRealOwnerSetting = function() {
		//월납보험료 25만원초과, 연납 300만원초과, 일시납 500만원 초과시 고객정보 사항 입력필수, 미입력시 팝업안내
		//          안내문구 : “고객 실명확인사항을 입력하세요”
		//변경 : 저축/연금 가입 전건, 보장성 월납 25만원 이상 or 일시납 1천만원 이상[N1410-00071] -> 월납25만원이상제외
	    var sRealOwnerFlag = false;

		if(gBunsMetdDocuClsfCd == "5" || gBunsMetdDocuClsfCd == "7") { //저축,연금
		   sRealOwnerFlag = true;
		}
		else if(gPaymCyclCd == "99" &&  wconv.toNumber(gBussPrem) >= 10000000){ //일시납 1천만원이상
		   sRealOwnerFlag = true;
	    }

		if(sRealOwnerFlag){
		}else{
			sel_RlnmCnfmRgcr.setDisabled(true);
		    edt_RvMatt1.setDisabled(true);
		    edt_RvMatt2.setDisabled(true);
		}	
	};	

	//**********************************************************
	// 실명확인표 셀렉트박스 체인지
	//**********************************************************
	scwin.sel_RlnmCnfmRgcr_onchange = function() {
		// 실명확인표 선택에 따른 에디트박스 값 세팅
		scwin.setRlnmCnfmRgcr();	
	};

	//**********************************************************
	// 실소유자 여부 셀렉트박스 뷰체인지
	//**********************************************************		
	scwin.sel_RealOwner_onviewchange = function(info) {
		// 실소유자 여부 변경 시	
		var sRealOwner = sel_RealOwner.getValue();
		switch(sRealOwner) {
			// Y
			case "1":
				edt_RealName.setDisabled(true);
				edt_RealCd.setDisabled(true);
				edt_RealNatnl.setDisabled(true);
				edt_RealName.setValue("");
				edt_RealCd.setValue("");
				edt_RealNatnl.setValue("");
				break;
			// N
			case "2" :
			    edt_RealName.setDisabled(false);
				edt_RealCd.setDisabled(false);
				edt_RealNatnl.setDisabled(false);
			    break;
			default:

				break;		
		}
	};

	//**********************************************************
	// 실명확인 버튼 클릭이벤트
	//**********************************************************			
	scwin.btn_RlnmCnfm_onclick = function() {
		// 실명확인정보조회
		scwin.getRlnmCnfmInfo();	
	};

	//**********************************************************
	// 서브미션 완료후 CALLBACK 
	//**********************************************************
	scwin.subCallBackfunction = function(e){
		// e.subID : 서브미션 ID
		// e.flag  : S성공;
		// e.msg   : 트렌젝션 처리후 메세지
		// e."받을데이타셋"
		switch(e.subID){
			// 실명확인정보조회
			case "retrieveRlnmCnfmInfo":
				if(e.flag == "s") {
//					dst_BussActDataInfoVo.setJSON(e.result);	
					// 조회데이터로 화면세팅 		
//					scwin.setBussActDataAdm();	
				} else {
					wprom.alert(e.msg);
				}

			break;

		}
	};

	//**********************************************************
	// /contract/longterm/CBSC0008.do 실명확인정보조회
	//**********************************************************
	scwin.getRlnmCnfmInfo = function() {
		dst_RlnmCnfmInfoVO.set("empNo" , gUserID);

		var trans = new transSub();                  		// transaction 보낼 객체 생성
		trans.setId("retrieveRlnmCnfmInfo");        		// transaction 보낼 SubmissionID
		trans.setUrl("/contract/longterm/CBSC0008.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_RlnmCnfmInfoVO");					
		trans.targetDs("dst_RlnmCnfmInfoVO");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 저장버튼 클릭이벤트
	//**********************************************************			
	scwin.btn_save_onclick = function() {		
		// 저장데이터 세팅 
		var bRtn = scwin.lfn_SaveData();
		//ㅍ입력항목 정합성 체크	
		if(scwin.lfn_Validation_Check()) {
			// 저장서비스 콜 
			var sPlanNo = gPlanNo;	// 어디서 가져오는 값인지?
			var sRealName = edt_RealName.getValue();
			var sRealCd = edt_RealCd.getValue();
			var sRealNatnl = edt_RealNatnl.getValue();
			var sRealOwnerYn = sel_RealOwner.getValue();


			Ds_LTIDA64.setCellData(0,"sPlanNo", gPlanNo);
			Ds_LTIDA64.setCellData(0,"sRealOwnerNm", sRealName);        
			Ds_LTIDA64.setCellData(0,"sRealOwnerCd", sRealCd);        
			Ds_LTIDA64.setCellData(0,"sRealOwnerNatnl", sRealNatnl);

			util.insertColumn(Ds_LTIDA64, [ ["gUserID","gUserID","text"],      
											["sRealOwnerYn","sRealOwnerYn","text"]
											]);      

			Ds_LTIDA64.setCellData(0,"gUserID", gUserID);
			util.setCompBindRefresh(Ds_LTIDA64.getID());
	   	    util.setRowPosition(Ds_LTIDA64, 0);  	
			if(sRealOwnerYn == "2"){
				Ds_LTIDA64.setCellData(0, "sRealOwnerYn", "N");
			}else{
				Ds_LTIDA64.setCellData(0, "sRealOwnerYn", "Y");
			}

			// 저장 서비스 호출 
			var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "saveLtida64");
			MethodObj.setId("saveLtida64");

			MethodObj.setUrl("/contract/CBSC071PController.do");
			MethodObj.setHashTable();   // 저장할 때

			MethodObj.addSingleData("LTIDA64", "LTIDA64", "Ds_LTIDA64:A");	
			MethodObj.setXPath("//WARNING");
			MethodObj.sync();
			MethodObj.send();

			//에러처리
			util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
			if(DsWs_Warning.getRowCount() != 0){
				alert(DsWs_Warning.getCellData(0, "msg"));
				return;
			}else{
				alert("저장 되었습니다.");
		    }

			// 재조회 
			scwin.lfn_Search();
			// 팝업화면 닫기 
			scwin.btn_close_onclick();
		}
	};

	//**********************************************************
	// 입력항목 정합성 체크
	//**********************************************************			
	scwin.lfn_Validation_Check = function() {
		// 입력항목 정합성 체크
		var chkFlag = true;
		var sRlnmCnfmRgcr = sel_RlnmCnfmRgcr.getValue();
		var sRvMatt1 = edt_RvMatt1.getValue();
		var sRvMatt2 = edt_RvMatt2.getValue();
		var sRealOwner = sel_RealOwner.getValue();
		var sRealCd = edt_RealCd.getValue();
		var sRealName = edt_RealName.getValue();
		var sRealNatnl = edt_RealNatnl.getValue();

  		if(sel_RlnmCnfmRgcr.getDisabled() == false) {

		    if(sRlnmCnfmRgcr == "" || sRlnmCnfmRgcr == "00" || sRvMatt1 == "" ){

		       wprom.alertF("내고객 실명 확인사항을 입력하세요", {}, edt_RvMatt1);	
			   chkFlag = false;
			   return chkFlag;
		    }else if(sRlnmCnfmRgcr > "02" && sRvMatt2 == "" && rtrim(sRvMatt2) > ""){

		       wprom.alertF("고객 실명 확인사항을 입력하세요", {}, edt_RvMatt2);	       
     		   chkFlag = false;
			   return chkFlag;
			  }
		}

	    if(sRealOwner == "0" ){
	    	wprom.alertF("실소유자 여부를 구분해 주세요.", {}, sel_RealOwner);	      
			chkFlag = false;
	       	return chkFlag;
	    }
	    else if(sRealOwner == "2"){
			if(util.isNull(sRealCd) && wconv.length(sRealCd) != 13){
		   		wprom.alertF("주민번호를 정확히 입력해 주세요.", {}, edt_RealCd);	      
		     	chkFlag = false;
	       		return chkFlag;
		 	}

		  	if(sRealName == ""){
		  		wprom.alertF("실소유자 이름을 입력하시기 바랍니다.", {}, edt_RealName);	    
		    	chkFlag = false;
	       		return chkFlag;
	  		}
			if(sRealCd == ""){
				wprom.alertF("실소유자 주민번호를 입력하시기 바랍니다.", {}, edt_RealCd);	    
		    	chkFlag = false;
	       		return chkFlag;
			  }
		 	if(sRealNatnl == ""){
		  		wprom.alertF("실소유자 국가를 입력하시기 바랍니다.", {}, edt_RealNatnl);	    
	    		chkFlag = false;
       			return chkFlag;
		  	}
     	}

     	return chkFlag;
	};

//**********************************************************
// 저장데이터 세팅 
//**********************************************************			
	scwin.lfn_SaveData = function() {
     	// 입력데이터 데이터셋 세팅 
     	// 실명확인표 비활성이면 
     	if(sel_RlnmCnfmRgcr.getDisabled() == true){
		    DsWs_LTIDA03.setCellData(0, "sRlnmCnfmDate", "");
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfm", "");
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfmPstnFlgcd", "");
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfmInfoRvMatt1", "");
		    DsWs_LTIDA03.setCellData(0, "sRlnmCnfmInfoRvMatt2", "");
		    DsWs_LTIDA03.setCellData(0, "sRlnmCnfmRgcr", "");
		}else{    
		    DsWs_LTIDA03.setCellData(0, "sRlnmCnfmDate", gsSysDate);
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfm", util.getGdsValue("gDoer"));
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfmPstnFlgcd", "01");//본인'01'
 		//미성년자 체크 
		   	if(getAge(DsWs_LTIDA03.getCellData(0, "sCrtorCd"), gsSysDate) < 20 )
		   	{
				DsWs_LTIDA03.setCellData(0, "sRlnmCnfmPstnFlgcd", "02"); //법정대리인'02'
		   	}
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfmInfoRvMatt1", edt_RvMatt1.getValue());
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfmInfoRvMatt2", edt_RvMatt2.getValue());
			DsWs_LTIDA03.setCellData(0, "sRlnmCnfmRgcr", sel_RlnmCnfmRgcr.getValue());
		}

		// 저장 서비스 호출 
		var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "setCmMetd");
		MethodObj.setId("setCmMetd");

		MethodObj.setParam("sRlnmCnfmRgcr", DsWs_LTIDA03.getCellData(0, "sRlnmCnfmRgcr"));
		MethodObj.setParam("sRlnmCnfmInfoRvMatt1", DsWs_LTIDA03.getCellData(0, "sRlnmCnfmInfoRvMatt1"));
		MethodObj.setParam("sRlnmCnfmInfoRvMatt2", DsWs_LTIDA03.getCellData(0, "sRlnmCnfmInfoRvMatt2"));
		MethodObj.setParam("sRlnmCnfmDate", DsWs_LTIDA03.getCellData(0, "sRlnmCnfmDate"));
		MethodObj.setParam("sRlnmCnfm", DsWs_LTIDA03.getCellData(0, "sRlnmCnfm"));
		MethodObj.setParam("sRlnmCnfmPstnFlgcd", DsWs_LTIDA03.getCellData(0, "sRlnmCnfmPstnFlgcd"));
		MethodObj.setParam("sPlanNo", DsWs_LTIDA03.getCellData(0, "sPlanNo"));

		MethodObj.setUrl("/contract/CBSC071PController.do");
		MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA64DocRtn']/LTIDA64");
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA03DocRtn']/LTIDA03");
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA00DocRtn']/LTIDA00");  
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA00DocRtn']/LTIEA00");
		MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA64DocRtn']/LTIEA64");
		MethodObj.addXPath("//WARNING");    
		MethodObj.sync();
		MethodObj.send();

		wconv.clear(DsWs_Warning);
	    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning", "Y");
		if (DsWs_Warning.getRowCount() != 0) {
			wprom.alert(DsWs_Warning.getCellData(0, "msg"));
			return;
		} 
		else {
			util.jSonDsCreate(_tranSync.getDsJson("LTIDA64"), "DsWs_LTIDA64", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIDA03"), "DsWs_LTIDA03", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_LTIDA00", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LTIEA00", "Y");
			util.jSonDsCreate(_tranSync.getDsJson("LTIEA64"), "DsWs_LTIEA64", "Y");
	    }
	};

