head.script:
//========================================================//
// 파라메터 정보 및 리턴정보 영역(사용설명 포함)
//========================================================//
// * param
//   sStndDate : 기준일자 => 값 없을경우 팝업에서 현재일자 자동세팅
//
//
// * return
//    retArr[0]    : 업종대분류코드
//    retArr[1]    : 업종대분류명
//    retArr[2]    : 업종중분류코드
//    retArr[3]    : 업종중분류명
//    retArr[4]    : 업종코드
//    retArr[5]    : 업종명
//    retArr[6]    : 요율
//    retArr[7]    : 업종해설1
//    retArr[8]    : 업종해설2
//    retArr[9]    : 인수기준내용1
//    retArr[10]   : 인수기준내용2
//    retArr[11]   : 업종시설단위
//
//
//========================================================//
//========================================================//
// 전역변수 선언 영역
//========================================================//
var sStndDate = "";

// 기준일자
var sToday = "";

// 현재일자(서버일자)
var applyEnable = "N";

var pageSize="";
var pageNo="";
var fv_args;
//설계상태(인수완료)-적용버튼(비활성화)
//========================================================//
//========================================================//
// 초기 OnLoad 영역
//========================================================//
//---------------------------------------------------------------------------
// function 명 : FORM_OnLoadCompleted(obj)
// 내용        : FORM OnLoad
//---------------------------------------------------------------------------
scwin.init = function() {
	var pro = Promise.resolve();
		window.top.util.winTopShowModalChk = "T";
		util.winTopShowModal();
	pro = pro.then(function(rv) {
    	return util.winTopShowModalPromise();
	}); 
	pro = pro.then(function(rv) {
     //init 부분 함수 입력하는곳
	wprom.setPopupInit(725,472, "업종찾기");
	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
	} catch(e) {
	};

    //trace("파라메터 값 :::::::::::::::applyEnable="+ applyEnable);
    if (fv_args.applyEnable == "Y")
        btn_apply.setDisabled({"disabled":false});

    //trace("파라메터 값 :::::::::::::::"+ sStndDate + applyEnable);
    // 파라메터
    sStndDate = fv_args.sStndDate;

    // 현재시간 setting
    //global.http.Sync = true;
    //getServerTime();
    //sToday = substr(gDs_ServerTime.GetColumn(0, "sToday"),0,8);
    //global.http.Sync = false;	
    sToday = wconv.substr(gfn_GetSysTime(), 0, 8);
    // 기준일자 설정
    if (wconv.trim(sStndDate) == null || wconv.trim(sStndDate) == "") {
        sStndDate = sToday;
    }Ds_BntpCd
    /*
    	// 공통코드 조회
    	lfn_getCommonCodes();
    */
    // 초기값 설정
    scwin.lfn_initDataset();

    // 업종대분류 조회(01:개발원코드(화재손해업종))
    scwin.lfn_Search();

        return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); 
	});
	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});	
	pro = pro.catch(function(rj) {
		util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	});	 
	return pro; 
};

//---------------------------------------------------------------------------
// function 명 : lfn_getCommonCodes()
// 내용        : 공통코드 조회
// Param       : sobjObj         // ServiceObject 객체
//             : dobjObj         // DataObject 객체
//             : dataSetIds      // Dataset 아이디
//             : sCdTypes        // 항목코드
//             : componentIds    // Component Id ==> 없으면 안넣어도 됨
//             : titleTypes      // Combo 항목추가: 전체(A) , 선택(S) , 없음(N)
//---------------------------------------------------------------------------
scwin.lfn_getCommonCodes = function() {
    wconv.clear(DsWs_BntpLargClsfCd);
    wconv.clear(DsWs_BntpDurnClsfCd);
    wconv.clear(DsWs_BntpCd);
    DsWs_BntpLargClsfCd.DataObject = "DOBJ_Code";
    DsWs_BntpDurnClsfCd.DataObject = "DOBJ_Code";
    DsWs_BntpCd.DataObject = "DOBJ_Code";


    // 공통코드 조회
    gfn_getCommonCodes("DsWs_BntpLargClsfCd DsWs_BntpDurnClsfCd DsWs_BntpCd", "S71 ObjtFlagMcls S70", "", "N N N"); //trace(DsWs_BntpLargClsfCd.SaveXML());
    //trace(DsWs_BntpDurnClsfCd.SaveXML());
    //trace(DsWs_BntpCd.SaveXML());
};

//---------------------------------------------------------------------------
// function 명 : lfn_initDataset
// 내용        : 초기값 설정
//---------------------------------------------------------------------------
scwin.lfn_initDataset = function() {
    DsWs_BntpLargClsfCd.removeAll();
    DsWs_BntpDurnClsfCd.removeAll();
    DsWs_BntpCd.removeAll();
    Ds_BntpLargClsfCd.removeAll();
    Ds_BntpDurnClsfCd.removeAll();
    Ds_BntpCd.removeAll();
};

//========================================================//
//========================================================//
// 자체 이벤트 영역
//========================================================//
//---------------------------------------------------------------------------
// function 명 : btn_search_OnClick(obj)
// 내용        : 조회 OnClick
//---------------------------------------------------------------------------
scwin.btn_search_OnClick_Async = function() {
	util.eventAsyncCall(this, scwin.btn_search_OnClick, arguments);
};
scwin.btn_search_OnClick = function(e) {
    scwin.lfn_Search();
};

//---------------------------------------------------------------------------
// function 명 : edt_BntpName_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
// 내용        : 검색업종명 엔터
//---------------------------------------------------------------------------
scwin.edt_BntpName_OnKeyDown_Async = function(e) {
	if (e.keyCode == 13) {
	util.eventAsyncCall(this, scwin.edt_BntpName_OnKeyDown, arguments);
	}
};
scwin.edt_BntpName_OnKeyDown = function(e) {
    if (e.keyCode == 13) {
        scwin.lfn_Search();
    }
};

//---------------------------------------------------------------------------
// function 명 : btn_Close_OnClick(obj)
// 내용        : 닫기 OnClick
//---------------------------------------------------------------------------
scwin.btn_Close_OnClick = function(e) {
    wprom.close();
};

//---------------------------------------------------------------------------
// function 명 : btn_apply_OnClick(obj)
// 내용        : 적용 OnClick
//---------------------------------------------------------------------------
scwin.btn_apply_OnClick = function(e) {
    if (Ds_BntpCd.getRowPosition() < 0) {    	
        wprom.alert("업종을 선택하세요!");
        return;
    }
    var retArr = new Array();
    var i = 0;
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpLargClsfCd"); // 업종대분류코드
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpLargClsfCdName"); // 업종대분류명
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpDurnClsfCd"); // 업종중분류코드
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpDurnClsfCdName"); // 업종중분류명
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpCd"); // 업종코드
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpCdName"); // 업종명
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "nRato"); // 요율
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpExpln1"); // 업종해설1
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpExpln2"); // 업종해설2
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sUdrtkStndCont1"); // 인수기준내용1
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sUdrtkStndCont2"); // 인수기준내용2
    retArr[i++] = Ds_BntpCd.getCellData(Ds_BntpCd.getRowPosition(), "sBntpEqmtUnit"); // 업종시설단위
    //alert(retArr);
    wprom.close(retArr);
};

//---------------------------------------------------------------------------
// function 명 : grd_BntpLargClsfCd_OnCellClick(obj)
// 내용        : 업종분류 OnCellClick
//---------------------------------------------------------------------------
scwin.grd_BntpLargClsfCd_OnCellClick = function(row, col) {
    // 업종중분류 조회(01:개발원코드(화재손해업종))

    scwin.lfn_getBntpDurnClsfTask();
    Static3.setValue("");
};

//---------------------------------------------------------------------------
// function 명 : grd_BntpDurnClsfCd_OnCellClick(obj)
// 내용        : 건물구분 OnCellClick
//---------------------------------------------------------------------------
scwin.grd_BntpDurnClsfCd_OnCellClick = function(row, col) {
    // 업종 조회(01:개발원코드(화재손해업종))
    scwin.lfn_getBntpCdTask();
    Static3.setValue("<");
};

//---------------------------------------------------------------------------
// function 명 : grd_BntpCd_OnCellDblClick(obj)
// 내용        : 업종명 그리드 OnCellDblClick
//---------------------------------------------------------------------------
scwin.grd_BntpCd_OnCellDblClick = function(row, col) {
    // 적용 OnClick
    scwin.btn_apply_OnClick();
};

