//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 업종명 조회
//---------------------------------------------------------------------------
scwin.lfn_Search = function() {
    var sBntpName = wconv.trim(edt_BntpName.getValue());
    if (wconv.length(sBntpName) == 0) {
        scwin.lfn_initDataset(); // 데이터셋 초기값 설정
        // 업종대분류 조회(01:개발원코드(화재손해업종))
        scwin.lfn_getBntpLargClsfTask();
    } else {
        Ds_BntpLargClsfCd.removeAll();
        Ds_BntpDurnClsfCd.removeAll();
        // 업종 조회(01:개발원코드(화재손해업종))
        scwin.lfn_getBntpCdTask();
    }
};

//========================================================//
//========================================================//
// 서비스호출 영역
//========================================================//
//---------------------------------------------------------------------------
// function 명 : lfn_getBntpLargClsfTask
// 내용        : 업종대분류 조회(01:개발원코드(화재손해업종))
// param       : sStndDate = 기준일자
//---------------------------------------------------------------------------
scwin.lfn_getBntpLargClsfTask = function() {
    // DataSet 초기화
    wconv.clear(Ds_BntpLargClsfCd);
    wconv.clear(DsWs_BntpLargClsfCd);
    // Service Object 설정
    //SOBJ_Search.WSDL = gds.gWsdlBasis;
    // 서비스 호출 메소드 정의(서비스 오브젝트, 타스크, 액션, 페이지당 사이즈, 가져올 현재 페이지)
    //var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.ssr.pln.task.DBB610Task", "getBntpLargClsf", "", "");
    //gfn_SetParam(MethodObj, "sStndDate", wconv.trim(sStndDate)); // 기준일자

    var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB610Task", "getBntpLargClsf", pageSize, pageNo);
	MethodObj.setId("getBntpLargClsf");
	MethodObj.setParam( "sStndDate", wconv.trim(sStndDate)); // 기준일자
	MethodObj.setUrl("/contract/CBSC072PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/vector/data/LTIBB01Larg");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); //DataCollection에 WARING을 만들어줌(msg, level, detail, timestamp)
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Larg"), "DsWs_BntpLargClsfCd");
	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Larg"), "Ds_BntpLargClsfCd");
	//Json에 자동적으로 생성되게 해줌
	util.setCompBindRefresh("DsWs_BntpLargClsfCd");//바인딩을 해줌
	util.setCompBindRefresh("Ds_BntpLargClsfCd");//바인딩을 해줌
	util.setRowPosition(DsWs_BntpLargClsfCd, 0);  
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
 	//에러처리
 	    if (DsWs_Warning.getRowCount() != 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
            return;
        }

    wconv.copy(Ds_BntpLargClsfCd, DsWs_BntpLargClsfCd);
};

//---------------------------------------------------------------------------
// function 명 : lfn_getBntpDurnClsfTask
// 내용        : 업종중분류 조회(01:개발원코드(화재손해업종))
// param       : sStndDate = 기준일자
//             : sBntpLargClsfCd = 업종대분류코드
//---------------------------------------------------------------------------

scwin.lfn_getBntpDurnClsfTask = function() {
    // DataSet 초기화
    wconv.clear(Ds_BntpDurnClsfCd);
    wconv.clear(DsWs_BntpDurnClsfCd);
    wconv.clear(Ds_BntpCd);
    wconv.clear(DsWs_BntpCd);
    // Service Object 설정
    //SOBJ_Search.WSDL = gds.gWsdlBasis;
    var sBntpLargClsfCd = Ds_BntpLargClsfCd.getCellData(grd_BntpLargClsfCd.getFocusedRowIndex(), "sBntpLargClsfCd");// 업종대분류코드
    // 서비스 호출 메소드 정의(서비스 오브젝트, 타스크, 액션, 페이지당 사이즈, 가져올 현재 페이지)


    //var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.ssr.pln.task.DBB610Task", "getBntpDurnClsf", "", "");
    //gfn_SetParam(MethodObj, "sStndDate", wconv.trim(sStndDate)); // 기준일자
    //gfn_SetParam(MethodObj, "sBntpLargClsfCd", wconv.trim(sBntpLargClsfCd)); // 업종대분류코드

    var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB610Task", "getBntpDurnClsf", pageSize, pageNo);
	MethodObj.setId("getBntpDurnClsf");
	MethodObj.setParam( "sStndDate", wconv.trim(sStndDate)); // 기준일자
	MethodObj.setParam( "sBntpLargClsfCd", wconv.trim(sBntpLargClsfCd)); // 업종대분류코드
	MethodObj.setUrl("/contract/CBSC072PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/vector/data/LTIBB01Durn");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); //DataCollection에 WARING을 만들어줌(msg, level, detail, timestamp)
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Durn"), "DsWs_BntpDurnClsfCd");
	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Durn"), "Ds_BntpDurnClsfCd");
	//Json에 자동적으로 생성되게 해줌
	util.setCompBindRefresh("DsWs_BntpDurnClsfCd");//바인딩을 해줌
	util.setCompBindRefresh("Ds_BntpDurnClsfCd");//바인딩을 해줌
	util.setRowPosition(DsWs_BntpDurnClsfCd, 0);	
    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
 	//에러처리
 	    if (DsWs_Warning.getRowCount() != 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
            return;
        }
//    if(MethodObj == false) {	
//    	wprom.alert(DsWs_Warning.getCellData(0, "msg"));
//        return;
//    }
    wconv.copy(Ds_BntpDurnClsfCd, DsWs_BntpDurnClsfCd);
};

//---------------------------------------------------------------------------
// function 명 : lfn_getBntpCdTask
// 내용        : 업종 조회(01:개발원코드(화재손해업종))
// param       : sStndDate = 기준일자
//             : sBntpLargClsfCd = 업종대분류코드
//             : sBntpLargClsfCd = 업종대분류코드
//---------------------------------------------------------------------------
scwin.lfn_getBntpCdTask = function() {
    // DataSet 초기화
    wconv.clear(Ds_BntpCd);
    wconv.clear(DsWs_BntpCd);
    // Service Object 설정
    //SOBJ_Search.WSDL = gds.gWsdlBasis;
    var sBntpLargClsfCd = wconv.trim(Ds_BntpLargClsfCd.getCellData(grd_BntpLargClsfCd.getFocusedRowIndex(), "sBntpLargClsfCd")); // 업종대분류코드
    var sBntpDurnClsfCd = wconv.trim(Ds_BntpDurnClsfCd.getCellData(grd_BntpDurnClsfCd.getFocusedRowIndex(), "sBntpDurnClsfCd")); // 업종중분류코드
    var sBntpCdName = wconv.trim(edt_BntpName.getValue());
    if (sBntpLargClsfCd == null || sBntpLargClsfCd == "")
        sBntpLargClsfCd = "%";
    if (sBntpDurnClsfCd == null || sBntpDurnClsfCd == "")
        sBntpDurnClsfCd = "%";
    if (sBntpCdName == null || sBntpCdName == "")
        sBntpCdName = "%";

    // 서비스 호출 메소드 정의(서비스 오브젝트, 타스크, 액션, 페이지당 사이즈, 가져올 현재 페이지)
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.ssr.pln.task.DBB610Task", "getBntpCd", "", "");
//    gfn_SetParam(MethodObj, "sStndDate", wconv.trim(sStndDate)); // 기준일자
//    gfn_SetParam(MethodObj, "sBntpLargClsfCd", wconv.trim(sBntpLargClsfCd)); // 업종대분류코드
//    gfn_SetParam(MethodObj, "sBntpDurnClsfCd", wconv.trim(sBntpDurnClsfCd)); // 업종중분류코드
//    gfn_SetParam(MethodObj, "sBntpCdName", wconv.trim(sBntpCdName)); // 검색업종명

	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB610Task", "getBntpCd", pageSize, pageNo);
	MethodObj.setId("getBntpCd");
	MethodObj.setParam( "sStndDate", wconv.trim(sStndDate)); // 기준일자
	MethodObj.setParam( "sBntpLargClsfCd", wconv.trim(sBntpLargClsfCd)); // 업종대분류코드
	MethodObj.setParam( "sBntpDurnClsfCd", wconv.trim(sBntpDurnClsfCd)); // 업종중분류코드
	MethodObj.setParam( "sBntpCdName", wconv.trim(sBntpCdName)); // 검색업종명
	MethodObj.setUrl("/contract/CBSC084PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/vector/data/LTIBB01Bntp");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); //DataCollection에 WARING을 만들어줌(msg, level, detail, timestamp)
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Bntp"), "DsWs_BntpCd");
	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Bntp"), "Ds_BntpCd");
	//Json에 자동적으로 생성되게 해줌
	util.setCompBindRefresh("DsWs_BntpCd");//바인딩을 해줌
	util.setCompBindRefresh("Ds_BntpCd");//바인딩을 해줌
	util.setRowPosition(DsWs_BntpCd, 0);	
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
 	//에러처리
 	    if (DsWs_Warning.getRowCount() != 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
            return;
        }
    wconv.copy(Ds_BntpCd, DsWs_BntpCd); //trace(Ds_BntpCd.SaveXML());
    //retArr[i++] = Ds_BntpCd.GetColumn(Ds_BntpCd.row, "sBntpDurnClsfCdName"    );    // 업종중분류명
}; //========================================================//
