scwin.init = function() {
var pro = Promise.resolve();

window.top.util.winTopShowModalChk = "T";
util.winTopShowModal();



pro = pro.then(function(rv) {
    return util.winTopShowModalPromise();
});



pro = pro.then(function(rv) {

     //init 부분 함수 입력하는곳


	wprom.windowOpenInit(F230_Pop);
	var indata = $w.getParameter("param");
	try {
		fv_args = JSON.parse(indata);
		console.log(fv_args);

	     if (wconv.length(fv_args.sCrNo) > 0) {
	        sCrNo=fv_args.sCrNo;
	        issuObjFlg = fv_args.issuObjFlg;
			chkPost  = fv_args.chkPost;
			chkEmail =	 fv_args.chkEmail;
			chkFax 	 = fv_args.chkFax;
			sFax1 	 = fv_args.sFax1;
			sFax2 	 = fv_args.sFax2;
			sFax3 	 = fv_args.sFax3;
			sContName = fv_args.sContName;

	    }
	} catch(e) {
	};
    // 확인버튼 비활성화
    //btn_accept.Enable = false;
    // DATA가 없을경우 계약조회 버튼 활성화
    if (wconv.trim(sCrNo) == "" || wconv.trim(sCrNo) == null) {
        wprom.alert("계약번호를 입력하십시요. "); //this.Btn_Close_OnClick();
    } else {
        // datamove

        sCrNo = sCrNo.toUpperCase();
        msk_sCrNo.setValue(sCrNo);
        scwin.lfn_Search(); // 영수증발행대상조회
    }
    if (issuObjFlg == "01") {
//        Static0.setValue("영수증발행대상");
		wprom.setPopupInit(871, 404, "영수증발행대상");
    } else if (issuObjFlg == "02") {
//        Static0.setValue("신용카드매출전표");
		wprom.setPopupInit(871, 404, "신용카드매출전표");
    }

    //init 함수를 종료하고자 할때 사용한다.
//    return Promise.reject("오류관련처리");
});


pro = pro.then(function(rv) {
    util.winTopHideModal();
});



pro = pro.catch(function(rj) {
    util.winTopHideModal();
    //alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
});



return pro;
};

