scwin.lfn_Search = function(e) {
    // 초기화
    wconv.clear(Ds_LTIFA12);
    wconv.clear(DsWs_LTIFA12);
    wconv.clear(Ds_LTIFA12_TMEP);
    wconv.clear(Ds_LTIEA00);
    wconv.clear(DsWs_LTIEA00);
    wconv.clear(warning);
//    SOBJ_Search.WSDL = gds.gWsdlBasis;

    // 입력값 chk
    if (!scwin.lfn_inputChk()) {
        return;
    }

    // 이체 계좌정보조회
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.dpt.rec.task.FAF230Task", "getIssuRecpList", "", "");
//    gfn_SetParam(MethodObj, "sCrNo", wconv.trim(msk_sCrNo.getValue())); // 계약번호
    //gfn_SetParam(MethodObj, "sRecpNo", trim(sRecpNo)); // 영수증번호
    // 서비스 호출
//    gfn_CallService(SOBJ_Search, DOBJ_DataSearch, MethodObj);
//    wconv.trace(replace(gfn_XmlEncoder(DOBJ_DataSearch.getValue()), "><", ">\n<"));

	var MethodObj = new callService("com.inswave.longterm.dpt.rec.task.FAF230Task", "getIssuRecpList", pageSize, pageNo);
	MethodObj.setId("getIssuRecpList");
	MethodObj.setParam( "sCrNo", wconv.trim(msk_sCrNo.getValue())); 
	MethodObj.setParam( "sRecpNo", wconv.trim(sRecpNo));

	MethodObj.setXPath("//*/vector/data/LTIFA12");//(dsWS의 XPath)
	MethodObj.addXPath("//*/data/LTIEA00");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LTIEA00");
	util.jSonDsCreate(_tranSync.getDsJson("LTIFA12"), "DsWs_LTIFA12");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");

	util.setCompBindRefresh("DsWs_LTIEA00");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_LTIFA12");//바인딩을 해줌
	util.setCompBindRefresh("warning");//바인딩을 해줌
	util.setRowPosition(Ds_LTIFA12_TMEP, 0);


    // 오류처리
    if (warning.getRowCount() != 0) {
        wprom.alert(warning.getCellData(0, "msg"));
        return;
    }
//    wconv.copy(Ds_LTIEA00, "DsWs_LTIEA00");
//    wconv.copy(Ds_LTIFA12_TMEP, "DsWs_LTIFA12");
	util.copyM(Ds_LTIEA00, DsWs_LTIEA00.getAllJSON());
	util.copyM(Ds_LTIFA12_TMEP, DsWs_LTIFA12.getAllJSON());
	util.setCompBindRefresh("Ds_LTIEA00");//바인딩을 해줌
	util.setCompBindRefresh("Ds_LTIFA12_TMEP");//바인딩을 해줌
	util.setRowPosition(Ds_LTIEA00, 0);
	util.setRowPosition(Ds_LTIFA12_TMEP, 0);
    util.insertColumn(Ds_LTIFA12_TMEP, [
        [
            "sSelYn",
            "sSelYn",
            "text"
        ]
    ]); // 선택여부
    if (issuObjFlg == "02") {
        //신용카드매출전표
//        wconv.filter(Ds_LTIFA12_TMEP, "sMntpFlgcd=="+"2",true);
		var funcFilter1 = function(cellData, tmpParam, rowIdx) {
	   		if (DS_MEMBER.getCellData(rowIdx, "sMntpFlgcd") == "2") {
	   				return true;
	   			}
	    	return false;
	    }
		DS_MEMBER.clearFilter();
	    DS_MEMBER.setColumnFilter({"type":"func", "colIndex":"sMntpFlgcd", "key":funcFilter1, "condition":"and"});
//        wconv.copyF(Ds_LTIFA12, "Ds_LTIFA12_TMEP");

	util.copyM(Ds_LTIFA12, Ds_LTIFA12_TMEP.getAllJSON());
	util.setCompBindRefresh("Ds_LTIFA12");//바인딩을 해줌
	util.setRowPosition(Ds_LTIFA12, 0);
    } else {
//        wconv.copy(Ds_LTIFA12, "Ds_LTIFA12_TMEP");
	    util.copyM(Ds_LTIFA12, Ds_LTIFA12_TMEP.getAllJSON());
		util.setCompBindRefresh("Ds_LTIFA12");//바인딩을 해줌
		util.setRowPosition(Ds_LTIFA12, 0);
    }
//	상품명&계약자명 입력이 안되서 하드코딩 
   	Edit0.setValue( Ds_LTIEA00.getCellData( 0 , "sGdCdName" )  );
	edt_sCrtorName.setValue( Ds_LTIEA00.getCellData( 0 , "sCrtorName" )  );

    // 조회후 화면 컨트롤    
    scwin.lfn_Search_After();
};

