scwin.lfn_inputChk = function(obj) {
    // 계약번호
    if (msk_sCrNo.getValue() == "" || msk_sCrNo.getValue() == null) {
        wprom.alert("계약번호를 입력하세요.");
        return false;
    }
    return true;

};

