//---------------------------------------------------------------------------
scwin.grd_issuRecpList_OnCellClick = function(nRow, nCell) {

    if (nCell == 0) {
        if (Ds_LTIFA12.getCellData(nRow, "sSelYn") == "0") {
            // 설정
            Ds_LTIFA12.setCellData(nRow, "sSelYn", "0"); //선택여부
            btn_Issu.setDisabled(true);
        } else {
            for (var i = 0; i < Ds_LTIFA12.getRowCount(); i++) {
                Ds_LTIFA12.setCellData(i, "sSelYn", "0"); //선택여부 초기화
            }

            //var isAuthByID = nfn_checkAuthByID(SOBJ_LOAD, DOBJ_LOAD, "ds_code", gUserID , "22040");
            //trace("isAuthByID : " + isAuthByID + " true : " + true + " false : " + false);
            var sIssuFlag = Ds_LTIFA12.getCellData(nRow, "sIssuFlag"); //발행여부 0가능, 1불가능
            if (sIssuFlag == "1") {
//                if (nfn_checkAuthByID(SOBJ_LOAD, DOBJ_LOAD, "ds_code", gds.gUserID, "22040")) 
                if (nfn_checkAuthByID( "ds_code", util.getGdsValue("gUserID"), "22040")){
                    Ds_LTIFA12.setCellData(nRow, "sSelYn", "1"); //선택여부
                    btn_Issu.setDisabled(false);
                } else {
                    wprom.alert("이미 발행된 영수증입니다.");
                }
            } else {
                // 설정
                Ds_LTIFA12.setCellData(nRow, "sSelYn", "1"); //선택여부
                btn_Issu.setDisabled(false);
            }
        }
    }

    //버튼 활성화
    if (wconv.findRow(Ds_LTIFA12, "sSelYn", "1") < 0) {} else {}
};

