scwin.common_OnKeyDown = function(obj, nChar, bShift, bCtrl, bAlt, LLParam, HLParam) {
    if (nChar == 13) {
        scwin.lfn_Search_Async(1);
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_crntDate
// 내용        : 서버날짜 가져오기
//---------------------------------------------------------------------------
scwin.lfn_crntDate = function() {
    // 현재일
    gfn_getServerTime();
    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간

    sDate = wconv.mid(sysDate, 0, 8);
    sTime = wconv.mid(sysDate, 8, 6);

};

//scwin.promise.btn_Issu_OnClick 에서 promise. 삭제
scwin.btn_Issu_OnClick = function(obj) {
    var promise = Promise.resolve();
    scwin.lfn_crntDate();
    currentYMD = wconv.mid(sysDate, 2, 6);
//    sIssID = gds.gUserID + currentYMD;
	sIssID = util.getGdsValue("gUserID") + currentYMD;
    // 멀티 발행처리
    nRow = Ds_LTIFA12.getRowCount();

    for (i = 0; i < nRow; i++) {
        if (Ds_LTIFA12.getCellData(i, "sSelYn") == "1") {
                sCrNo = Ds_LTIFA12.getCellData(i, "sCrNo"); // 계약번호
                sRecpNo = Ds_LTIFA12.getCellData(i, "sRecpNo"); // 영수증번호
                nDlSeqno = Ds_LTIFA12.getCellData(i, "nDlSeqno"); // 영수증번호

            //금종구분
            // 영수증 발행정보 Update
            // if(lfn_recpIsuuUpt(sCrNo,sRecpNo,nDlSeqno)){
            if (issuObjFlg == "01") {
                promise = promise.then(function(rtnVal) {     


//                    return scwin.promise.openPvRecp(sCrNo, nDlSeqno, sRecpNo, gds.gUserDepartName, gds.gUserEmpName, sDate, sTime, sIssID);
					return scwin.openPvRecp(sCrNo, nDlSeqno, sRecpNo, util.getGdsValue("gUserDepartName"), util.getGdsValue("gUserEmpName"), sDate, sTime, sIssID);
                });
            } else if (issuObjFlg == "02") {
                promise = promise.then(function(rtnVal) {
//                    return scwin.promise.openPvRecpCard(i, nDlSeqno, gds.gUserDepartName, gds.gUserEmpName, sDate, sTime, sIssID);
					return scwin.openPvRecpCard(i, nDlSeqno, util.getGdsValue("gUserDepartName"), util.getGdsValue("gUserEmpName"), sDate, sTime, sIssID);
                });
            }
        }
    }
    return promise;
};
