	util.setRowPosition(Ds_LTIFA12In, 0);

	console.log("ds_Save_chk 값=======================>\n"
	+ds_Save_chk.getAllData());
    // 정상여부판단	
    if (warning.getRowCount() != 0) {
        wprom.alert(warning.getCellData(0, "msg"));
        return false;
    }
    return true;
};

