//---------------------------------------------------------------------------
// tobe : scwin.getRepoServiceNm
// asis : 
// 내용 : oz에 해당하는 기간계 서비스 명가져옴.
//---------------------------------------------------------------------------
scwin.getRepoServiceNm = function(ozName){	

	taskValue = "";
	actionValue = "";

	if( ozName == "F240_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE240Task"; actionValue =	"printF240_RepV01";}
	else if( ozName == "F260_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE230Task"; actionValue =	"printF260_RepV01";}
	else if( ozName == "B990_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE250Task"; actionValue =	"printB990_RepV01";}

	console.log("레포트=====================>\n"
		+"ozName:"+ozName+"/\n"
		+"taskValue:"+taskValue+"/\n"
		+"actionValue:"+actionValue+"/\n"
		+"====================================\n"
		);

	return taskValue+"^"+actionValue;
};
