scwin.init = function() {
    /** 링크 콤보 데이터를 가저온다. 
        반드시 링크 데이터셋 ID = Ds_LinkList, 링크콤보 ID = cmb_linkMenu !!!!!
        링크 버튼 ID = btn_linkMenu !!!!!*/
    //gfn_GetLinkList("G170");

	edt_sInqrFlagCd.hide();

    //----- 버튼 처리(입력, 수정, 삭제, 결재, 취소, 인쇄, 엑셀, 닫기)
	gfn_btnEnable(false, false, false, false, false, false, false, true);

	//Mask Edit 정규식 입력
	//"######-#ZZZZZZ" , "A-####-#######"
	var maskRegExp = [/^[0-9\s]{7}[A-Z0-9\s]{6}$/, /^[A-Z\s]{1}[0-9\s]{11}$/, /^[a-zA-Z0-9\s]{7}$/];
	var maxLength  = [13, 12, 7];
	var chkIndex   = 0;
	msk_sInqrFlagCd_Event = new wMaskEditObj(msk_sInqrFlagCd, maskRegExp, maxLength, chkIndex);

    // 기본사항 입력.
    scwin.bascMattSet();
};

