scwin.bascMattSet = function() {
    // 현재시간 setting
    //global.http.Sync = true;
    //getServerTime();
    //var sToday = gDs_ServerTime.GetColumn(0, "sToday");
    //sToday = sToday.substr(0,8);
    //global.http.Sync = false;
    //sToday = wconv.subStr(gfn_GetSysTime(), 0, 8);

    var sToday = WebSquare.date.getCurrentServerDate("yyyyMMdd");
    cal_sStndEndDate.setValue(sToday);
    cal_sStndStrtDate.setValue(wconv.addMonth(sToday, -1));

    // 지급상태조회
    gfn_getCommonCode("DsWs_PymStat", "31", "PymStat");
    scwin.getPymStatCd();

    cmb_sRtnFlag.focus();

    btn_search.addClass("a_disabled");
};


//---------------------------------------------------------------------------
// function 명 : getPymStatCd
// 내용        : 지급상태 combo box 허용값은 지급완료, 미지급만
//---------------------------------------------------------------------------
scwin.getPymStatCd = function() {
    util.removeRow(DsWs_PymStat, 2);
    util.removeRow(DsWs_PymStat, 1);

    cmb_sPymStatCd.setNodeSet("data:Ds_PymStat", "sHnglCdName", "sCd");

    util.appendRow(Ds_PymStat);
    Ds_PymStat.setCellData(0, "sCd", "");
    Ds_PymStat.setCellData(0, "sHnglCdName", "- 전체 -");

    util.appendRow(Ds_PymStat);
    Ds_PymStat.setCellData(1, "sCd", DsWs_PymStat.getCellData(0, "sCd"));
    Ds_PymStat.setCellData(1, "sHnglCdName", DsWs_PymStat.getCellData(0, "sHnglCdName"));

    util.appendRow(Ds_PymStat);
    Ds_PymStat.setCellData(2, "sCd", DsWs_PymStat.getCellData(1, "sCd"));
    Ds_PymStat.setCellData(2, "sHnglCdName", DsWs_PymStat.getCellData(1, "sHnglCdName"));

    cmb_sPymStatCd.setSelectedIndex(0);
};


//---------------------------------------------------------------------------
// function 명 : cmb_sInqrFlag_OnChanged
// 내용        : 조회구분 선택에 따른 mask 변형
//---------------------------------------------------------------------------
scwin.cmb_sInqrFlag_OnChanged = function(info) { //function(obj, strCode, strText, nOldIndex, nNewIndex) {

	var oldValue = info.oldValue;
    var newValue = info.newValue;
    var oldSelectedIndex = info.oldSelectedIndex;
    var newSelectedIndex = info.newSelectedIndex;

    if (oldSelectedIndex != newSelectedIndex) {
    	edt_sInqrFlagCd.setValue("");
        msk_sInqrFlagCd.setValue("");
        msk_sInqrFlagCd.setDisabled(false);

        //btn_search.Enable = true;
       	btn_search.removeClass("a_disabled");
        if (newValue == "0") {
			edt_sInqrFlagCd.hide();//.Visible = false; //csr
            msk_sInqrFlagCd.setDisplayFormat("");
            msk_sInqrFlagCd.setDisabled(true);
            btn_search.setDisabled(true);
			msk_sInqrFlagCd.show();//.Visible = true; //csr
            btn_search.addClass("a_disabled");
        } else if (newValue == "2") {
			msk_sInqrFlagCd.show();//.Visible = true;
			edt_sInqrFlagCd.hide();//.Visible = false;
        	msk_sInqrFlagCd_Event.setIndex(0);  //"######-#ZZZZZZ";
            msk_sInqrFlagCd.setDisplayFormat("######-#######");
             msk_sInqrFlagCd.setPlaceholder("______-_______");
            btn_search.setDisabled(true);
            btn_search.addClass("a_disabled");
			msk_sInqrFlagCd.focus();            
        } else if (newValue == "1") { //계약번호
			edt_sInqrFlagCd.hide();//.Visible = false;
			msk_sInqrFlagCd.show();//.Visible = true;
        	btn_search.removeClass("a_disabled");
        	msk_sInqrFlagCd_Event.setIndex(1);  //"A-####-#######";
            msk_sInqrFlagCd.setDisplayFormat("#-####-#######");
            msk_sInqrFlagCd.setPlaceholder("_-____-_______");
            btn_search.setDisabled(false);
            btn_search.removeClass("a_disabled");
			msk_sInqrFlagCd.focus();
		}
		else if(newValue == "3") { //계약자명
			edt_sInqrFlagCd.show();//.Visible = true;
			msk_sInqrFlagCd.hide();//.Visible = false;
			btn_search.setDisabled(true);//.Enable = false;
			edt_sInqrFlagCd.focus();
		}
		else if(newValue == "4" || newValue == "5" || newValue == "6") { //조직코드,취급자,사용인
			edt_sInqrFlagCd.hide();//.Visible = false;
			msk_sInqrFlagCd.show();//.Visible = true;
			msk_sInqrFlagCd_Event.setIndex(2);  //"@@@@@@@";
			msk_sInqrFlagCd.setDisplayFormat("#######");
			msk_sInqrFlagCd.setPlaceholder("_______");
			btn_search.setDisabled(false);//.Enable = true;
			msk_sInqrFlagCd.focus();
	    }
    }
};

