scwin.lfn_Search_Async = function() {
	util.eventAsyncCall(this, scwin.lfn_Search, arguments);
}
scwin.lfn_Search = function() {

    Ds_totCnt.removeAll();
    DsWs_totCnt.removeAll();
    Ds_ExptMwayEnd.removeAll();
    DsWs_ExptMwayEnd.removeAll();

    scwin.commSearch(1);

    if (DsWs_ExptMwayEnd.getRowCount() > 0) {
        // 페이징처리를 위해 전체 총데이터수를 구한다..
        gfn_MakePagingData(Ds_Page, DsWs_totCnt.getCellData(0, "nCount"), pageSize, pageCount);
        Cmb_Page.setValue("1");
    } else {
        gfn_MakePagingData(Ds_Page, 0, pageSize, pageCount);
        Cmb_Page.setValue("");
    }

	gfn_LogCreditInfoVt(Ds_ExptMwayEnd, "", "S", "sCrtorCd");
};

//---------------------------------------------------------------------------
// function 명 : commSearch
// 내용        : 페이징 조회
//---------------------------------------------------------------------------	 
scwin.commSearch = function(pageNo) {
    if (scwin.chkEssnInpt() == false) {
        return;
    }

	DsWs_totCnt.removeAll();
	DsWs_ExptMwayEnd.removeAll();
	DsWs_Warning.removeAll();

    var transObj = new callService("com.inswave.longterm.pay.pin.task.GBG170Task", "selectExptMwayEndObjtCr", pageSize, pageNo);
	transObj.setUrl("/contract/CBSC079MController.do");
	transObj.setId("interfaceSample");
	transObj.setParam("sRtnFlag"       , cmb_sRtnFlag.getValue());      // 환급구분
	transObj.setParam("sStndStrtDate"  , cal_sStndStrtDate.getValue()); // 기준시작일자
	transObj.setParam("sStndEndDate"   , cal_sStndEndDate.getValue());  // 기준종료일자
	transObj.setParam("sInqrFlag"      , cmb_sInqrFlag.getValue());     // 구분
	transObj.setParam("sInqrFlagCd"    , msk_sInqrFlagCd.getValue());   // 구분코드
	transObj.setParam("sPymStatCd"     , cmb_sPymStatCd.getValue());    // 지급상태

	transObj.setXPath("//*/hashtable/data/vector/data/GBG170");
	transObj.addXPath("//*/hashtable/data/nCount");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	DsWs_ExptMwayEnd.setJSON(_tranSync.getDsJson("GBG170"));
	DsWs_totCnt.setJSON(_tranSync.getDsJson("nCount"));
	DsWs_Warning.setJSON(_tranSync.getDsJson("WARNING"));

    if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
		wprom.alert(DsWs_Warning.getCellData(0, "msg"));
    } else {
        if (DsWs_ExptMwayEnd.getRowCount() == 0) {
            wprom.alert("해당 데이터가 없습니다.");
        } else {
            var sInsurTerm = "";
            var sCrtorCd = "";
            for (var i = 0; i < DsWs_ExptMwayEnd.getRowCount(); i++) {
                sCrtorCd = scwin.getCrtorCdMask(DsWs_ExptMwayEnd.getCellData(i, "sCrtorCd"));
                sInsurTerm = scwin.getDateMask(DsWs_ExptMwayEnd.getCellData(i, "sInsurStrtDate")) + " ~ " + scwin.getDateMask(DsWs_ExptMwayEnd.getCellData(i, "sInsurEndDate"));
                DsWs_ExptMwayEnd.setCellData(i, "sCrtorCd", sCrtorCd);
                DsWs_ExptMwayEnd.setCellData(i, "sInsurTerm", sInsurTerm);
            }
            Ds_ExptMwayEnd.removeAll();
            Ds_ExptMwayEnd.setJSON(DsWs_ExptMwayEnd.getAllJSON());
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : getCrtorCdMask
// 내용        : 입력된 계약자코드를 Length에 따라 변경한다.
//---------------------------------------------------------------------------
scwin.getCrtorCdMask = function(strText) {
    if (strText.length == 13) {
        strText = strText.substr(0, 6) + "-" + strText.substr(6);
    } else if (strText.length == 10) {
        strText = strText.substr(0, 3) + "-" + strText.substr(3, 2) + "-" + strText.substr(5);
    }
    return strText;
};

//---------------------------------------------------------------------------
// function 명 : getDateMask
// 내용        : 입력된 날짜를 DATE MASK 형태로 변경한다.
//---------------------------------------------------------------------------
scwin.getDateMask = function(strText) {
    if (strText.length != 8) {
        return false;
    }
    strText = strText.substr(0, 4) + "-" + strText.substr(4, 2) + "-" + strText.substr(6);
    return strText;
};

//---------------------------------------------------------------------------
// function 명 : chkEssnInpt
// 내용        : 필수입력 check
//---------------------------------------------------------------------------
scwin.chkEssnInpt = function() {

    if (cmb_sInqrFlag.getValue() == "1" || cmb_sInqrFlag.getValue() == "2") {
        var sInqrFlagLen = wconv.length(msk_sInqrFlagCd.getValue());
        if (cmb_sInqrFlag.getValue() == "2") {
            if (sInqrFlagLen < 13) {
                wprom.alert(cmb_sInqrFlag.getText() + " 입력 오류입니다!");
                msk_sInqrFlagCd.setValue("");
                msk_sInqrFlagCd.focus();
                return false;
            }
        } else if (cmb_sInqrFlag.getValue() == "1") {
            if (sInqrFlagLen < 12) {
                wprom.alert(cmb_sInqrFlag.getText() + " 입력 오류입니다!");
                msk_sInqrFlagCd.setValue("");
                msk_sInqrFlagCd.focus();
                return false;
            }
        }
    } else if (cmb_sInqrFlag.getValue() == "3" || cmb_sInqrFlag.getValue() == "4" || 
                cmb_sInqrFlag.getValue() == "5" || cmb_sInqrFlag.getValue() == "6")  {
		if(msk_sInqrFlagCd.getValue() == "" && edt_sInqrFlagCd.getValue() == "") {
			alert("조회구분값을 입력하세요.");
			return false;
		}
    }
    return true;
};

