scwin.btn_search_OnClick = function(obj) {

    var promise = Promise.resolve();

    if (cmb_sInqrFlag.getValue() == "1") {
        promise = promise.then(function(rtnVal) {
            // 계약번호
            //return wprom.promise.popupOpen("/ui/cbsc/CBSC031P.xml", "1", 704, 552, "TaskBar=true Resize=true");
			var popInfo = {
				"width"     : 720,
				"height"    : 625,
				"indata"    : "", //indata,
				"title"     : "계약찾기"
			};
			return wprom.popupOpen("/ui/cbsc/CBSC031P.xml", popInfo);
        });
        promise = promise.then(function(rst) {
            if (wconv.length(rst) != 0) {
                var arrRst = (rst + "").split("^");
                msk_sInqrFlagCd.setValue(arrRst[0]);
            }
        });
    }  else if (cmb_sInqrFlag.getValue() == "2") {
        promise = promise.then(function(rtnVal) {
            // 계약자코드
			var popInfo = {
				"width"     : 660,
				"height"    : 532,  
				"indata"    : "", //indata,
				"title"     : "고객검색"
			};
            return wprom.popupOpen("/ui/cbso/CBSO004P.xml", popInfo);
        });
        promise = promise.then(function(rst) {

            if (wconv.length(rst) != 0) {
                var arrRst = (rst + "").split("^");
                msk_sInqrFlagCd.setValue(arrRst[0]);
            }
        });
    } else if(cmb_sInqrFlag.getValue() == "4") {	// 조직코드
	    promise = promise.then(function(result) {
		    var sArg = {};
			var popInfo = {
					"width"     : 730,
					"height"    : 500, 
					"title"     : "조직 찾기 [0120]",
			};
			return wprom.popupOpen("/ui/cbso/CBSO020P.xml", popInfo);		// 조직 찾기 팝업 처리
	    });
	    promise = promise.then(function(rst) {
			if(wconv.length(rst) != 0){
				var arrRst = (rst + "").split("^");
				msk_sInqrFlagCd.setValue( arrRst[0] );
			}
		});	
	}	else if(cmb_sInqrFlag.getValue() == "5" || cmb_sInqrFlag.getValue() == "6") {	// 취급자, 사용인
		    promise = promise.then(function(result) { 
			var popInfo = {
					"width"     : 660,
					"height"    : 430, 
					"callback"  : "",
					"title"     : "사원찾기 [R960_Pop]",
				};
				return wprom.popupOpen("/ui/cbso/CBSO015P.xml", popInfo);			// 사원 찾기 팝업 처리[/popup::R960_Pop.xml]
	    });	 
	    promise = promise.then(function(rst) {
			if(wconv.length(rst) != 0){
				var arrRst = (rst + "").split("^");
				msk_sInqrFlagCd.setValue( arrRst[0] );
			}	    	
	    });

	}
};

