//---------------------------------------------------------------------------
// function 명 : cal_sStndStrtDate_OnChanged
// 내용        : 기준시작일 선택시 Event 처리
//---------------------------------------------------------------------------
scwin.cal_sStndStrtDate_OnChanged = function(info) {
	//console.log("[OnviewChange]");
    if (wconv.toNumber(cal_sStndStrtDate.getValue()) > wconv.toNumber(cal_sStndEndDate.getValue())) {
        wprom.alert("기준시작일 입력 오류입니다.!");
        cal_sStndStrtDate.setValue(info.oldValue);
        return false;
    }
};

//---------------------------------------------------------------------------
// function 명 : cal_sStndEndDate_OnChanged
// 내용        : 기준시작일 선택시 Event 처리
//---------------------------------------------------------------------------
scwin.cal_sStndEndDate_OnChanged = function(info) {
    if (wconv.toNumber(cal_sStndStrtDate.getValue()) > wconv.toNumber(cal_sStndEndDate.getValue())) {
        wprom.alert("기준종료일 입력 오류입니다.!");
        cal_sStndEndDate.setValue(info.oldValue);
        return false;
    }
};

