//---------------------------------------------------------------------------
// function 명 : lfn_Search_OnKeyDown
// 내용        : 조회기준 입력 후 엔터 이벤트
//---------------------------------------------------------------------------
scwin.lfn_Search_OnKeyDown_Async = function(e) {
	if (e.keyCode == 13) {
		util.eventAsyncCall(this, scwin.lfn_Search_OnKeyDown, arguments);
	}
}
scwin.lfn_Search_OnKeyDown = function(e) {
	//console.log("[OnKeyDown]");
    if (e.keyCode == 13) {
    	btn_main_search.focus();
        scwin.lfn_Search();
    }
};

//---------------------------------------------------------------------------
// function 명 : fn_dsp_sCrtorCd
// 내용        : 계약자코드 displayFormat 설정
//---------------------------------------------------------------------------
scwin.fn_dsp_sCrtorCd = function(dspV) {
	return gfn_changeMask(dspV ,1)
};


//---------------------------------------------------------------------------
// function 명 : fn_dsp_sCrtorName
// 내용        : 계약자명 displayFormat 설정
//---------------------------------------------------------------------------
scwin.fn_dsp_sCrtorName = function(dspV) {
	return gfn_changeMask(dspV ,4)
};


scwin.lfn_Grid_CustFormat = function(data, formattedData, rowIndex, colIndex) {
	if (grid_1.getColumnID(colIndex) == "sCrNo") {
		grid_1.setCellStyle(rowIndex, colIndex, "text-decoration", "underline");
		grid_1.setCellStyle(rowIndex, colIndex, "font-weight", "bold");
	}
	return formattedData;
};
