//	###################################################

var gUserID = util.getGdsValue("gUserID") ;    // global 사용자 ID
var gsSysDate; 	// global 현재일자
var gDuty;    		// global 직급구분

//**************************************************************************************************
//  화면 로드
//**************************************************************************************************
scwin.init = function() { 
	console.log("[/CBSC092M.xml] [init()] ==> [초기화] [TEST_01]" );

 	// 공통 버튼 권한 설정 - |1.입력|2.수정|3.삭제|4.결재|5.취소|6.인쇄|7.엑셀   총 7개
	gfn_btnEnable(false, false, false, false, true, false, false); 

 	gsSysDate = WebSquare.date.getCurrentServerDate("yyyyMMdd");
	gDuty = util.getGdsValue("gUserPosition");    // global 직급구분 
  	//console.log("[/CBSC092M.xml] [init()] ==> [초기화] [gsSysDate]"+ gsSysDate +"[gDuty]"+ gDuty );

  	//초기화
	scwin.initialize(0); 	// 수납확정일별 Tab 
	Tab0.setSelectedTabIndex(1);	// 1 번째 Tab 설정
	scwin.initialize(1); 	// 취급자별Tab  
	Tab0.setSelectedTabIndex(2);	// 2 번째 Tab 설정
	scwin.initialize(2);	// 계약번호별 Tab
	Tab0.setSelectedTabIndex(3);	// 3 번째 Tab 설정
	scwin.initialize(3);	// 카드번호별 Tab
	Tab0.setSelectedTabIndex(4);	// 4 번째 Tab 설정
	scwin.initialize(4);	// 수납번호별 Tab
	Tab0.setSelectedTabIndex(5);	// 5번째 Tab 설정
	scwin.initialize(5);	// 수납확정자별 Tab
	Tab0.setSelectedTabIndex(0);	// 0 번째 Tab 설정

	cmb_linkMenu.hide();		// 링크 메뉴 콤보 숨기기 설정 
	btn_linkMenu.hide();		// 링크 메뉴 버튼 숨기기 설정 

//	Tab0.setSelectedTabIndex(1);	// 1 번째 Tab 설정	  			// TEST @@@ ===>  
//	Tab0_tab2_Cal_sFromDate.setValue("20171101"); 				// 회계일자1		 // TEST @@@ ===>
//	Tab0_tab2_Edt_sLastAgnt.setValue("2090037"); 					// 취급자 사번	 // TEST @@@ ===>
 	//Tab0_tab6_Edt_sUserID.setValue("2090037"); 					// 수납확정자, 취급자 사번): 수납확정일별 Tab	  // TEST @@@ ===>
	// Tab0_tab3_Edt_sPolicyNo.setValue("L23110000038");		// 계약번호: 계약번호별 Tab	    // TEST @@@ ===> 
	// Tab0_tab4_Edt_sCardNo.setValue("111111C0000014");		// 카드번호: 카드번호별 Tab    	// TEST @@@ ===> 
	// Tab0_tab5_Edt_sCollNo.setValue("RR00001");					// 수납번호:  수납번호별 Tab	 	 // TEST @@@ ===>  
};

//**************************************************************************************************
//  화면 초기화
//**************************************************************************************************
scwin.initialize = function(ntype) {
	//console.log("[/CBSC092M.xml] [initialize()] ==> [화면 초기화] [TEST] [Tab_ntype]"+ ntype );

	var idx = ntype;  	// Tab Index

	if (idx == 0 ) {			 // 수납확정일별 Tab(Tab0) 이면
		Tab0_tab1_Cal_sAcctDate.setValue(gsSysDate); // 회계일자
      	//console.log("[/CBSC092M.xml] [initialize()] ==> [Tab1. 수납확정일별  Tab] [Tab0_tab1_Cal_sAcctDate]"+ Tab0_tab1_Cal_sAcctDate.getValue() );

       // 2004-01-19 jini, 직급구분[sDuty] = '12','16'이면 수납확정자를 조회할수 없음
        if (gDuty == "12" || gDuty == "16") {
            Tab0_tab1_Edt_sUserID.setValue(gds.gUserID); // 수납확정자
            Tab0_tab1_Edt_sUserIDName.setValue(gds.gUserEmpName);
            Tab0_tab1_Edt_sUserID.setDisabled(false); 			// 수납확정자 비활성화 
            Tab0_tab1_Btn_sUserID.setDisabled(false); 			// 수납확정자 버튼 비활성화            
        } else if (gds.gUserID.substring(0, 1) == "3") {
            Tab0_tab1_Edt_sUserID.setValue(gds.gUserID); // 수납확정자
            Tab0_tab1_Edt_sUserIDName.setValue(gds.gUserEmpName);
            Tab0_tab1_Edt_sUserID.setDisabled(false); 			// 수납확정자 비활성화 
            Tab0_tab1_Btn_sUserID.setDisabled(false); 			// 수납확정자 버튼 비활성화  
       } else {
           Tab0_tab1_Edt_sUserID.setValue("");	// 수납확정자
           Tab0_tab1_Edt_sUserIDName.setValue("");
       } 

	} else if (idx == 1 ) {		// 취급자별 Tab(Tab1) 이면  
        Tab0_tab2_Cal_sFromDate.setValue(gsSysDate);	// 회계시작일자
        Tab0_tab2_Cal_sToDate.setValue(gsSysDate);		// 회계종료일자
 	 	//console.log("[/CBSC092M.xml] [initialize()] ==> [Tab2. 취급자별 Tab] [회계시작일자]"+ Tab0_tab2_Cal_sFromDate.getValue() );

        //2004-01-19 jini, 직급구분[sDuty] = '12','16'이면 취급자를 조회할수 없음
        if (gDuty == "12" || gDuty == "16") {
            Tab0_tab2_Edt_sLastAgnt.setValue(gds.gUserID); // 취급자
            Tab0_tab2_Edt_sLastAgntName.setValue(gds.gUserEmpName);
            Tab0_tab2_Edt_sLastAgnt.Enable = false;
            Tab0_tab2_Btn_sLastAgnt.Enable = false;
        } else if (gds.gUserID.substring(0, 1) == "3") {
            Tab0_tab2_Edt_sLastAgnt.setValue(gds.gUserID); // 취급자
            Tab0_tab2_Edt_sLastAgntName.setValue(gds.gUserEmpName);
            Tab0_tab1_Edt_sUserID.setDisabled(false); 			// 수납확정자 비활성화 
            Tab0_tab1_Btn_sUserID.setDisabled(false); 			// 수납확정자 버튼 비활성화   
//            Tab0_tab2_Edt_sLastAgnt.Enable = false;
//            Tab0_tab2_Btn_sLastAgnt.Enable = false;
        } else {
            Tab0_tab2_Edt_sLastAgnt.setValue(""); // 취급자
            Tab0_tab2_Edt_sLastAgntName.setValue("");
        } 
        Tab0_tab2_Msk_nFundCnt.setValue(0);     
        Tab0_tab2_Msk_nFundAmt.setValue(0);
        Tab0_tab2_Msk_nDiffAmt.setValue(0);
        Tab0_tab2_Msk_nApplyCnt.setValue(0);
        Tab0_tab2_Msk_nApplyAmt.setValue(0); 

	} else if (idx == 2) {	// 계약번호별 Tab(Tab2) 이면 
	        Tab0_tab3_Edt_sPolicyNo.setValue("");	// 계약번호
	        Tab0_tab3_Edt_nChangeNo.setValue(""); // 변경회차
	        Tab0_tab3_Msk_nFundCnt.setValue(0);
	        Tab0_tab3_Msk_nFundAmt.setValue(0);
	        Tab0_tab3_Msk_nDiffAmt.setValue(0);
	        Tab0_tab3_Msk_nApplyCnt.setValue(0);
	        Tab0_tab3_Msk_nApplyAmt.setValue(0);  

    } else if (idx == 3) {	// 카드번호별 Tab(Tab3) 이면
        Tab0_tab4_Edt_sCardNo.setValue(""); // 카드번호
        Tab0_tab4_Cal_sFromDate.setValue(gsSysDate); // 회계일자
        Tab0_tab4_Cal_sToDate.setValue(gsSysDate);

        Tab0_tab4_Msk_nFundCnt.setValue(0);
        Tab0_tab4_Msk_nFundAmt.setValue(0);
        Tab0_tab4_Msk_nDiffAmt.setValue(0);
        Tab0_tab4_Msk_nApplyCnt.setValue(0);
        Tab0_tab4_Msk_nApplyAmt.setValue(0);

    } else if (idx == 4) {	// 수납번호별 Tab(Tab4) 이면
        Tab0_tab5_Cal_sFromDate.setValue(gsSysDate); // 회계일자
        Tab0_tab5_Cal_sToDate.setValue(gsSysDate);
        Tab0_tab5_Edt_sCollNo.setValue("");	 // 수납번호

        Tab0_tab5_Msk_nFundCnt.setValue(0);
        Tab0_tab5_Msk_nFundAmt.setValue(0);
        Tab0_tab5_Msk_nDiffAmt.setValue(0);
        Tab0_tab5_Msk_nApplyCnt.setValue(0);
        Tab0_tab5_Msk_nApplyAmt.setValue(0); 

    } else if (idx == 5) { 	 // Tab6. 수납확정자별 Tab(Tab5) 이면
    	//console.log("[/CBSC092M.xml] [initialize()] ==> [Tab6. 수납확정자별] [sTab6SearhOk]"+ sTab6SearhOk + "[Tab0_tab1_Cal_sAcctDate]"+ Tab0_tab1_Cal_sAcctDate.getValue() );

        Tab0_tab6_Cal_sFromDate.setValue(gsSysDate);	 // 회계일자
        Tab0_tab6_Cal_sToDate.setValue(gsSysDate);

        // 2004-01-19 jini, 직급구분[sDuty] = '12','16'이면 수납확정자를 조회할수 없음
        if (gDuty == "12" || gDuty == "16") {
            Tab0_tab6_Edt_sUserID.setValue(gds.gUserID); // 수납확정자
            Tab0_tab6_Edt_sUserIDName.setValue(gds.gUserEmpName);
            Tab0_tab6_Edt_sUserID.Enable = false;
            Tab0_tab6_Btn_sUserID.Enable = false;
        } else if (gds.gUserID.substring(0, 1) == "3") {
            Tab0_tab6_Edt_sUserID.setValue(gds.gUserID); // 수납확정자
            Tab0_tab6_Edt_sUserIDName.setValue(gds.gUserEmpName);
            Tab0_tab6_Edt_sUserID.Enable = false;
            Tab0_tab6_Btn_sUserID.Enable = false;
        } else {
            Tab0_tab6_Edt_sUserID.setValue("");	 // 수납확정자
            Tab0_tab6_Edt_sUserIDName.setValue("");
        }

       	Tab0_tab6_Msk_nFundCnt.setValue(0);		// 입금합계(건) 
		Tab0_tab6_Msk_nFundAmt.setValue(0);		// 입금합계(원) 
		Tab0_tab6_Msk_nDiffAmt.setValue(0);		// 차액(원)
		Tab0_tab6_Msk_nApplyCnt.setValue(0);		// 계약합계(건)
		Tab0_tab6_Msk_nApplyAmt.setValue(0);   // 계약합계(원)   
    } 
};

/*******************************************/
//공통버튼: 취소
/*******************************************/
scwin.lfn_Cancel = function() {
	var tabIdx = Tab0.getSelectedTabIndex();
    scwin.initialize(tabIdx);
};

//**************************************************************************
// 조회 버튼 클릭 이벤트  ==> 01. 수납확정일별 Tab
//************************************************************************** 
scwin.btn_search_onclick = function() {  
	util.eventAsyncCall(this, scwin.doSearch, arguments);		// 비동기 방식 호출 	
};

