//**************************************************************************
//  수납확정내역 조회
//**************************************************************************
scwin.doSearch = function() {

	//console.log("[/CBSC092M.xml] [doSearch()] ==> [조회] [TEST_01]"); 

	var tabIdx = Tab0.getSelectedTabIndex();  	// 현재 선택된 tab의 index를 반환

	if (scwin.lfn_chkData(tabIdx) == false) return;

	else if(tabIdx == 0) {	// Tab의 1번째 값 이면(수납확정일별 Tab 이면)
		//console.log("[/CBSC092M.xml] [doSearch()] ==> [Tab1. 수납확정일별 Tab 조회] [회계일자]"+ Tab0_tab1_Cal_sAcctDate.getValue() +"[gUserID]"+ gUserID );  

		if ( !isSearchDate2("Ds_Auth", Tab0_tab1_Cal_sAcctDate.getValue(), gUserID, "회계일자")) { 	// 2012.08.29 정해성 추가 ==>  2년이전의 일자에 대해 조회했을경우 자금관리권한이 있는 사용자만 검색 가능
			return;
		} 

		wconv.clear(Ds_ACCBA01Tab1);

 		//console.log("\n\n 		[/CBSC092M.xml] [doSearch()] ==> [01. 수납확정일별 수납확정내역 조회] [TEST_01] #############] =====> ");  

		var MethodObj = new callService("com.inswave.accounting.task.AccountingCollTask", "selectCollSettledListByDate");  	// 01. 수납확정일별 수납확정내역 조회 Obj 
		MethodObj.setId("selectCollSettledListByDate")	;		// ID 셋팅
		MethodObj.setParam("sAcctDate", Tab0_tab1_Cal_sAcctDate.getValue() );	// Param 셋팅(회계일자) 
		MethodObj.setParam("sUserID", Tab0_tab1_Edt_sUserID.getValue() );			// Param 셋팅(수납확정자)   
		MethodObj.setUrl("/contract/CBSC092MController.do"); 	// 서버단 호출 URL
		MethodObj.setXPath("//ACCBA01");		// XPath
		MethodObj.addXPath("//WARNING");	// XPath(No)
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.send();		// 최종 전송 
		//console.log("[/CBSC092M.xml] [doSearch()] ==> [01. 수납확정일별 수납확정내역 조회, 전송_완료] [TEST_91]");   

		//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_ACCBA01Tab1.setJSON(_tranSync.getDsJson("ACCBA01"));		// DS 셋팅(조회 결과) 
		Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  	// Warning DS 셋팅(조회 결과) 
		//console.log("[/CBSC092M.xml] [doSearch()] ==> [I/F 조회 완료] [DS, 조회수]"+ Ds_ACCBA01Tab1.getRowCount()  ); 

		if (Ds_Warning.getRowCount() != 0) {
	        wprom.alert(Ds_Warning.getCellData(0, "msg"));
	        return false;
	    }

	    if (Ds_ACCBA01Tab1.getRowCount() == 0) {		//  DS가 존재 안하면 
			wprom.alert("해당 데이터가 존재하지 않습니다."); 
	        return; 
	    }  		 

	}	else if(tabIdx == 1) {		// Tab의 1번째 값 이면(취급자별 Tab 이면)

		if ( !isSearchDate2("Ds_Auth", Tab0_tab2_Cal_sFromDate.getValue(), gUserID, "회계일자")) { 	// 2012.08.29 정해성 추가 ==>  2년이전의 일자에 대해 조회했을경우 자금관리권한이 있는 사용자만 검색 가능
			return;
		}

		Tab0_tab2_Msk_nFundCnt.setValue(0);			// 입금합계(건)
		Tab0_tab2_Msk_nFundAmt.setValue(0);		// 입금합계(원)
		Tab0_tab2_Msk_nDiffAmt.setValue(0);			// 차액(원)
		Tab0_tab2_Msk_nApplyCnt.setValue(0);		// 계약합계(건)
		Tab0_tab2_Msk_nApplyAmt.setValue(0);		// 계약합계(원)

		wconv.clear(Ds_ACCA001Tab2_1);
		wconv.clear(Ds_ACCB001Tab2_2);

		//console.log("\n\n 		[/CBSC092M.xml] [doSearch()] ==> [02. 취급자별 수납확정내역 조회] [TEST_01] #############] =====> ");  

		var MethodObj = new callService("com.inswave.accounting.task.AccountingCollTask", "selectCollSettledList");  	// 02. 취급자별 수납확정내역 조회 Obj 
		MethodObj.setId("selectCollSettledList");			// ID 셋팅
		MethodObj.setParam("sSelectCd", "1");															// Param 셋팅(선택구분) 
		MethodObj.setParam("sFromDate", Tab0_tab2_Cal_sFromDate.getValue() );		// Param 셋팅(회계시작일자)
		MethodObj.setParam("sToDate", Tab0_tab2_Cal_sToDate.getValue() );				// Param 셋팅(회계종료일자) 
		MethodObj.setParam("sLastAgnt", Tab0_tab2_Edt_sLastAgnt.getValue() );			// Param 셋팅(취급자)   
		MethodObj.setUrl("/contract/CBSC092MController.do"); 	// 서버단 호출 URL
		MethodObj.setXPath("//ACCA001");			// XPath
		MethodObj.addXPath("//ACCB001");			// XPath
		MethodObj.addXPath("//WARNING");		// XPath(No) 
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.send();		// 최종 전송  
		//console.log("[/CBSC092M.xml] [doSearch()] ==> [02. 취급자별 수납확정내역 조회, 전송_완료] [TEST_91]");   

		//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_ACCA001Tab2_1.setJSON(_tranSync.getDsJson("ACCA001"));		// 입금 DS 셋팅(조회 결과) 
		Ds_ACCB001Tab2_2.setJSON(_tranSync.getDsJson("ACCB001"));		// 계약 DS 셋팅(조회 결과) 
		//console.log("[/CBSC092M.xml] [doSearch()] ==> [취급자별 Tab] [입금 DS_갯수]"+ Ds_ACCA001Tab2_1.getRowCount()  ); 
   		//console.log("[/CBSC092M.xml] [doSearch()] ==> [계약 DS_갯수]"+ Ds_ACCB001Tab2_2.getRowCount() +"[Ds_ACCB001Tab2_2]"+ WebSquare.xml.serialize(Ds_ACCB001Tab2_2.getAllXML()) ); 

		Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  		// Warning DS 셋팅(조회 결과) 

		if (Ds_Warning.getRowCount() != 0) {
	        wprom.alert(Ds_Warning.getCellData(0, "msg"));
	        return false;
	    }

	    if (Ds_ACCA001Tab2_1.getRowCount() + Ds_ACCB001Tab2_2.getRowCount() < 1) {	//  DS가 존재 안하면 
			wprom.alert("해당 데이터가 존재하지 않습니다."); 
	        return; 
	    }

		Tab0_tab2_Msk_nFundCnt.setValue(Ds_ACCA001Tab2_1.getRowCount()); 			// 입금합계(건)
		Tab0_tab2_Msk_nFundAmt.setValue(Ds_ACCA001Tab2_1.getColSum("nInAmt"));		// 입금합계(원)
		Tab0_tab2_Msk_nFundAmt.setValue(Ds_ACCA001Tab2_1.getColSum("nInAmt"));		// 입금합계(원)
		Tab0_tab2_Msk_nDiffAmt.setValue(Ds_ACCA001Tab2_1.getColSum("nInAmt") - Ds_ACCB001Tab2_2.getColSum("nExptPrem"));  	// 차액(원)
		Tab0_tab2_Msk_nApplyCnt.setValue(Ds_ACCB001Tab2_2.getRowCount()); 			// 계약합계(건)
		Tab0_tab2_Msk_nApplyAmt.setValue(Ds_ACCB001Tab2_2.getColSum("nExptPrem"));		// 계약합계(원)

	} else if(tabIdx == 2 ) {		// Tab의 2번째 값 이면(계약번호별 Tab 이면)

		Tab0_tab3_Msk_nFundCnt.setValue(0);			// 입금합계(건)
		Tab0_tab3_Msk_nFundAmt.setValue(0);		// 입금합계(원)
		Tab0_tab3_Msk_nDiffAmt.setValue(0);			// 차액(원)
		Tab0_tab3_Msk_nApplyCnt.setValue(0);		// 계약합계(건)
		Tab0_tab3_Msk_nApplyAmt.setValue(0);		// 계약합계(원)

