		Tab0_tab3_Msk_nFundAmt.setValue(Ds_ACCA001Tab3_1.getColSum("nInAmt"));  			// 입금합계(원)
		Tab0_tab3_Msk_nDiffAmt.setValue(Ds_ACCA001Tab3_1.getColSum("nInAmt") - Ds_ACCB001Tab3_2.getColSum("nExptPrem"));  	// 차액(원)
		Tab0_tab3_Msk_nApplyCnt.setValue(Ds_ACCB001Tab3_2.getRowCount()); 			// 계약합계(건)
		Tab0_tab3_Msk_nApplyAmt.setValue(Ds_ACCB001Tab3_2.getColSum("nExptPrem"));		// 계약합계(원) 

