		Tab0_tab4_Msk_nDiffAmt.setValue(Ds_ACCA001Tab4_1.getColSum("nInAmt") - Ds_ACCB001Tab4_2.getColSum("nExptPrem"));  	// 차액(원)
		Tab0_tab4_Msk_nApplyCnt.setValue(Ds_ACCB001Tab4_2.getRowCount()); 			// 계약합계(건)
		Tab0_tab4_Msk_nApplyAmt.setValue(Ds_ACCB001Tab4_2.getColSum("nExptPrem"));		// 계약합계(원) 

