		Tab0_tab6_Msk_nFundCnt.setValue(Ds_ACCA001Tab6_1.getRowCount()); 			// 입금합계(건)
		Tab0_tab6_Msk_nFundAmt.setValue(Ds_ACCA001Tab6_1.getColSum("nInAmt"));  			// 입금합계(원)
		Tab0_tab6_Msk_nDiffAmt.setValue(Ds_ACCA001Tab6_1.getColSum("nInAmt") - Ds_ACCB001Tab6_2.getColSum("nExptPrem"));  	// 차액(원)
		Tab0_tab6_Msk_nApplyCnt.setValue(Ds_ACCB001Tab6_2.getRowCount()); 			// 계약합계(건)
		Tab0_tab6_Msk_nApplyAmt.setValue(Ds_ACCB001Tab6_2.getColSum("nExptPrem"));		// 계약합계(원) 
	} 	
};

//**************************************************************************
// 조회시 필수 선택 데이터 확인
//**************************************************************************
scwin.lfn_chkData = function(ntype) {
	//console.log("[/CBSC092M.xml] [lfn_chkData()] ==> [조회시 필수 선택 데이터 확인] [TEST] [ntype]"+ ntype );  

    // 수납확정일별 
    if (ntype == 0) {
        if (wconv.length(Tab0_tab1_Cal_sAcctDate.getValue()) != 8) {
            wprom.alert("회계시작일자 8자리를 입력해주십시오."); 
            util.focus(Tab0_tab2_Cal_sAcctDateFrom);  // 포커스 설정  
            return false;
        }
    } else if (ntype == 1) {
        if (wconv.length(Tab0_tab2_Cal_sFromDate.getValue()) != 8) {
            wprom.alert("회계시작일자 8자리를 입력해주십시오."); 
			util.focus(Tab0_tab2_Cal_sFromDate);  // 포커스 설정  
            return false;
        }
        if (wconv.length(Tab0_tab2_Cal_sToDate.getValue()) != 8) {
            wprom.alert("회계종료일자 8자리를 입력해주십시오."); 
			 util.focus(Tab0_tab2_Cal_sToDate);  // 포커스 설정  
            return false;
        }
        if (Tab0_tab2_Cal_sFromDate.getValue() > Tab0_tab2_Cal_sToDate.getValue()) {
            wprom.alert("회계일자 이상입니다.");
			util.focus(Tab0_tab2_Cal_sFromDate);  // 포커스 설정  
            return false;
        }
        if (wconv.length(Tab0_tab2_Edt_sLastAgnt.getValue()) != 7) {
            wprom.alert("취급자를 입력해야 합니다.");
			util.focus(Tab0_tab2_Edt_sLastAgnt);  // 포커스 설정  
            return false;
        }
    } else if (ntype == 2) {	// 계약번호별 Tab 이면
        if (wconv.length(Tab0_tab3_Edt_sPolicyNo.getValue()) != 12) {
            wprom.alert("계약번호 12자리를 입력해주십시오.");
            util.focus(Tab0_tab3_Edt_sPolicyNo);  // 포커스 설정  
           return false;
        }
    } else if (ntype == 3) {
        if (Tab0_tab4_Edt_sCardNo.getValue() == "") {
            wprom.alert("카드번호를 입력해주십시오.");
			util.focus(Tab0_tab4_Edt_sCardNo);  // 포커스 설정  
            return;
        }
    } else if (ntype == 4) {
        if (wconv.length(Tab0_tab5_Cal_sFromDate.getValue()) != 8) {
            wprom.alert("회계시작일자 8자리를 입력해주십시오.");
			 util.focus(Tab0_tab3_Edt_sPolicyNo);  // 포커스 설정  
            return false;
        }
        if (wconv.length(Tab0_tab5_Cal_sToDate.getValue()) != 8) {
            wprom.alert("회계종료일자 8자리를 입력해주십시오.");
		 util.focus(Tab0_tab3_Edt_sPolicyNo);  // 포커스 설정  
            return false;
        }
        if (Tab0_tab5_Cal_sFromDate.getValue() > Tab0_tab2_Cal_sToDate.getValue()) {
            wprom.alert("회계일자 이상입니다.");
			util.focus(Tab0_tab5_Cal_sFromDate);  // 포커스 설정  
            return false;
        }
        if (wconv.length(Tab0_tab5_Edt_sCollNo.getValue()) != 7) {
            wprom.alert("수납번호를 입력해야 합니다.");
			 util.focus(Tab0_tab5_Edt_sCollNo);  // 포커스 설정  
            return false;
        }
    } else if (ntype == 5) {  
        if (wconv.length(Tab0_tab6_Cal_sFromDate.getValue()) != 8) {
            wprom.alert("회계시작일자 8자리를 입력해주십시오.");
			util.focus(Tab0_tab6_Cal_sFromDate);  // 포커스 설정  
            return false;
        }     
        if (wconv.length(Tab0_tab6_Cal_sToDate.getValue()) != 8) {
            wprom.alert("회계종료일자 8자리를 입력해주십시오.");
			util.focus(Tab0_tab6_Cal_sToDate);  // 포커스 설정  
            return false;
        }      
        if (Tab0_tab6_Cal_sFromDate.getValue() > Tab0_tab6_Cal_sToDate.getValue()) {
            wprom.alert("회계일자 이상입니다.");
			util.focus(Tab0_tab6_Cal_sFromDate);  // 포커스 설정  
            return false;
        } 
        if (WebSquare.date.dateDiff( Tab0_tab6_Cal_sFromDate.getValue(), Tab0_tab6_Cal_sToDate.getValue()) > 30 ) {  
            wprom.alert("회계일자 조회기간은 30일 이하입니다.");
			util.focus(Tab0_tab6_Cal_sToDate);  // 포커스 설정  
     		return false;
        } 
        if (wconv.length(Tab0_tab6_Edt_sUserID.getValue()) != 7) {
            wprom.alert("수납확정자를 입력해야 합니다.");
			util.focus(Tab0_tab6_Edt_sUserID);  // 포커스 설정  
            return false;
        }
    }

    return;
};

//**************************************************************************************************
//  수납확정자 팝업
//************************************************************************************************** 
scwin.uf_sUserIDPop1 = function(obj, tab) {
	//console.log("[/CBSC092M.xml] [uf_sUserIDPop1()] ==> [사원목록 조회 팝업] [TEST] [tab]"+ tab );

	var promise = Promise.resolve();   // Promise 설정

    promise = promise.then(function(rtnVal) { 
     	var sArg = {"tab":tab 
	               ,"tab_desc":"tab활용"
	               };	 // 팝업 창으로 보낼 자료

		var popInfo = {
			"width"     : 532,
			"height"    : 513,
			"top"       : 240,
			"left"      : 240,
			"indata"    : sArg,		 //indata
			"callback"  : "",
			"title"     : "사원목록"
		};
		return wprom.popupOpen("/ui/cbso/CBSO033P.xml", popInfo);		// 사원목록 조회  
    });	 // end of promise()

    promise = promise.then(function(rst) {
    	 //console.log("[/CBSC092M.xml] [uf_sUserIDPop1()] ==> [promise.then] [TEST] [rst]"+ rst );

        if (wconv.length(rst) != 0) {
            var arrRst = (rst + "").split("^"); 
         	var sTab = arrRst[2]; 
	   		//console.log("[/CBSC092M.xml] [uf_sUserIDPop1()] ==> [promise.then] [TEST] [arrRst[0]]"+ arrRst[0] +"[arrRst[1]"+ arrRst[1] +"[arrRst[2]"+ arrRst[2] ); 

	        if (sTab =="tab2") {  // 수납확정자 팝업을 했으면
		  		Tab0_tab2_Edt_sLastAgnt.setValue(arrRst[0]); 			// 취급자 사번 
				Tab0_tab2_Edt_sLastAgntName.setValue(arrRst[1]);	// 취급자  
				Tab0_tab2_Edt_sLastAgnt.focus();   
			} else if (sTab =="tab6") {  // 수납확정자 팝업을 했으면
		  		Tab0_tab6_Edt_sUserID.setValue(arrRst[0]); 			// 취급자 사번 
				Tab0_tab6_Edt_sUserIDName.setValue(arrRst[1]);	// 취급자
				Tab0_tab6_Edt_sUserID.focus();   	
			} else {
				Tab0_tab1_Edt_sUserID.setValue(arrRst[0]); 			// 취급자 사번 
				Tab0_tab1_Edt_sUserIDName.setValue(arrRst[1]);	// 취급자  
				Tab0_tab1_Edt_sUserID.focus();  
			}   
        }
    });	 // end of promise()
};

