};

//**************************************************************************************************
//  수납확정자 박스 내용 변경시(onviewchange: 키보드 조작을 통해 input의 값을 변경한 후, 포커스가 빠져나가자마자 발생)
//************************************************************************************************** 
scwin.Tab0_tab1_Edt_sUserID_OnKillFocus = function(e) {
	//console.log("[/CBSC092M.xml] [Tab0_tab1_Edt_sUserID_OnKillFocus()] ==> [onviewchange 이벤트] [Tab0_tab1_Edt_sUserID.getValue()]"+ Tab0_tab1_Edt_sUserID.getValue() );

	Tab0_tab1_Edt_sUserIDName.setValue("");			// 수납확정자명

	if (wconv.length(Tab0_tab1_Edt_sUserID.getValue()) == 7) { 	 // 수납확정자 코드가 7자리 이면
		scwin.uf_sUserIDPop1(""); 	// 수납확정자 팝업
 	}
};

//**************************************************************************************************
//  수납확정자 팝업  ==> 팝업 no, 수납확정자 조회 후 셋팅(onviewchange 이벤트_결과)
//**************************************************************************************************
scwin.uf_sUserIDPop1 = function(_btn) {
	//console.log("[/CBSC092M.xml] [uf_sUserIDPop1()] ==> [onviewchange 이벤트_결과] [TEST_01] [Edt_sUserID]"+ Tab0_tab1_Edt_sUserID.getValue() +"[Edt_sUserIDName]"+ Tab0_tab1_Edt_sUserIDName.getValue() ); 

    var sPopParm = " psButton=" + _btn; 
  	var argArry = agf_Pop("사원목록", Tab0_tab1_Edt_sUserID.getValue(), Tab0_tab1_Edt_sUserIDName.getValue(), "", sPopParm); 
  	//console.log("[/CBSC092M.xml] [uf_sUserIDPop1()] ==> [TEST_90] [argArry]"+ argArry +"[argArry.length]"+ argArry.length);

    if (argArry == null) {
        return;
    }
    if (argArry[0] == "noneClose") {
       Tab0_tab1_Edt_sLastAgnt.getValue() = "";
        Tab0_tab1_Edt_sLastAgntName.getValue() = "";
		return; 
    }

   	if ( wconv.length(argArry[0]) > 0 ) { 
		Tab0_tab1_Edt_sUserIDName.setValue(argArry[1]);			//  수납확정자명 
		util.focus(Tab0_tab1_Edt_sUserID); 	 // 수납확정자 포커스 설정 
	} else {
        Tab0_tab1_Edt_sUserID.getValue()     = "";
        Tab0_tab1_Edt_sUserIDName.getValue() = "";	//  수납확정자명 
		 util.focus(Tab0_tab1_Edt_sUserID);  // 포커스 설정  
	}
};

