//**************************************************************************************************
//  취급자 팝업
//**************************************************************************************************
scwin.uf_sLastAgntPop = function(_btn) { 
	//console.log("[/CBSC092M.xml] [uf_sLastAgntPop()] ==> [취급자 팝업 이벤트] [TEST_01] [_btn]"+ _btn +"[Edt_sUserID]"+ Tab0_tab2_Edt_sLastAgnt.getValue() +"[Edt_sUserIDName]"+ Tab0_tab2_Edt_sLastAgntName.getValue() ); 

	var sPopParm = " psButton=" + _btn; 
	var argArry = agf_Pop("사원목록", Tab0_tab2_Edt_sLastAgnt.getValue(), Tab0_tab2_Edt_sLastAgntName.getValue(), "", sPopParm); 
	//console.log("[/CBSC092M.xml] [uf_sLastAgntPop()] ==> [TEST_90] [arry 필드 갯수]"+ argArry +"[argArry.length]"+ argArry.length);

	if ( argArry == null ) { return; }
	if ( argArry[0] == "noneClose" ) { 
		Tab0_tab2_Edt_sLastAgnt.setValue(""); 
        Tab0_tab2_Edt_sLastAgntName.setValue(""); 
		return; 
	}
	//console.log("[/CBSC092M.xml] [uf_sLastAgntPop()] ==> [TEST_92] [argArry[0]]"+ argArry[0] +"[argArry[1]"+ argArry[1] ); 

	if (wconv.length(argArry[0]) > 0) { 
		Tab0_tab2_Edt_sLastAgntName.setValue(argArry[1]);	//  취급자명
		util.focus(Tab0_tab2_Edt_sLastAgnt); 	 // 취급자 포커스 설정 
	} else {
        Tab0_tab2_Edt_sLastAgnt.setValue(""); 
        Tab0_tab2_Edt_sLastAgntName.setValue(""); 	//  취급자명
		//Tab0_tab2_Edt_sLastAgnt.SetFocus();
		util.focus(Tab0_tab2_Edt_sLastAgnt); 	 //  포커스 설정 
	}
};

