				Tab0_tab6_Edt_sUserIDName.setValue(arrRst[1]);	// 취급자
				Tab0_tab6_Edt_sUserID.focus();   	
			} else {	// 수납확정일별 Tab에서 팝업을 했으면
				Tab0_tab1_Edt_sUserID.setValue(arrRst[0]); 			// 취급자 사번 
				Tab0_tab1_Edt_sUserIDName.setValue(arrRst[1]);	// 취급자  
				Tab0_tab1_Edt_sUserID.focus();  
			}   
        }
    });	 // end of promise()
};

//**************************************************************************************************
//  수납확정일별 탭의 Grid에서 수납확정자 클릭 시
//************************************************************************************************** 
scwin.Tab0_tab1_Grid01_OnCellClick = function(row, col) {  
	//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [Grid 클릭 이벤트] [TEST_01] [row]"+ row +"[col]"+ col );  

 	var nRow  = row;

	if( col == 1 ) {   //  수납확정자 칼럼 클릭 이면 
//		Tab0_tab6_Msk_nFundCnt.setValue(0);		// 입금합계(건) 
//		Tab0_tab6_Msk_nFundAmt.setValue(0);		// 입금합계(원) 
//		Tab0_tab6_Msk_nDiffAmt.setValue(0);		// 차액(원)
//		Tab0_tab6_Msk_nApplyCnt.setValue(0);		// 계약합계(건)
//		Tab0_tab6_Msk_nApplyAmt.setValue(0);   // 계약합계(원) 

		wconv.clear(Ds_ACCA001Tab6_1);
		wconv.clear(Ds_ACCB001Tab6_2);

		Tab0.setSelectedTabIndex(5);	 // 수납확정자별Tab 으로 이동

		//console.log("\n\n 		[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [06. 수납확정자별 수납확정내역 조회] [TEST_01] #############] =====> ");  

		// 조회조건 셋팅
		Tab0_tab6_Cal_sFromDate.setValue(Ds_ACCBA01Tab1.getCellData(nRow, "sFromDate"));		// 회계시작일자  
		Tab0_tab6_Cal_sToDate.setValue(Ds_ACCBA01Tab1.getCellData(nRow, "sToDate"));	  			// 회계종료일자 
		Tab0_tab6_Edt_sUserID.setValue(Ds_ACCBA01Tab1.getCellData(nRow, "sUserID"));;				// 수납확정자ID
		Tab0_tab6_Edt_sUserIDName.setValue(Ds_ACCBA01Tab1.getCellData(nRow, "sUserIDName"));;		// 수납확정자
//		console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [Grid 클릭 이벤트] [조회조건 셋팅] [회계시작일자_2]"+ Tab0_tab6_Cal_sFromDate.getValue() +"[회계종료일자_2]"+ Tab0_tab6_Cal_sToDate.getValue() 
//			 +"[수납확정자_2]"+ Tab0_tab6_Edt_sUserID.getValue() +"[수납확정자_2]"+ Tab0_tab6_Edt_sUserIDName.getValue()
//		);   

		var MethodObj = new callService("com.inswave.accounting.task.AccountingCollTask", "selectCollSettledList");  	// 06. 수납확정자별 수납확정내역 조회 Obj 
		MethodObj.setId("selectCollSettledList");			// ID 셋팅
		MethodObj.setParam("sSelectCd", "5");															// Param 셋팅(선택구분) 
		MethodObj.setParam("sFromDate", Tab0_tab6_Cal_sFromDate.getValue() );		// Param 셋팅(회계시작일자)
		MethodObj.setParam("sToDate", Tab0_tab6_Cal_sToDate.getValue() );				// Param 셋팅(회계종료일자) 
		MethodObj.setParam("sUserID", Tab0_tab6_Edt_sUserID.getValue() );			// Param 셋팅(수납확정자)  
		MethodObj.setUrl("/contract/CBSC092MController.do"); 	// 서버단 호출 URL
		MethodObj.setXPath("//ACCA001");			// XPath
		MethodObj.addXPath("//ACCB001");			// XPath
		MethodObj.addXPath("//WARNING");		// XPath(No) 
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.send();		// 최종 전송 
		//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [최종 전송_완료] [TEST_91]");  

		//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_ACCA001Tab6_1.setJSON(_tranSync.getDsJson("ACCA001"));		// 입금 DS 셋팅(조회 결과) 
		Ds_ACCB001Tab6_2.setJSON(_tranSync.getDsJson("ACCB001"));		// 계약 DS 셋팅(조회 결과) 
		//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [I/F 조회 완료] [입금 DS_갯수]"+ Ds_ACCA001Tab6_1.getRowCount() +"[Ds_ACCA001Tab6_1]"+ WebSquare.xml.serialize(Ds_ACCA001Tab6_1.getAllXML()) ); 
		//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [I/F 조회 완료] [계약 DS_갯수]"+ Ds_ACCB001Tab6_2.getRowCount() +"[Ds_ACCB001Tab6_2]"+ WebSquare.xml.serialize(Ds_ACCB001Tab6_2.getAllXML()) ); 

		Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  		// Warning DS 셋팅(조회 결과) 

		if (Ds_Warning.getRowCount() != 0) {
	        wprom.alert(Ds_Warning.getCellData(0, "msg"));
	        return false;
	    }

	    if (Ds_ACCA001Tab6_1.getRowCount() + Ds_ACCB001Tab6_2.getRowCount() < 1) {	//  DS가 존재 안하면 
			wprom.alert("해당 데이터가 존재하지 않습니다."); 
	         return false;
	    }
 		//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [최종 전송_완료] [Ds_ACCA001Tab6_1.getRowCount()]"+ Ds_ACCA001Tab6_1.getRowCount());  
 		//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [최종 전송_완료] [차액(원)] [Ds_ACCA001Tab6_1.nInAmt()]"+ Ds_ACCA001Tab6_1.getColSum("nInAmt") +"[Ds_ACCB001Tab6_2.nExptPrem()]"+ Ds_ACCB001Tab6_2.getColSum("nExptPrem")  );  

 		sTab6SearhOk = "Y";   // 수납확정자별 Tab 조회 성공

		Tab0_tab6_Msk_nFundCnt.setValue(Ds_ACCA001Tab6_1.getRowCount()); 					// 입금합계(건) 
		Tab0_tab6_Msk_nFundAmt.setValue(Ds_ACCA001Tab6_1.getColSum("nInAmt"));  			// 입금합계(원) 
		Tab0_tab6_Msk_nDiffAmt.setValue(Ds_ACCA001Tab6_1.getColSum("nInAmt") - Ds_ACCB001Tab6_2.getColSum("nExptPrem"));  	// 차액(원)
		Tab0_tab6_Msk_nApplyCnt.setValue(Ds_ACCB001Tab6_2.getRowCount()); 			// 계약합계(건)
		Tab0_tab6_Msk_nApplyAmt.setValue(Ds_ACCB001Tab6_2.getColSum("nExptPrem"));		// 계약합계(원)  
	} 

	//console.log("[/CBSC092M.xml] [Tab0_tab1_Grid01_OnCellClick()] ==> [최종 전송_완료] [TEST_end]");  
};

//**************************************************************************************************
// Grid 칼럼 expression  처리 함수
//**************************************************************************************************
scwin.lfn_getColumnValue = function(data, formattedData, rowIndex, colIndex) {
	//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [TEST_01] [nTabIndex]"+ nTabIndex +"[rowIndex]"+ rowIndex +"[colIndex]"+ colIndex  ); 

	var sRslt = ""; 
	var tabIdx = Tab0.getSelectedTabIndex();  // tab의 index

	var Objects = WebSquare.util.getComponentById(this.id);   // WebSquare 타입 Objects 
	//console.log("[/CBSC128P.xml] [lfn_getColumnValue()] ==> [TEST_02] [tabIdx]"+ tabIdx +"[colIndex]"+ colIndex  +"[Objects.getColumnID(colIndex)]"+ Objects.getColumnID(colIndex) ); 

	if (tabIdx  == 0) {  	// Tab1 이면(수납확정일별 Tab)   
		if (Objects.getColumnID(colIndex)  == "sUserID") {  		// 입금 Grid 칼럼이 '수납확정자' 이면  
			var sUserID = Ds_ACCBA01Tab1.getCellData(rowIndex, "sUserID");   // 사용자ID 
			var sUserIDName = Ds_ACCBA01Tab1.getCellData(rowIndex, "sUserIDName");	  // 사용자  
			sRslt = sUserID +'   '+ sUserIDName;			// 수납확정자 
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [입금 Grid 칼럼이 '수납번호' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    

		} else {	// 입금 Grid 칼럼이'회계일자(수납확정일별)' 이면
			var sFromDate = Ds_ACCBA01Tab1.getCellData(rowIndex, "sFromDate");   // 회계일자1 
			var sToDate = Ds_ACCBA01Tab1.getCellData(rowIndex, "sToDate");		// 회계일자2   
			sRslt = wconv.substr(sFromDate, 0, 4) +"/"+ wconv.substr(sFromDate, 4, 2) +"/"+  wconv.substr(sFromDate, 6, 2)
			 		 +" ~ "+ wconv.substr(sToDate, 0, 4) +"/"+ wconv.substr(sToDate, 4, 2) +"/"+  wconv.substr(sToDate, 6, 2);	// 회계일자 
		}

	} else if (tabIdx  == 1) {  // Tab2 이면(취급자별 Tab)
		//console.log("[/CBSC128P.xml] [lfn_getColumnValue()] ==> [Tab2 이면(취급자별 Tab)] [Objects.getColumnID(colIndex)]"+ Objects.getColumnID(colIndex) ); 

		if (Objects.getColumnID(colIndex)  == "sCollNo") {  		// 입금 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCA001Tab2_1.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCA001Tab2_1.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCA001Tab2_1.getCellData(rowIndex, "sCollNo");  		// 수납번호

			if(!util.isNull(sCollNo)) {				 
				sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			}
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [입금 Grid 칼럼이 '수납번호' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    
		}  
	 	if (Objects.getColumnID(colIndex)  == "sLastAgnt") {  		// 입금 Grid 칼럼이 '취급자' 이면    
			var sLastAgnt = Ds_ACCA001Tab2_1.getCellData(rowIndex, "sLastAgnt");   // 사용자ID  
			var sLastAgntName = Ds_ACCA001Tab2_1.getCellData(rowIndex, "sLastAgntName");	  // 사용자  

			sRslt = sLastAgnt +'   '+ sLastAgntName;			// 취급자   
		}		

		if (Objects.getColumnID(colIndex)  == "sCollNo2") {  		// 청약 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCB001Tab2_2.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCB001Tab2_2.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCB001Tab2_2.getCellData(rowIndex, "sCollNo");  // 수납번호

			if(!util.isNull(sCollNo)) {				 
				sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			} 
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [청약 Gri 칼럼이 '취급자별' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    
		}    

	} else if (tabIdx  == 2) {  		// Tab3 이면(계약호별Tab)   	
		if (Objects.getColumnID(colIndex)  == "sCollNo") {  		// 입금 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCA001Tab3_1.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCA001Tab3_1.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCA001Tab3_1.getCellData(rowIndex, "sCollNo");  
			sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [입금 Grid 칼럼이 '취급자별' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    
		}  
	 	if (Objects.getColumnID(colIndex)  == "sLastAgnt") {  		// 입금 Grid 칼럼이 '취급자' 이면    
			var sLastAgnt = Ds_ACCA001Tab3_1.getCellData(rowIndex, "sLastAgnt");   // 사용자ID  
			var sLastAgntName = Ds_ACCA001Tab3_1.getCellData(rowIndex, "sLastAgntName");	  // 사용자  
			sRslt = sLastAgnt +'   '+ sLastAgntName;			// 취급자   
		}			

		if (Objects.getColumnID(colIndex)  == "sCollNo2") { 		// 청약 Gri 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCB001Tab3_2.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCB001Tab3_2.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCB001Tab3_2.getCellData(rowIndex, "sCollNo");  	// 수납번호

			if(!util.isNull(sCollNo)) {				 
				sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			} 
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [청약 Gri 칼럼이 '취급자별' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    
		}   	

	} else if (tabIdx  == 3) {  	// Tab4 이면(카드번호별Tab)   
		if (Objects.getColumnID(colIndex)  == "sCollNo") {  		// 입금 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCA001Tab4_1.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCA001Tab4_1.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCA001Tab4_1.getCellData(rowIndex, "sCollNo");  
			sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [입금 Grid 칼럼이 '취급자별' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    
		}  
	 	if (Objects.getColumnID(colIndex)  == "sLastAgnt") {  		// 입금 Grid 칼럼이 '취급자' 이면    
			var sLastAgnt = Ds_ACCA001Tab4_1.getCellData(rowIndex, "sLastAgnt");   // 사용자ID  
			var sLastAgntName = Ds_ACCA001Tab4_1.getCellData(rowIndex, "sLastAgntName");	  // 사용자  
			sRslt = sLastAgnt +'   '+ sLastAgntName;			// 취급자   
		}			

		if (Objects.getColumnID(colIndex)  == "sCollNo2") {  		// 청약 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCB001Tab4_2.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCB001Tab4_2.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCB001Tab4_2.getCellData(rowIndex, "sCollNo");  	// 수납번호

			if(!util.isNull(sCollNo)) {				 
				sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			} 
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [청약 Gri 칼럼이 '취급자별' 이면] [수납번호_Grid2] [sRslt]"+ sRslt );    
		}   

	} else if (tabIdx  == 4) {  	// Tab5 이면(수납번호별Tab)	 
		if (Objects.getColumnID(colIndex)  == "sCollNo") {  		// Tab5에서 Grid 칼럼이 '수납번호' 이면    
			var sAcctDate = Ds_ACCA001Tab5_1.getCellData(rowIndex, "sAcctDate");   // 입금일자
			var sCollBranch = Ds_ACCA001Tab5_1.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCA001Tab5_1.getCellData(rowIndex, "sCollNo");
			sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호  
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [Tab5 이면(수납번호별Tab)_입금 Grid 칼럼이 '수납번호' 이면] [sRslt]"+ sRslt ); 
		}  		 
		if (Tab0_tab5_Grid05_1.getColumnID(colIndex)  == "sLastAgnt") {  		// 입금 Grid 칼럼이 '취급자' 이면    
			var sLastAgnt_New = Ds_ACCA001Tab5_1.getCellData(rowIndex, "sLastAgnt");   // 사용자ID  
			var sLastAgntName_New = Ds_ACCA001Tab5_1.getCellData(rowIndex, "sLastAgntName");	  // 사용자  
			sRslt = sLastAgnt_New +'   '+ sLastAgntName_New;			// 취급자 
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue()] ==> [Tab5 이면(수납번호별Tab)_입금 Grid 칼럼이 '취급자' 이면] [sRslt]"+ sRslt ); 
		}  

		if (Objects.getColumnID(colIndex)  == "sCollNo2") {  		// 청약 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCB001Tab5_2.getCellData(rowIndex, "sAcctDate");   // 입금일자
			var sCollBranch = Ds_ACCB001Tab5_2.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCB001Tab5_2.getCellData(rowIndex, "sCollNo");  	// 수납번호

			if(!util.isNull(sCollNo)) {				 
				sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			}  
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue2()] ==> [Tab5 이면(수납번호별Tab)_청약 Gri 칼럼이 '수납번호' 이면] [sRslt]"+ sRslt ); 
		}  		 

	} else if (tabIdx  == 5) {  		// Tab6 이면(수납확정자별Tab)    
		if (Objects.getColumnID(colIndex)  == "sCollNo") {  		// Tab6에서 Grid 칼럼이 '수납번호' 이면   
			var sAcctDate = Ds_ACCA001Tab6_1.getCellData(rowIndex, "sAcctDate");   // 사용자ID  
			var sCollBranch = Ds_ACCA001Tab6_1.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCA001Tab6_1.getCellData(rowIndex, "sCollNo");
			sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호  
		}  

		if (Objects.getColumnID(colIndex)  == "sLastAgnt") {  		// 입금 Grid 칼럼이 '취급자' 이면   
			var sLastAgnt = Ds_ACCA001Tab6_1.getCellData(rowIndex, "sLastAgnt");   // 사용자ID  
			var sLastAgntName = Ds_ACCA001Tab6_1.getCellData(rowIndex, "sLastAgntName");	  // 사용자  
			sRslt = sLastAgnt +'   '+ sLastAgntName;			// 취급자  
		} 

		if (Objects.getColumnID(colIndex)  == "sCollNo2") {  		// 청약 Grid 칼럼이 '수납번호' 이면
			var sAcctDate = Ds_ACCB001Tab6_2.getCellData(rowIndex, "sAcctDate");   // 입금일자
			var sCollBranch = Ds_ACCB001Tab6_2.getCellData(rowIndex, "sCollBranch");
			var sCollNo = Ds_ACCB001Tab6_2.getCellData(rowIndex, "sCollNo");  	// 수납번호

			if(!util.isNull(sCollNo)) {				 
				sRslt = sAcctDate +'   '+ sCollBranch +'   '+ sCollNo;			// 수납번호
			} 
			//console.log("[/CBSC092M.xml] [lfn_getColumnValue2()] ==> [Tab6 이면(수납확정자별Tab)_청약 Gri 칼럼이 '수납번호' 이면] [sRslt]"+ sRslt ); 
		}  		
	}

	formattedData = sRslt;

	return formattedData;
};
