	    Tab_Step1.Ds_Pibo.setCellData(0, "sPftbpInrpsRelnName", "법정상속인");
	    // 비선택시는 스페이스 처리
	    Tab_Step1.Ds_Pibo.setCellData(0, "sXcptPftbpInrpsRelnCd", "");
	    Tab_Step1.Ds_Pibo.setCellData(0, "sXcptPftbpInrpsRelnName", "");

		//체크박스 매핑
		//보험료 매핑
