	scwin.id_faq_onclick = function() {
		var sGdCd = edt_sGdCd.getValue();

		if (wconv.length(wconv.trim(sGdCd)) > 0) { 
			var oDsJson=[{"sGdCd" : sGdCd}];
			gds.gDs_LinkArg.setJSON(oDsJson);
			gfn_OpenLinkPage("CBSC004M");
		} else {
			alert("상품을 선택하세요.");
		}
	};
