scwin.grd_BntpCd1_OnCellClick = function(row, col) {
    var promise = Promise.resolve();
     switch (col) {
        case 0:
            promise = promise.then(function(rtnVal) {
            	var id = "grd_BntpCd1";
                // 업종 조회(01:개발원코드(화재손해업종))
         	     return scwin.lfn_dialogB610_Pop(col, row, id);
            });
            break;
        case 3: 			  
            util.removeRow(Ds_BntpGrid1, row);
            var insRow = util.insertRow(Ds_BntpGrid1, row);
            /*수작업 변환 개발자 가이드(테스트 중)
                dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
            break;
        default:
            break;
    }
    return promise;
};

