//---------------------------------------------------------------------------
// function 명 : lfn_getBntpCdTask
// 내용        : 업종 조회(01:개발원코드(화재손해업종))
// param       : sStndDate = 기준일자
//             : sBntpLargClsfCd = 업종대분류코드
//             : sBntpLargClsfCd = 업종대분류코드
//---------------------------------------------------------------------------
scwin.lfn_getBntpCdTask = function() {
    // DataSet 초기화
    wconv.clear(Ds_BntpCd);
    wconv.clear(DsWs_BntpCd);
    // Service Object 설정
    //SOBJ_Search.WSDL = gds.gWsdlBasis;
    var sBntpLargClsfCd = "%"; // 업종대분류코드
    var sBntpDurnClsfCd = "%"; // 업종중분류코드
    var sBntpCdName = "%";

    // 서비스 호출 메소드 정의(서비스 오브젝트, 타스크, 액션, 페이지당 사이즈, 가져올 현재 페이지)
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.ssr.pln.task.DBB620Task", "getBntpCd", "", "");
//    gfn_SetParam(MethodObj, "sStndDate", wconv.trim(sStndDate)); // 기준일자
//    gfn_SetParam(MethodObj, "sBntpLargClsfCd", wconv.trim(sBntpLargClsfCd)); // 업종대분류코드
//    gfn_SetParam(MethodObj, "sBntpDurnClsfCd", wconv.trim(sBntpDurnClsfCd)); // 업종중분류코드
//    gfn_SetParam(MethodObj, "sBntpCdName", wconv.trim(sBntpCdName)); // 검색업종명
    //alert(trim(sBntpLargClsfCd) + ":::" + trim(sBntpDurnClsfCd) + ":::" + trim(sBntpCdName));

    var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB620Task", "getBntpCd", pageSize, pageNo);
    MethodObj.setId("getBntpCd");
	MethodObj.setParam( "sStndDate", wconv.trim(sStndDate)); // 기준일자
	MethodObj.setParam( "sBntpLargClsfCd", wconv.trim(sBntpLargClsfCd)); // 업종대분류코드
	MethodObj.setParam( "sBntpDurnClsfCd", wconv.trim(sBntpDurnClsfCd)); // 업종중분류코드	
	MethodObj.setUrl("/contract/CBSC097PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("///*/vector/data/LTIBB01Bntp");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); //DataCollection에 WARING을 만들어줌(msg, level, detail, timestamp)
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Bntp"), "DsWs_BntpCd");
	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Bntp"), "Ds_BntpCd");
	//Json에 자동적으로 생성되게 해줌
	util.setCompBindRefresh("DsWs_BntpCd");//바인딩을 해줌
	util.setCompBindRefresh("Ds_BntpCd");//바인딩을 해줌
	util.setRowPosition(DsWs_BntpCd, 0);
    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
 	//에러처리
    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }
    wconv.copy(Ds_BntpCd, DsWs_BntpCd); //trace(Ds_BntpCd.SaveXML());
};

//---------------------------------------------------------------------------
// function 명 : lfn_getMaxBntpCdTask
// 내용        : Max업종 조회(01:개발원코드(화재손해업종))
// param       : sStndDate = 기준일자
//             : sBntpCdArray = 업종코드Array
//---------------------------------------------------------------------------
scwin.lfn_getMaxBntpCdTask = function(sBntpCdArray) {
    // DataSet 초기화
    wconv.clear(DsWs_BntpResult);
    // Service Object 설정
    //SOBJ_Search.WSDL = gds.gWsdlBasis;
    // 서비스 호출 메소드 정의(서비스 오브젝트, 타스크, 액션, 페이지당 사이즈, 가져올 현재 페이지)
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.ssr.pln.task.DBB620Task", "getMaxBntpCd", "", "");
//    gfn_SetParam(MethodObj, "sStndDate", wconv.trim(sStndDate)); // 기준일자
//    gfn_SetParam(MethodObj, "sBntpCdArray", sBntpCdArray); // 업종코드Array(콤마[,]로 구분함)
    //alert(trim(sStndDate) + ":::" + trim(sBntpCdArray));

    var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB620Task", "getMaxBntpCd", pageSize, pageNo);
	MethodObj.setId("getMaxBntpCd");
	MethodObj.setParam( "sStndDate", wconv.trim(sStndDate)); // 기준일자
	MethodObj.setParam( "sBntpCdArray", sBntpCdArray); // 업종코드Array(콤마[,]로 구분함)
	MethodObj.setUrl("/contract/CBSC097PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/vector/data/LTIBB01Bntp");//(dsWS의 XPath)
	MethodObj.addXPath("//WARNING"); //DataCollection에 WARING을 만들어줌(msg, level, detail, timestamp)
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Bntp"), "DsWs_BntpResult");
	util.jSonDsCreate(_tranSync.getDsJson("LTIBB01Bntp"), "Ds_BntpResult");
	util.setCompBindRefresh("DsWs_BntpResult");//바인딩을 해줌	
	util.setCompBindRefresh("Ds_BntpResult");//바인딩을 해줌	
	util.setRowPosition(DsWs_BntpResult, 0);	
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
 	//에러처리
 	    if (DsWs_Warning.getRowCount() != 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
            return;
        }
    wconv.copy(Ds_BntpResult, DsWs_BntpResult); //trace(Ds_BntpResult.SaveXML());
};

