//---------------------------------------------------------------------------
// function 명 : lfn_getBntpCdTask
// 내용        : 업종 조회(01:개발원코드(화재손해업종))
// param       : sStndDate = 기준일자
//             : sInsBntpCd = 가입업종코드
//---------------------------------------------------------------------------
scwin.lfn_dialogB610_Pop = function(col, row, id) {
    var promise = Promise.resolve();
    //var param = "sStndDate=" + fv_args.sStndDate;
    promise = promise.then(function(rtnVal) {
    		var indata = {"sStndDate":fv_args.sStndDate};
    		var popInfo = {
				"width"     : 718,
				"height"    : 469,
				"indata"    : indata,
				"title"     : "업종찾기"
			};
        return wprom.popupOpen("/ui/cbsc/CBSC072P.xml", popInfo);
    });
    promise = promise.then(function(result) {
        /*wprom.popupOpen(가이드참조)*/        
        if (result != "" && result != 0) {
            if (id == "grd_BntpCd1") {
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpLargClsfCd", result[0]); // 업종대분류코드
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpLargClsfCdName", result[1]); // 업종대분류명
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpLargClsfCd", result[2]); // 업종중분류코드
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpDurnClsfCdName", result[3]); // 업종중분류명
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpCd", result[4]); // 업종코드
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpCdName", result[5]); // 업종명
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "nRato", result[6]); // 요율
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpExpln1", result[7]); // 업종해설1
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpExpln2", result[8]); // 업종해설2
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sUdrtkStndCont1", result[9]); // 인수기준내용1
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sUdrtkStndCont2", result[10]); // 인수기준내용2
                Ds_BntpGrid1.setCellData(Ds_BntpGrid1.getRowPosition(), "sBntpEqmtUnit", result[11]); // 업종시설단위
            } else if (id == "grd_BntpCd2") {
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpLargClsfCd", result[0]); // 업종대분류코드
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpLargClsfCdName", result[1]); // 업종대분류명
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpLargClsfCd", result[2]); // 업종중분류코드
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpDurnClsfCdName", result[3]); // 업종중분류명
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpCd", result[4]); // 업종코드
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpCdName", result[5]); // 업종명
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "nRato", result[6]); // 요율
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpExpln1", result[7]); // 업종해설1
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpExpln2", result[8]); // 업종해설2
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sUdrtkStndCont1", result[9]); // 인수기준내용1
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sUdrtkStndCont2", result[10]); // 인수기준내용2
                Ds_BntpGrid2.setCellData(Ds_BntpGrid2.getRowPosition(), "sBntpEqmtUnit", result[11]); // 업종시설단위
            }
        }
        return;
    });
    return promise;
}; //========================================================//
