	scwin.cbo_gubn_onviewchange = function(info) {
		var compID = ""
		if(cbo_gubn1th.getValue() == "C"){
			compID = "section_"+cbo_gubn1th.getValue()+cbo_gubn4th.getValue();
			cbo_gubn2th.setStyle( "display","none" );
			cbo_gubn3th.setStyle( "display","none" );
			cbo_gubn4th.setStyle( "display","" );
		}else if(cbo_gubn1th.getValue() == "B"){
			compID = "section_"+cbo_gubn1th.getValue()+cbo_gubn2th.getValue();
			cbo_gubn2th.setStyle( "display","" );			
			cbo_gubn3th.setStyle( "display","none" );			
			cbo_gubn4th.setStyle( "display","none" );
		}else if(cbo_gubn1th.getValue() == "A"){
			compID = "section_"+cbo_gubn1th.getValue()+cbo_gubn3th.getValue();
			cbo_gubn2th.setStyle( "display","none" );
			cbo_gubn3th.setStyle( "display","" );	
			cbo_gubn4th.setStyle( "display","none" );		
		}		

		$("[id^='section']").css("display","none");
		$("#"+compID).css("display","block");
	};


	scwin.btn_CBSC103M_onclick = function() {
		var pro = Promise.resolve();
		pro = pro.then(function(rv) {

			//팜업창 위치
			var popInfo = {
				"width"     : 1208,                  //폭    : 생략시 기본값
				"height"    : 630,                  //높이   : 생략시 기본값  
//				"top"       : 120,                   //top   : 생략시 중심
//				"left"      : 120,                   //left  : 생략시 중심
				"indata"    : "",               //보낼 자료
				"title"     : util.getPathTitle(),                   //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
				"callback"  : scwin.popupCallback,  //팝업창 종료시 실행될 callback 함수 특별한경우가 아닌경우 사용하지 말것
													//callback 함수 내에서 wprom.alert, wprom.confirm, wprom.popupOpen 호출 금지.
			};
			return wprom.popupOpen("/ui/cbsc/CBSC103M.xml", popInfo);
		});
	};

	scwin.cbo_gubn_onviewchange1 = function(info) {
		var compID = ""

		if(cbo_gubn1th.getValue() == "C"){			
			compID = "section_"+cbo_gubn1th.getValue()+"1";			
			cbo_gubn2th.setStyle( "display","none" );
			cbo_gubn3th.setStyle( "display","none" );
			cbo_gubn4th.setStyle( "display","" );		
			cbo_gubn4th.setValue( "1" );	
		}else if(cbo_gubn1th.getValue() == "B"){
			compID = "section_"+cbo_gubn1th.getValue()+"1";			
			cbo_gubn2th.setStyle( "display","" );
			cbo_gubn2th.setValue( "1" );
			cbo_gubn3th.setStyle( "display","none" );
			cbo_gubn4th.setStyle( "display","none" );			
		}else if(cbo_gubn1th.getValue() == "A"){
			compID = "section_"+cbo_gubn1th.getValue()+"1";
			cbo_gubn2th.setStyle( "display","none" );
			cbo_gubn3th.setStyle( "display","" );
			cbo_gubn3th.setValue( "1" );			
			cbo_gubn4th.setStyle( "display","none" );			
		}
		$("[id^='section']").css("display","none");
		$("#"+compID).css("display","block");					

	};
