var sPlanNo = "";  		 // 설계/계약번호
var sGdCd = " ";			// 상품코드
var sGdCdNm = " ";	// 상품명
var sPlanProgRankCd = "";
var sSrdoIssuDate = "";	// 발행일
var sSvtYn = "";  
var tab;	//저축형인지 여부 
var sPsnGroupFlgcd = ""; 	 // 개인단체구분코드
var sToday;	//청약서발행일
var pcount = 0; 
