//---------------------------------------------------------------------------
// function 명 : lfn_OnLoadCompleted
// 내용        : 폼로드할때 처리할 내용
//---------------------------------------------------------------------------
scwin.init = function() {	
	console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_01]" );

	var pro = Promise.resolve();
	window.top.util.winTopShowModalChk = "T";
	util.winTopShowModal();

	pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>

		var indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata); 	// 부모창에서 받아온 값( 예) inJSON.sMyPlan)

		if (!util.isNull(inJSON.sPlanNo)) 	sPlanNo = inJSON.sPlanNo;		// 설계/계약번호
		if (!util.isNull(inJSON.sGdCd)) 	sGdCd = inJSON.sGdCd;				// 상품코드
		if (!util.isNull(inJSON.sGdCdNm)) 	sGdCdNm = inJSON.sGdCdNm;			// 상품명
		if (!util.isNull(inJSON.sPlanProgRankCd)) 	sPlanProgRankCd = inJSON.sPlanProgRankCd;	 
		if (!util.isNull(inJSON.sSrdoIssuDate)) 	sSrdoIssuDate = inJSON.sSrdoIssuDate; 	// 발행일
		if (!util.isNull(inJSON.sSvtYn)) 	sSvtYn = inJSON.sSvtYn;				//   
		if (!util.isNull(inJSON.tab)) 	tab = inJSON.tab;				// 저축형인지 여부
		if (!util.isNull(inJSON.sPsnGroupFlgcd)) sPsnGroupFlgcd = inJSON.sPsnGroupFlgcd;		 // 개인단체구분코드
		if (!util.isNull(inJSON.sScrnId)) sScrnId = inJSON.sScrnId;

		var pObj = wprom.getParamObject(); 
		parent_Ds_Pibo = pObj.Ds_Pibo;	// 부모창 피보험자 정보 Ds Set  
		//console.log("[CBSC128P.xml] [init()] ==> [폼 로딩] ==> [parent_Ds_Pibo_갯수_01]"+ parent_Ds_Pibo.getRowCount() +"[parent_Ds_Pibo]"+ WebSquare.xml.serialize(parent_Ds_Pibo.getAllXML()) );    	

		 sToday = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 		// global 현재일자

	    if (wconv.length(sPlanNo) == 0) {
	        wprom.alert("설계번호가 없습니다.");
	        scwin.btn_Exit_OnClick();
	        return;
	    }
	    if (wconv.length(sPlanNo) != 12) {
	        wprom.alert("설계번호가 정확하지 않습니다");
	        return;
	    }

		msk_sPlanNo.setValue(sPlanNo);  // 설계번호  
	    edt_sPsnGroupFlgcd.setValue(sPsnGroupFlgcd); 
	    edt_sGdCd.setValue(sGdCd);
	    edt_sGdCdNm.setValue(sGdCdNm);
	    sPlanProgRankCd = sPlanProgRankCd;
	    sSrdoIssuDate = sSrdoIssuDate;	// 발행일

	    var comp;  
		var compCnt = Div.getChildrenCount();		// Components 총 수   
	 	//console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_02] [sScrnId]"+ sScrnId +"[compCnt]"+ compCnt );

	    if (sScrnId == "D320") {		// 계약변경일 경우
	        for (var i = 0; i < compCnt; i++) {
	       // for (var i = 0; i < wconv.length(Div.Components); i++) {
	           // comp = Div.Components(i);
	           comp = Div.getAllChildren()[i];		// Components 담기  
	          // console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_51] [comp.id]"+ comp.id );

	            if (comp.id == "chk_Medical" || comp.id == "chk_Question") {		// 실손사전동의서, 질문서 이면
	            } else { 
	            	 eval(comp.id).setDisabled(true);   // disabel 처리 비활성화 처리  ===>
	              //  Div_eval(comp.id).Enable = false;
	            }
	        }
	    } else if (tab != "3") {
	    	//console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_03] [tab]"+ tab +"[sPlanProgRankCd]"+ sPlanProgRankCd );

	        if (wconv.toNumber(sPlanProgRankCd) != 0 && wconv.toNumber(sPlanProgRankCd) < 60 && sPlanProgRankCd != "56") { 
	          for (var i = 0; i < compCnt; i++) {
	       // for (var i = 0; i < wconv.length(Div.Components); i++) {
	                //comp = Div.Components(i);
	                comp = Div.getAllChildren()[i];		// Components 담기 

	                if (comp.id == "Div_chk_Gaip" || comp.id == "Div_chk_Hwangup" || comp.id == "Div_chk_GaipAll" || comp.id == "Div_chk_Personal" 
	                	|| comp.id == "Div_chk_Question" || comp.id == "Div_chk_CstCfst" || comp.id == "Div_chk_Medical" || comp.id == "Div_chk_PiboDetail" 
	                	|| comp.id == "Div_chk_OfseCnfm") {
	                } else {               
						// eval(comp.id).enable = false; 
						 eval(comp.id).setDisabled(true);   // disabel 처리 비활성화 처리  ===> // 청약서(chk_Chungyak), 상품설명서(chk_Goods)  비활성화 처리
	                    //console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_53_2] [비활성화 처리] [comp.id]"+ comp.id );
	                }
	            }
	        }

	        if (tab == "1" || tab == "2") {		// tab이 1또는 2 이면(tab1: 단체청약설계 )  
				Div_chk_Question.setDisabled(true);	// 질문서 체크박스 비활성화 처리  ===>
				Div_chk_Goods.setDisabled(false);		// 청약서 체크박스 활성화 처리
	          	//console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_61] [tab이 1또는 2 이면] [비활성화 처리] [comp.id]"+ comp.id ); 
	        }
	    }

	    //저축형인지
	    if (sSvtYn == "Y") {
	        Div_chk_BuDambo.visible = false;
	    }

	    // 질문서 피보험자 선택 
	    wconv.copy(Ds_Pibo, parent_Ds_Pibo);

	    if (Ds_Pibo.getTotalRow() <= 1) {
	        //cmb_Pibo.hide();

	    } else {
	        var insRow = util.insertRow(Ds_Pibo, 0);

	        Ds_Pibo.setCellData(0, "nInrpsSeqno", "0");

	        if (sPsnGroupFlgcd == "21") {
	            cmb_Pibo.DataColumn = "sGroupInrpsName";
	            Ds_Pibo.setCellData(0, "sGroupInrpsName", "전체");
	        } else {
	            cmb_Pibo.DataColumn = "sInrpsName";
	            Ds_Pibo.setCellData(0, "sInrpsName", "전체");
	        }
	        cmb_Pibo.setSelectedIndex(0);
	    } 

		return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); // =================================================>
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	//pro = pro.catch(function(rj) {
		//util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	//});

	return pro;

	//console.log("[/CBSC101P.xml] [init()] ==> [폼 로딩] [TEST_end]" );	
};

//---------------------------------------------------------------------------
// function 명 : chkEssnInpt
// 내용        : 필수입력 check
//---------------------------------------------------------------------------
scwin.chkEssnInpt = function() {

    var sCrNoLen = wconv.length(msk_sCrNo.Value);
    if (sCrNoLen == 0 && cmd_SearchStd.Value == "1") {
        wprom.alert("계약번호 를 입력하세요.");
        msk_sCrNo.SetFocus();
        //rdo_sIssu.Value = "";
        return false;
    }
    if (sCrNoLen == 0 && cmd_SearchStd.Value == "2") {
        wprom.alert("설계번호 를 입력하세요.");
        msk_sCrNo.SetFocus();
        //rdo_sIssu.Value = "";
        return false;
    }
    if (sCrNoLen < 12 && cmd_SearchStd.Value == "1") {
        wprom.alert(msk_sCrNo.Text + " 계약번호는 12자리 입니다.");
        msk_sCrNo.SetFocus();
        return false;
    }
    if (sCrNoLen < 12 && cmd_SearchStd.Value == "2") {
        wprom.alert(msk_sCrNo.Text + " 설계번호는 12자리 입니다.");
        msk_sCrNo.SetFocus();
        return false;
    }
    return true;
};

