scwin.chk_Print = function() {
	//console.log("[/CBSC101P.xml] [chk_Print()] ==> [증명서매칭] [TEST_01]");

	wconv.clear(DS_SAVESENDINFO);

	sysDate = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 		// 서버시간 
   	currentDate = wconv.substr(WebSquare.date.getCurrentServerDate("yyyyMMddHHmmss"), 2, 6); 		// global 현재시작 
    sIssID = gds.gUserID + currentDate;

	var compList = ["Div_chk_Goods","Div_chk_Chungyak","Div_chk_Question","Div_chk_Personal","Div_chk_PiboDetail" 
 				 		];

	for (var i = 0; i < compList.length; ++i) { 
   		//console.log("[/CBSC101P.xml] [chk_Print()] ==> [증명서매칭] [i_번째]"+ i +"[compList[i]]"+ compList[i] +"[Div_chk_Goods.getValue()]]"+ Div_chk_Goods.getValue() );		

       if (compList[i] == "Div_chk_Goods" && Div_chk_Goods.getValue() == "FALSE") { 	// 01. '상품설명서' 체크를 했으면
        	//console.log("[/CBSC101P.xml] [chk_Print()] ==> [증명서매칭_'상품설명서' 체크] [i_번째]"+ i +"[compList[i]]"+ compList[i] +"[Div_chk_Goods.getValue()]]"+ Div_chk_Goods.getValue() );	
            j = DS_SAVESENDINFO.insertRow(); 			
            DS_SAVESENDINFO.setCellData(j, "sFileName", "B999_Rep");
            DS_SAVESENDINFO.setCellData(j, "sOdiName", "B999_Rep");
            DS_SAVESENDINFO.setCellData(j, "sContents", "대면계약상품설명서");
