            DS_SAVESENDINFO.setCellData(j, "sGdCd", edt_sGdCd.getValue()); 
            DS_SAVESENDINFO.setCellData(j, "sDmCode", "L010");
            DS_SAVESENDINFO.setCellData(j, "sIssID", sIssID);
