             DS_SAVESENDINFO.setCellData(j, "sFileName", "B760_Rep");
            DS_SAVESENDINFO.setCellData(j, "sOdiName", "B753_Rep");
            DS_SAVESENDINFO.setCellData(j, "sContents", "대면계약청약서");
