			reportInfo.title    	 = sContents;	//팝업창 타이틀

			wprom.reportOpen(reportInfo);		 		
		} 
	}

	//console.log("[/CBSC101P.xml] [chk_Print()] ==> [증명서발급] [TEST_end]"); 
};

