scwin.fn_chkPrintView = function(obj) {
    var promise = Promise.resolve();
    //alert('fn_chkPrint'); return;
    //	if (DS_SAVESENDPRINT.GetCount() > 0){
    for (var i = 0; i < DS_SAVESENDINFO.getRowCount(); i++) {
        var sFileName = DS_SAVESENDINFO.getCellData(i, "sFileName");
        var sOdiId = DS_SAVESENDINFO.getCellData(i, "sOdiName");
        var sPlanNo = DS_SAVESENDINFO.getCellData(i, "sPlanNo");
        var sPsnGroupFlgcd = DS_SAVESENDINFO.getCellData(i, "sPsnGroupFlgcd");
        var sGdCd = DS_SAVESENDINFO.getCellData(i, "sGdCd");
        var copies = DS_SAVESENDINFO.getCellData(i, "copies");
        var sDmCode = DS_SAVESENDINFO.getCellData(i, "sDmCode");
        var sIssID = DS_SAVESENDINFO.getCellData(i, "sIssID");
        var sGubun = DS_SAVESENDINFO.getCellData(i, "sGubun"); //2016.08.16 
        var nInrpsSeqno = DS_SAVESENDINFO.getCellData(i, "nInrpsSeqno"); //2016.08.19
        if (nInrpsSeqno == null)
            nInrpsSeqno = "1";
        var strParam;
        strParam = "viewer.namespace=KW_Testozviewer\n";
        strParam += "connection.compressedform=true\n";
        strParam += "connection.servlet=" + gds.gLtiUrl + "/newoz/server\n";
        strParam += "connection.reportname=/longterm/" + sFileName + ".ozr\n"; //2016.08.16 
        //	strParam += "odi.odinames=/longterm/" + sOdiId + "\n";
        strParam += "odi.odinames=" + sOdiId + "\n";
        strParam += "information.debug=true\n";
        strParam += "viewer.isframe=false\n";
        strParam += "information.debug=true\n";
        strParam += "viewer.isframe=false\n";
        //		strParam += "viewer.isframe=true\n";
        strParam += "toolbar.all=true\n"; //툴바 감춤
        strParam += "viewer.useprogressbar=true\n"; //프로그래스바 감춤
        strParam += "print.copies=" + copies + "\n"; //인쇄매수설정
        strParam += "print.mode=silent\n"; //인쇄창 없이 바로 인쇄	
        strParam += "print.once=false\n"; //보고서인쇄를 한번만 할지를 설정
        strParam += "print.printername=DEFAULT_PRINTER\n"; //인쇄할 프린터설정
        strParam += "print.size=A4\n"; //인쇄용지크기	
        strParam += "print.mode=silent\n"; //인쇄창 없이 바로 인쇄	
        strParam += "odi." + sOdiId + ".pcount=6\n"; //2016.08.19
        strParam += "odi." + sOdiId + ".args1=sPlanNo=" + sPlanNo + "\n";
        strParam += "odi." + sOdiId + ".args2=sGdCd=" + sGdCd + "\n";
        strParam += "odi." + sOdiId + ".args3=sDmCode=" + sDmCode + "\n";
        strParam += "odi." + sOdiId + ".args4=sIssID=" + sIssID + "\n";
        strParam += "odi." + sOdiId + ".args5=sGubun=" + sGubun + "\n"; //2016.08.16
        strParam += "odi." + sOdiId + ".args6=nInrpsSeqno=" + nInrpsSeqno + "\n"; //2016.08.19
        var arg = "strParam =" + wconv.quote(strParam);
        promise = promise.then(function(rtnVal) {
            return wprom.promise.popupOpen("PROJECT_PATH_TEMP/ssr_out/B501.xml", arg, 928, 688); /*wprom.popupOpen(가이드참조)*/
        });
    }
    return promise;
};

//*************************************
//  oz 컴포넌트  동적으로 binding
//*************************************
scwin.lfn_beforOz = function(awForm, strParam) {
    /*if(chk_test.Value == "1" ){
    	alert(strParam);
    	return;
    }*/
    var oOz = null;
    var oZtransfer = null;

    //마우스 커서 Wait상태설정
    SetWaitCursor(true);
    SetCapture();

    //ZTransferX 컨트롤 생성
    oOz = find("EXOZViewer40_OCX");
    oZtransfer = find("ZtransferX_OCX");
    if (oOz != null) {
        Destroy("EXOZViewer40_OCX");
        oOz = null;
    }
    if (oZtransfer != null) {
        Destroy("ZtransferX_OCX");
        oZtransfer = null;
    }

    //ZTransferX 컨트롤 생성
    Create("ZTransferX", "ZtransferX_OCX", "left=\"0\" top=\"0\" width=\"0\" height=\"0\"");
    oZtransfer = find("ZtransferX_OCX");
    ZtransferX_OCX.InstallNamespace = "KW_Test";
    ZtransferX_OCX.InstallBase = "<PROGRAMS>/Forcs";

    //ZtransferX_OCX.InstallRegistOnly = true;
    if (indexOf(gds.gGenUrl, "http://10.1.1.129") > -1 || indexOf(gds.gGenUrl, "http://10.1.1.131") > -1) {
        ZtransferX_OCX.DownloadServer = "http://10.1.1.129/newozviewer/";
        ZtransferX_OCX.DownloadPort = "8182";
    } else {
        ZtransferX_OCX.DownloadServer = "http://10.1.1.60/newozviewer/";
        ZtransferX_OCX.DownloadPort = "8000";
    }
    ZtransferX_OCX.DownloadInstruction = "ozrviewer.idf";
    ZtransferX_OCX.StartInstall();

    //오즈 컨트롤 존재 여부 확인
    oOz = find("EXOZViewer40_OCX");

    //오즈 컨트롤 생성
    create("EXOZViewer40", "EXOZViewer40_OCX", "left=\"0\" right=\"0\" top=\"0\" width=\"0\" height=\"0\" bottom=\"0\"");
    EXOZViewer40_OCX.CreateReport(strParam);
    EXOZViewer40_OCX.OZPostCommand = "EXOZViewer400_OZPostCommand";


    //오즈 컨트롤 생성시까지 대기
    while (wconv.isEmpty(awForm)) {
        oOz = find("EXOZViewer40_OCX");
        if (oOz != null)
            break;
        Idle();
    }

    //마우스 커서 입력상태로 설정
    ReleaseCapture();
    SetWaitCursor(false);
};

//---------------------------------------------------------------------------
// function 명 : lfn_save()
// 내용        : 청약서발행후 발행일자update
//---------------------------------------------------------------------------
scwin.lfn_save = function() {
	console.log("[/CBSC101P.xml] [lfn_save()] ==> [청약서발행 후 발행일자 Up] [TEST_01]"); 

	var MethodObj = new callService("com.inswave.longterm.ssr.out.task.DBB410Task", "setPrintSave");	// 발행일자 Up 처리 Obj
	MethodObj.setId("setPrintSave");	// ID 셋팅  
	MethodObj.setParam("sPlanNo", msk_sPlanNo.getValue());	// Param 셋팅(설계번호) 
	MethodObj.setUrl("/contract/CBSC101PController.do");  			// 서버단 호출 URL  
	//MethodObj.setXPath("//*/RESULT");					// XPath(Ds_LTIDA00)
	MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
	MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]  
	MethodObj.setXPathDs();
	MethodObj.send();		// 최종 전송
	console.log("[/CBSC101P.xml] [lfn_save()] ==> [01. 발행일자 Up 처리, 전송_완료] [TEST_91]");

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)    
 	//console.log("[/CBSC014M.xml] [lfn_SearchData()] ==> [01. 개인청약사항조회, 전송_완료] [TEST_91]") 
	console.log("[/CBSC101P.xml] [lfn_save()] ==> [DsWs_Warning,_갯수]"+ DsWs_Warning.getRowCount() +"[DsWs_Warning]"+ WebSquare.xml.serialize(DsWs_Warning.getAllXML()) );  

    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }

    sSrdoIssuDate = sToday;	    //청약서발행일
};

