scwin.lfn_chk_OnClick = function(obj, strValue) {
    if (obj.id == "Div_chk_Question") {
        cmb_Pibo.setSelectedIndex(0);
        if (Div_eval(obj.ID).Value == true) {
            cmb_Pibo.setDisabled(false);
        } else {
            cmb_Pibo.setDisabled(true);
        }
    }
    if (obj.id == "Div_chk_CstCfst") {
        cmb_CstCfst.Index = 0;
        if (Div_eval(obj.ID).Value == true) {
            cmb_CstCfst.Enable = true;
        } else {
            cmb_CstCfst.Enable = false;
        }
    }
};
