};

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼 클릭 Event처리 ( 상담직원정정 조회 )
//---------------------------------------------------------------------------
scwin.lfn_Search_Async = function(e) { //완료
    util.eventAsyncCall(this, scwin.lfn_Search, arguments);
};

scwin.lfn_Search = function(e) {
    scwin.lfn_SearchData(1);
};

//---------------------------------------------------------------------------
// function 명 : lfn_SearchData
// 내용        : (계약번호로  상품,계약자,우편,E-Mail 정보등 ) 조회버튼 클릭 Event처리
//---------------------------------------------------------------------------
scwin.lfn_SearchData = function(obj) {
    wconv.clear(Ds_LTIEA00);
    wconv.clear(Ds_LTIEA00_Copy);
    wconv.clear(DsWs_LTIEA00);
//    SOBJ_DataSearch.WSDL = gds.gWsdlBasis;
//    var MethodObj = gfn_CreateMethodObj(SOBJ_DataSearch, "com.inswave.longterm.psv.ens.task.EBE114TASK", "selectCertiIssueInfo", "", "");
//
//    /*  조회기준 */
//    gfn_SetParam(MethodObj, "sSearchStd", "2"); // 조회기준 
//    gfn_SetParam(MethodObj, "sPlanNo", sPlanNo); // 설계번호 
//    gfn_CallService(SOBJ_DataSearch, DOBJ_DataSearch, MethodObj);
	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EBE114TASK", "selectCertiIssueInfo", pageSize, pageNo);
	MethodObj.setId("selectCertiIssueInfo");
	MethodObj.setParam( "sPlanNo", wconv.trim(sPlanNo));
	MethodObj.setParam( "sSearchStd", "2");  

	MethodObj.setUrl("/contract/CBSC102PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/vector/data/LTIEA00ADDR");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00ADDR"), "DsWs_LTIEA00");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

	util.setCompBindRefresh("DsWs_LTIEA00");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_Warning");//바인딩을 해줌
	util.setRowPosition(DsWs_LTIEA00, 0);


    if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
    } else {
    	util.copyM(Ds_LTIEA00_Copy,DsWs_LTIEA00.getAllJSON());
		util.setCompBindRefresh("Ds_LTIEA00_Copy");//바인딩을 해줌
		util.setRowPosition(Ds_LTIEA00_Copy, 0);
		util.copyM(Ds_LTIEA00,DsWs_LTIEA00.getAllJSON());
		util.setCompBindRefresh("Ds_LTIEA00");//바인딩을 해줌
		util.setRowPosition(Ds_LTIEA00, 0);
        // 안내양식 정보 가져오기.
        wconv.clear(DsWs_PRIAA02Warning);
        wconv.clear(DsWs_PRIAA02_List);
//        SOBJ_Get.WSDL = gds.gWsdlBasis;
//        var MethodObj = gfn_CreateMethodObj(SOBJ_Get, "com.inswave.longterm.com.com.task.AXR020Task", "selectAll", "", "");
//        gfn_CallService(SOBJ_Get, DOBJ_PRIAA02_List, MethodObj);
		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR020Task", "selectAll", pageSize, pageNo);
		MethodObj.setId("selectAll");

		MethodObj.setUrl("/contract/CBSC102PController.do");//배포후변경할것(controller위치와 이름)
		MethodObj.setXPath("//*/vector/data/PRIAA02");//(dsWS의 XPath)
		MethodObj.addXPath("//*/WARNING"); 
		MethodObj.sync();
		MethodObj.send();
		util.jSonDsCreate(_tranSync.getDsJson("PRIAA02"), "DsWs_PRIAA02_List");
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_PRIAA02Warning");

		util.setCompBindRefresh("DsWs_PRIAA02_List");//바인딩을 해줌
		util.setCompBindRefresh("DsWs_PRIAA02Warning");//바인딩을 해줌
		util.setRowPosition(DsWs_PRIAA02_List, 0);
	    if (DsWs_PRIAA02Warning.getRowCount() != 0) {
            wprom.alert(DsWs_PRIAA02Warning.getCellData(0, "msg"));
            return;
        } else {
            if (DsWs_PRIAA02_List.getRowCount() == 0) {
                wprom.alert("조건에 맞는 자료가 없습니다.");
            } else {
//                wconv.copy(Ds_PRIAA02_List, "DsWs_PRIAA02_List");
                util.copyM(Ds_PRIAA02_List,DsWs_PRIAA02_List.getAllJSON());
				util.setCompBindRefresh("Ds_PRIAA02_List");//바인딩을 해줌
				util.setRowPosition(Ds_PRIAA02_List, 0);
            }
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_edt_OnKeyDown
// 내용        : 계약번호 입력 후 엔터 이벤트
//---------------------------------------------------------------------------
scwin.lfn_edt_OnKeyDown = function(nChar) {
    if (nChar == 13) {
        scwin.lfn_Search_Async();
    }
};

