scwin.btn_send_OnClick = function(e) {
    //데이터 유효성 확인
    var validFlag = scwin.checkValid();
    if (!validFlag)
        return;
    wconv.clear(ds_Save_chk);
//    scwin.send_start();
	util.eventAsyncCall(this, scwin.send_start, arguments);
};

