scwin.send_start = function(obj) {

    if (rdo_sDmcode == "L224") {
        scwin.lfn_RenwlPrem(); //발송전 갱신보험료 입력
    }
    DS_SAVESENDINFO.removeAll(); //발송물정보 
    var insRow = util.insertRow(DS_SAVESENDINFO, "");    
    DS_SAVEEMAILINFO.removeAll(); //e-mail정보
    var insRow = util.insertRow(DS_SAVEEMAILINFO, "");

    if (chk_Email.getValue() == "1") {
        var result = scwin.setEmailInfo();
        if (!result) {
            return;
        }
    }
    DS_SAVEFAXINFO.removeAll(); //fax정보
    var insRow = util.insertRow(DS_SAVEFAXINFO, "");
    /*수작업 변환 개발자 가이드(테스트 중)
    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
    if (chk_Fax.getValue() == "1") {
        var result = scwin.setFaxInfo();
        if (!result) {
            return;
        }
    }
    var sCustTag = "  ";
    var sFaxTag = "  ";

    sFaxTag = sFaxTag + "action=" + " \"PrintProcess\" ";
    var headTag = " ";
    if (chk_Email.getValue() == "1") {
        headTag = headTag + "sEmailCh=\"Y\" ";
    } else {
        headTag = headTag + "sEmailCh=\"N\" ";
    }
    if (chk_Fax.getValue() == "1") {
        headTag = headTag + "sFaxCh=\"Y\" ";
    } else {
        headTag = headTag + "sFaxCh=\"N\" ";
    }
//    headTag = headTag + "sLoginName=" + " \"" + gds.gUserEmpName + "\" ";
	headTag = headTag + "sLoginName=" + " \"" + util.getGdsValue("gUserEmpName") + "\" ";
    headTag = headTag + "sIssuYear=" + " \"" + " " + "\" ";
    if (rdo_sDmcode == "L224") {
        headTag = headTag + "sDmCode=" + " \"" + "L224" + "\" ";
    } else {
        if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "3") {
            headTag = headTag + "sDmCode=" + " \"" + "L158" + "\" "; //재물보험 대면 가입설계서일체
        } else if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "7") {
            //연금 edit_by_sk
            headTag = headTag + "sDmCode=" + " \"" + "L187" + "\" ";
        } else {
            headTag = headTag + "sDmCode=" + " \"" + "L164" + "\" ";
        }
    }


    //alert("headTag========>>>>>"+headTag);
//    SOBJ_Save.WSDL = gds.gWsdlBasis;
//    var MethodObj = SOBJ_Save.CreateMethodObj("perform");

    //insert  XML문 Header 문구 작성
//    setHeader("hashtable" + headTag, "com.inswave.longterm.psv.ens.task.EBE114TASK", "PrintProcess");

	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EBE114TASK", "PrintProcessClip");
 	MethodObj.setId("PrintProcessClip"); 

	 if ( chk_Fax.getValue() == "1" ) {   
//	  MethodObj.addSingleData("sFaxInfo", "FAX0001", DS_SAVEFAXINFO.getID(), "action=\'PrintProcess\'");
		MethodObj.addVectorData("sFaxInfo", "FAX0001", "DS_SAVEFAXINFO:A");
	 }
	 if ( chk_Email.getValue() == "1" ){ 

//	  MethodObj.addSingleData("sEmailInfo", "EMLC000", "DS_SAVEEMAILINFO:A", "action=\"sendEmail\""); 
		MethodObj.addVectorData("sEmailInfo", "EMLC000", "DS_SAVEEMAILINFO:A");
	 } 
    //trace("111111=======post===="+DS_SAVESENDINFO.SaveXML());
    //trace("222222=======fax===="+Ds_SAVEFAXINFO.SaveXML());
    //trace("333333=======mail===="+DS_SAVEEMAILINFO.SaveXML());b
//    setTail("hashtable");
//    SOBJ_Save.CreateObj("in0");
//    MethodObj.in0 = insertStr;
//    gfn_CallService(SOBJ_Save, DOBJ_Save, MethodObj);
	MethodObj.setUrl("/contract/CBSC102PController.do");   // 서버단 호출 URL (수납 확정)    
 	MethodObj.setXPath("//*/WARNING"); // XPath
 	MethodObj.addXPath("//*/RESULT"); // XPath
 	MethodObj.setHeaderParam(headTag);
 	MethodObj.setHashTable();    //  전문의 앞이 hashtable로 보낼 때
 	MethodObj.sync();     // 동기 처리[기본(async:비동기)]
 	MethodObj.send();      // 최종 전송   // TEST @@@ ===>
 	 util.interfaceKey(_tranSync.result.serviceDs); 
 	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "Ds_SAVEFAXINFO_SaveWarning");	
	util.setCompBindRefresh("Ds_SAVEFAXINFO_SaveWarning");//바인딩을 해줌
	util.jSonDsCreate(_tranSync.getDsJson("RESULT"), "ds_Save_chk");	
	util.setCompBindRefresh("ds_Save_chk");//바인딩을 해줌

	console.log("ds_Save_chk 값=======================>\n"
		+ds_Save_chk.getAllData());

    if (Ds_SAVEFAXINFO_SaveWarning.getRowCount() != 0) {
        wprom.alert(Ds_SAVEFAXINFO_SaveWarning.getCellData(0, "msg"));
        return;
    }

    var message = "";
    if (chk_Fax.getValue() == "1") {
        if (ds_Save_chk.getCellData(0, "faxFlag") == "1") {
            message += "팩스발송이 요청되었습니다.\n";
        } else {
            message += "팩스발송중 오류가 발생 했습니다.\n";
        }
    } 
    if (chk_Email.getValue() == "1") {
        if (ds_Save_chk.getCellData(0, "mailFlag") == "1") {
            message += "메일발송이 요청되었습니다.";
        } else {
            message += "메일발송중 오류가 발생 했습니다.";
        }
    }
    wprom.alert(message);
};

// 이메일 주소 선택시 데이터 셋팅.
scwin.lfn_eMail_OnChanged = function() {
    eMailDomainAppend(edt_sEmail1.getValue(), cmb_Email.getValue());
//    if (wconv.mid(cmb_Email.getValue(), 0, 1) == "@") {
//        edt_sEmail1.setValue(wconv.mid(cmb_Email.getValue(), 1, wconv.length(cmb_Email.getValue())));
//    }
//wconv.mid(cmb_Email.getValue(), 0, 1) == "@"
    if (wconv.substr(cmb_Email.getValue(), 0, 1) == "@") {
//    edt_sEmail1.setValue(wconv.mid(cmb_Email.getValue(), 1, wconv.length(cmb_Email.getValue())));
		edt_sEmail1.setValue(wconv.substr(cmb_Email.getValue(), 1, wconv.length(cmb_Email.getValue())));
	    edt_sEmail1.setDisabled(false);
	    edt_sEmail.setDisabled(false);
    }else{
	    edt_sEmail1.setValue(cmb_Email.getValue());
//    edt_sEmail1.setDisabled(true);
//    edt_sEmail.setDisabled(true);
    }

};

//---------------------------------------------------------------------------
// function 명 : Div_common_OnChanged
// 내용        : Div  안에 모든 Radio  버튼 제어.
//---------------------------------------------------------------------------
scwin.Div_common_OnChanged = function(info) {
//    rdo_sDmcode = obj.CodeColumn;
var keystr = Object.keys(info);
rdo_sDmcode = info[keystr[1]];

if(rdo_sDmcode=="L006"){
	rdo_Select.setValue( "");
}else{
	rdo_L006.setValue( "" );
}
    // 우편발송 , 이메일 , 팩스 제어.
    scwin.OnPrintControl();
};

//---------------------------------------------------------------------------
// function 명 : OnPrintControl
// 내용        : 안내문의 종류에 따라 [우편발송 . 이메일 , 팩스]  Componunt Event처리
//---------------------------------------------------------------------------
scwin.OnPrintControl = function() {
    gfn_getServerTime();
    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간
//    crntYMD = wconv.mid(sysDate, 0, 8);
	crntYMD = wconv.substr(sysDate, 0, 8);

    //chk_Post.Value = false;
    chk_Email.setValue(false);
    chk_Fax.setValue(false);
    var sDmTpCd1;
    var sDmTpCd2;
    var sDmTpCd3;

    //trace(rdo_sDmcode);
    // 라디오버튼 클릭시 우편, 이메일 ,팩스 콤보박스 제어.
//    wconv.filter(Ds_PRIAA02_List, "sDmCode==" + wconv.quote(rdo_sDmcode));
	wconv.filter(Ds_PRIAA02_List, "sDmCode==" + "'"+rdo_sDmcode+"'" , true);

    //trace(Ds_PRIAA02_List.SaveXML());    		
    sDmTpCd1 = Ds_PRIAA02_List.getCellData(0, "sDmTpCd1");
    sDmTpCd2 = Ds_PRIAA02_List.getCellData(0, "sDmTpCd2");
    sDmTpCd3 = Ds_PRIAA02_List.getCellData(0, "sDmTpCd3");
    Ds_PRIAA02_List.removeColumnFilterAll();
    if (rdo_sDmcode == "L001" && cmd_SearchStd.getValue() == "3") {
        chk_Email.setValue(false);
        chk_Fax.setValue(false);
        cmb_DmMethod.Enable = false;
        // Fax
        chk_Fax.setDisabled(false);
        edt_fax1.setDisabled(false);
        edt_fax2.setDisabled(false);
        edt_fax3.setDisabled(false);
        edt_ContName.setDisabled(false);
        if (rdo_sDmcode == "L001" && crntYMD <= "20120401") {
            chk_Email.setDisabled(true);
            edt_sEmail.setDisabled(true);
            edt_sEmail1.setDisabled(true);
            cmb_Email.setDisabled(true);
        } else {
            chk_Email.setDisabled(false);
            edt_sEmail.setDisabled(false);
            edt_sEmail1.setDisabled(false);
            cmb_Email.setDisabled(false);
        }
        return;
    } else {
        chk_Email.setValue(false);
        chk_Fax.setValue(false);
        // Fax
        chk_Fax.setDisabled(true);
        edt_fax1.setDisabled(true);
        edt_fax2.setDisabled(true);
        edt_fax3.setDisabled(true);
        edt_ContName.setDisabled(true);
        chk_Email.setDisabled(true);
        edt_sEmail.setDisabled(true);
        edt_sEmail1.setDisabled(true);
        cmb_Email.setDisabled(true);
    }
    if (sDmTpCd1 == "2" && sDmTpCd2 == "2" && sDmTpCd3 == "2") //   우편발송 , E-MAIL ,  FAX 
    {
        // E-Mail
        chk_Email.setDisabled(false);
        edt_sEmail.setDisabled(false);
        edt_sEmail1.setDisabled(false);
        cmb_Email.setDisabled(false);
        // Fax
        chk_Fax.setDisabled(false);
        if (gds.gProdYN == "Y") {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        } else {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        }
        edt_ContName.setDisabled(false);
    } else if (sDmTpCd1 == "2" && sDmTpCd2 == "2") //   우편발송 , E-MAIL
    {
        chk_Fax.setValue(false);
        // E-Mail
        chk_Email.setDisabled(false);
        edt_sEmail.setDisabled(false);
        edt_sEmail1.setDisabled(false);
        cmb_Email.setDisabled(false);
        // Fax
        chk_Fax.setDisabled(true);
        edt_fax1.setDisabled(true);
        edt_fax2.setDisabled(true);
        edt_fax3.setDisabled(true);
        edt_ContName.setDisabled(true);
    } else if (sDmTpCd2 == "2" && sDmTpCd3 == "2") //   E-MAIL ,  FAX 
    {
        // E-Mail
        chk_Email.setDisabled(false);
        edt_sEmail.setDisabled(false);
        edt_sEmail1.setDisabled(false);
        cmb_Email.setDisabled(false);
        // Fax
        chk_Fax.setDisabled(false);
        if (gds.gProdYN == "Y") {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        } else {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        }
        edt_ContName.setDisabled(false);
    } else if (sDmTpCd1 == "2") //   우편발송 
    {
        chk_Email.setValue(false);
        chk_Fax.setValue(false);
        // E-Mail
        chk_Email.setDisabled(true);
        edt_sEmail.setDisabled(true);
        edt_sEmail1.setDisabled(true);
        cmb_Email.setDisabled(true);
        // Fax
        chk_Fax.setDisabled(true);
        edt_fax1.setDisabled(true);
        edt_fax2.setDisabled(true);
        edt_fax3.setDisabled(true);
        edt_ContName.setDisabled(true);
    } else if (sDmTpCd3 == "2") // fax
    {
        chk_Email.setValue(false);
        // E-Mail
        chk_Email.setDisabled(true);
        edt_sEmail.setDisabled(true);
        edt_sEmail1.setDisabled(true);
        cmb_Email.setDisabled(true);
        // Fax
        chk_Fax.setDisabled(false);
        if (gds.gProdYN == "Y") {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        } else {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        }
        edt_ContName.setDisabled(false);
    } else if (sDmTpCd2 == "2") //   E-MAIL
    {
        // E-Mail
        chk_Email.setDisabled(false);
        edt_sEmail.setDisabled(false);
        edt_sEmail1.setDisabled(false);
        cmb_Email.setDisabled(false);
        chk_Fax.setDisabled(false);
        edt_fax1.setDisabled(true);
        edt_fax2.setDisabled(true);
        edt_fax3.setDisabled(true);
        edt_ContName.setDisabled(true);
    } else if (rdo_sDmcode == "LL01" || rdo_sDmcode == "LL02") // 증권팩
    {
        chk_Email.setValue(false);
        chk_Fax.setValue(false);
        // E-Mail
        chk_Email.setDisabled(true);
        edt_sEmail.setDisabled(true);
        edt_sEmail1.setDisabled(true);
        cmb_Email.setDisabled(true);
        // Fax
        chk_Fax.setDisabled(true);
        edt_fax1.setDisabled(true);
        edt_fax2.setDisabled(true);
        edt_fax3.setDisabled(true);
        edt_ContName.setDisabled(true);
        if (rdo_sDmcode == "LL02") {
            // E-Mail
            chk_Email.setDisabled(false);
            edt_sEmail.setDisabled(false);
            edt_sEmail1.setDisabled(false);
            cmb_Email.setDisabled(false);
        }
    } else if (rdo_sDmcode == "LL04") // 변경팩
    {
        chk_Email.setValue(false);
        chk_Fax.setValue(false);
        // E-Mail
        chk_Email.setDisabled(true);
        edt_sEmail.setDisabled(true);
        edt_sEmail1.setDisabled(true);
        cmb_Email.setDisabled(true);
        // Fax
        chk_Fax.setDisabled(false);
        edt_fax1.setDisabled(false);
        edt_fax2.setDisabled(false);
        edt_fax3.setDisabled(false);
        edt_ContName.setDisabled(false);
    }



    //   우편발송 , FAX 			
    if (sDmTpCd1 == "2" && sDmTpCd2 == "1" && sDmTpCd3 == "2") {
        chk_Email.setValue(false);
        // E-Mail
        chk_Email.setDisabled(true);
        edt_sEmail.setDisabled(true);
        edt_sEmail1.setDisabled(true);
        cmb_Email.setDisabled(true);
        // Fax
        chk_Fax.setDisabled(false);
        if (util.getGdsValue("gProdYN") == "Y") {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        } else {
            edt_fax1.setDisabled(false);
            edt_fax2.setDisabled(false);
            edt_fax3.setDisabled(false);
        }
        edt_ContName.setDisabled(false);
    }


    //   E-Mail
    if (sDmTpCd1 == "1" && sDmTpCd2 == "2" && sDmTpCd3 == "1") {
        chk_Email.setValue(false);
        // E-Mail
        chk_Email.setDisabled(false);
        edt_sEmail.setDisabled(false);
        edt_sEmail1.setDisabled(false);
        cmb_Email.setDisabled(false);
        // Fax
        chk_Fax.setDisabled(true);
        edt_fax1.setDisabled(true);
        edt_fax2.setDisabled(true);
        edt_fax3.setDisabled(true);
        edt_ContName.setDisabled(true);
    }
    sDmTpCd1 = "";
    sDmTpCd2 = "";
    sDmTpCd3 = "";
};

