scwin.chk_Print = function(obj) {

    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간
//    currentDate = wconv.mid(sysDate, 2, 6);
	currentDate = wconv.substr(sysDate, 2, 6);
//    sIssID = gds.gUserID + currentDate;
	sIssID = util.getGdsValue("gUserID") + currentDate;
    scwin.lfn_getCrInfo(sPlanNo, "1");
    DS_SAVESENDPRINT.removeAll();
    var j;
    if (rdo_sDmcode == "L164") {
        util.appendRow(DS_SAVESENDPRINT);
        j = DS_SAVESENDPRINT.getRowPosition();
        if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "3") {
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "B870_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sOdiName", "B870_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sContents", "재물보험 대면 가입설계서일체");
            DS_SAVESENDPRINT.setCellData(j, "sDmCode", "L158");
        } else if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "7") {
            //연금 edit_by_sk
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "C400_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sOdiName", "C400_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sContents", "가입설계서일체");
            DS_SAVESENDPRINT.setCellData(j, "sDmCode", "L187");
        } else {
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "B400_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sOdiName", "B400_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sContents", "가입설계서일체");
            DS_SAVESENDPRINT.setCellData(j, "sDmCode", "L164");
        }
        DS_SAVESENDPRINT.setCellData(j, "sPlanNo", sPlanNo);
        DS_SAVESENDPRINT.setCellData(j, "sIssID", sIssID);
        DS_SAVESENDPRINT.setCellData(j, "sGdCd", DsWs_getCrInfo.getCellData(0, "sGdCd"));
        DS_SAVESENDPRINT.setCellData(j, "pCount", 3);
        DS_SAVESENDPRINT.setCellData(j, "copies", 1); //lfn_save();
        //대면 가입설계동의서
    } else if (rdo_sDmcode == "L140") {
        util.appendRow(DS_SAVESENDPRINT);
        j = DS_SAVESENDPRINT.getRowPosition();
        DS_SAVESENDPRINT.setCellData(j, "sFileName", "B741_Rep");
        DS_SAVESENDPRINT.setCellData(j, "sOdiName", "B741_Rep");
        DS_SAVESENDPRINT.setCellData(j, "sContents", "가입설계동의서");
        DS_SAVESENDPRINT.setCellData(j, "sDmCode", "L140");
        DS_SAVESENDPRINT.setCellData(j, "sPlanNo", sPlanNo);
        DS_SAVESENDPRINT.setCellData(j, "sIssID", sIssID);
        DS_SAVESENDPRINT.setCellData(j, "pCount", 3);
        DS_SAVESENDPRINT.setCellData(j, "copies", 1); //lfn_save();  동의서 출력은 청약서 출력일자 업데이트 안함.
        //대면 알릴의무
    } else if (rdo_sDmcode == "L006") {
        if (wconv.substr(DsWs_getCrInfo.getCellData(0, "sGdCd"), 0, 2) == "21") {
            util.appendRow(DS_SAVESENDPRINT);
            j = DS_SAVESENDPRINT.getRowPosition();
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "B744_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sOdiName", "B744_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sContents", "알릴의무");
            DS_SAVESENDPRINT.setCellData(j, "sPlanNo", sPlanNo);
            DS_SAVESENDPRINT.setCellData(j, "sDmCode", "L141");
            DS_SAVESENDPRINT.setCellData(j, "sIssID", sIssID);
            DS_SAVESENDPRINT.setCellData(j, "pCount", 3);
            DS_SAVESENDPRINT.setCellData(j, "copies", 1); //lfn_save();
        } else {
            util.appendRow(DS_SAVESENDPRINT);
            j = DS_SAVESENDPRINT.getRowPosition();
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "B680_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sOdiName", "B680_Rep");
            DS_SAVESENDPRINT.setCellData(j, "sContents", "알릴의무");
            DS_SAVESENDPRINT.setCellData(j, "sPlanNo", sPlanNo);
            DS_SAVESENDPRINT.setCellData(j, "sDmCode", "L006");
            DS_SAVESENDPRINT.setCellData(j, "sIssID", sIssID);
            DS_SAVESENDPRINT.setCellData(j, "pCount", 3);
            DS_SAVESENDPRINT.setCellData(j, "copies", 1); //lfn_save();
        }
    } else if (rdo_sDmcode == "L224") {
        //상품설명서
        util.appendRow(DS_SAVESENDPRINT);
        j = DS_SAVESENDPRINT.getRowPosition();
        if (DsWs_getCrInfo.getCellData(0, "sInsurStrtdate") < sStdDate201701) {
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "B999_Rep_20170101");
        } else {
            DS_SAVESENDPRINT.setCellData(j, "sFileName", "B999_Rep");
        }
        DS_SAVESENDPRINT.setCellData(j, "sOdiName", "B999_Rep");
