        DS_SAVESENDPRINT.setCellData(j, "sPlanNo", sPlanNo);
        DS_SAVESENDPRINT.setCellData(j, "sIssID", sIssID);
        DS_SAVESENDPRINT.setCellData(j, "pCount", 4);
        DS_SAVESENDPRINT.setCellData(j, "copies", 1);
    }


    //trace("1111111111111111111111111111===>"+DS_SAVESENDPRINT.SaveXML());
    return true;
};

//---------------------------------------------------------------------------
// function 명 : lfn_save()
// 내용        : 청약서발행후 발행일자update
//---------------------------------------------------------------------------
scwin.lfn_save = function() {
    //데이터셋 초기화
//    SOBJ_Save.WSDL = gds.gWsdlBasis; //기본 웹서비스
//    DsWs_Warning.DataObject = "DOBJ_Save";
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Save, "com.inswave.longterm.ssr.out.task.DBB410Task", "setPrintSave", "", "");
//    gfn_SetParam(MethodObj, "sPlanNo", sPlanNo);
//    var result = gfn_CallService(SOBJ_Save, DOBJ_Save, MethodObj);
	var MethodObj = new callService("com.inswave.longterm.ssr.out.task.DBB410Task", "setPrintSave", 0, 0);
	MethodObj.setId("setPrintSave");
	MethodObj.setParam("sPlanNo", sPlanNo);
	MethodObj.setUrl("/csbc/CBSC102MController.do");
	MethodObj.setXPath("//*/RESULT");
	MethodObj.addXPath("//*/WARNING");	
	MethodObj.sync();
	MethodObj.send();    
	util.interfaceKey(_tranSync.result.serviceDs);	
	ds_Save_chk.setJSON (_tranSync.getDsJson("RESULT")); // dataset 데이터
	ds_Wanning.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터
	Ds_SAVEFAXINFO_SaveWarning.setJSON (_tranSync.getDsJson("WARNING")); // SchemaInfo="DataObject:DOBJ_AddUser" ★확인필요

    //trace("DOBJ_Save.Value ==> " + DOBJ_Save.Value);
    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }


    //청약서발행일
    sSrdoIssuDate = sToday;
};

//  발송 버튼 이벤트시 유효성검사.
scwin.checkValid = function() {
    if (chk_Email.getValue() != "1" && chk_Fax.getValue() != "1") {
        wprom.alert("Email or Fax 발송 종류를 선택하십시오.");
        return false;
    }
    if (chk_Email.getValue() == "1") {
        if (wconv.length(edt_sEmail.getValue()) == 0) {
            wprom.alert("Email 주소를 입력하십시오.");
            return false;
        }
        if (wconv.length(edt_sEmail1.getValue()) == 0) {
            wprom.alert("Email 주소를 선택 하십시오.");
            return false;
        }
    }
    if (chk_Fax.getValue() == "1") {
        if (wconv.length(edt_fax1.getValue()) == 0) {
            wprom.alert("Fax 정보를 정확히 입력하십시오.");
            return false;
        }
        if (wconv.length(edt_fax2.getValue()) == 0) {
            wprom.alert("Fax 정보를 정확히 입력하십시오.");
            return false;
        }
        if (wconv.length(edt_fax3.getValue()) == 0) {
            wprom.alert("Fax 정보를 정확히 입력하십시오.");
            return false;
        }
    }
    return true;
};

//  E-Mail 정보 셋팅.
scwin.setEmailInfo = function() {

    //trace("=======================================================================>>> GdNm : " + Ds_LTIEA00.GetColumn(0, "GdNm") );
    var sEmail = wconv.trim(edt_sEmail.getValue()) + "@" + wconv.trim(edt_sEmail1.getValue());
    if (wconv.length(sEmail) < 3) {
        wprom.alert("E-mail을 입력하세요");
        return false;
    }
	wconv.clear(DS_SAVEEMAILINFO); 
	util.appendRow(DS_SAVEEMAILINFO); 

    //    var filePath   = "/home/tmax/jeus/webhome/servlet_home/webapps/printing/HTMLdone/";
    var filePath = "/home/tmax/jeus/webhome/servlet_home/webapps/printing/HTMLdone/longterm/";
    var fromName = "더케이손해보험";
    var fromEmail = "master@educar.co.kr";

    DS_SAVEEMAILINFO.setCellData(0, "sBizCode", sPlanNo);
    DS_SAVEEMAILINFO.setCellData(0, "nChgNum", "");
    DS_SAVEEMAILINFO.setCellData(0, "sCustNo", ""); //Ds_LTIEA00.GetColumn(0, "sCrtorCd")  );

    if (rdo_sDmcode == "L224") {
        DS_SAVEEMAILINFO.setCellData(0, "sDmCode", rdo_sDmcode);
        DS_SAVEEMAILINFO.setCellData(0, "sCustNo", parent.Ds_Pibo.getCellData(wconv.findRow(parent.Ds_Pibo, "sel", "1"), "sInrpsCd"));
    } else {
        if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "7") {
            //연금 edit_by_sk
            DS_SAVEEMAILINFO.setCellData(0, "sDmCode", "L187");
        } else {
