				+"sBizCode:"+DS_SAVEEMAILINFO.getCellData(0,"sBizCode")+"\n"
				+"sType:"+DS_SAVEEMAILINFO.getCellData(0,"sType")+"\n"
				+"sCustNo:"+DS_SAVEEMAILINFO.getCellData(0,"sCustNo")+"\n"
				);

    return true;
};

// Fax 정보 셋팅.
scwin.setFaxInfo = function() {

//    scwin.lfn_getCrInfo(sPlanNo, "1");
	wconv.clear(DS_SAVEFAXINFO); 
	util.appendRow(DS_SAVEFAXINFO); 
    var sFax1 = wconv.trim(edt_fax1.getValue());
    var sFax2 = wconv.trim(edt_fax2.getValue());
    var sFax3 = wconv.trim(edt_fax3.getValue());
    DS_SAVEFAXINFO.setCellData(0, "nChgNum", ""); // 계약변경번호
    DS_SAVEFAXINFO.setCellData(0, "sBizCode", sPlanNo); // 계약번호 
    DS_SAVEFAXINFO.setCellData(0, "sIssuYear", ""); // 대상년도 정보
    DS_SAVEFAXINFO.setCellData(0, "sType", "1");
    DS_SAVEFAXINFO.setCellData(0, "sCustNo", ""); //Ds_LTIEA00.GetColumn(0, "sCrtorCd") ); // 계약자코드
    DS_SAVEFAXINFO.setCellData(0, "nSeqNo", "0");
    DS_SAVEFAXINFO.setCellData(0, "sGdCd", DsWs_getCrInfo.getCellData(0, "sGdCd")); //Ds_LTIEA00.GetColumn(0, "sGdCd") ); // 상품코드	
    if (rdo_sDmcode == "L006") {
        //갱신형 상품	
        if (wconv.substr(DsWs_getCrInfo.getCellData(0, "sGdCd"), 0, 2) == "21") {
            DS_SAVEFAXINFO.setCellData(0, "sDmCode", "L141");
        } else {
            DS_SAVEFAXINFO.setCellData(0, "sDmCode", rdo_sDmcode);
        }
    } else if (rdo_sDmcode == "L164") {
        if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "3") {
            DS_SAVEFAXINFO.setCellData(0, "sDmCode", "L158");
        } else if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "7") {
            //연금 edit_by_sk
            DS_SAVEFAXINFO.setCellData(0, "sDmCode", "L187");
        } else {
            DS_SAVEFAXINFO.setCellData(0, "sDmCode", rdo_sDmcode);
        }
    } else {
        DS_SAVEFAXINFO.setCellData(0, "sDmCode", rdo_sDmcode);
    }
    DS_SAVEFAXINFO.setCellData(0, "sFax1", sFax1); // 팩스번호
    DS_SAVEFAXINFO.setCellData(0, "sFax2", sFax2);
    DS_SAVEFAXINFO.setCellData(0, "sFax3", sFax3);
    DS_SAVEFAXINFO.setCellData(0, "sStatus", "0");
    DS_SAVEFAXINFO.setCellData(0, "sRequiredDate", "0");
//    DS_SAVEFAXINFO.setCellData(0, "sUserID", gds.gUserID);
//    DS_SAVEFAXINFO.setCellData(0, "sUserName", gds.gUserEmpName);
//    DS_SAVEFAXINFO.setCellData(0, "sLoginName", gds.gUserEmpName);
//    DS_SAVEFAXINFO.setCellData(0, "sDepartName", gds.gUserDepartName); // gUserDepartCode
	DS_SAVEFAXINFO.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
    DS_SAVEFAXINFO.setCellData(0, "sUserName", util.getGdsValue("gUserEmpName"));
    DS_SAVEFAXINFO.setCellData(0, "sLoginName", util.getGdsValue("gUserEmpName"));
    DS_SAVEFAXINFO.setCellData(0, "sDepartName", util.getGdsValue("gUserDepartName")); // gUserDepartCode
    DS_SAVEFAXINFO.setCellData(0, "sCrNo", ""); //Ds_LTIEA00.GetColumn(0, "sCrNo") ); // 계약번호	
    DS_SAVEFAXINFO.setCellData(0, "sPlanNo", sPlanNo); // 설계번호
    DS_SAVEFAXINFO.setCellData(0, "sPsnGroupFlgcd", DsWs_getCrInfo.getCellData(0, "sPsnGroupFlgcd")); //Ds_LTIEA00.GetColumn(0, "sPsnGroupFlgcd") ); // 개인단체구분코드	
//    DS_SAVEFAXINFO.setCellData(0, "imgURL", gds.gLtiUrl);
	DS_SAVEFAXINFO.setCellData(0, "imgURL",  util.getGdsValue("gLtiUrl"));
    DS_SAVEFAXINFO.setCellData(0, "sContName", edt_ContName.getValue());
    DS_SAVEFAXINFO.setCellData(0, "nBussPrem", DsWs_getCrInfo.getCellData(0, "nBussPrem")); //Ds_LTIEA00.GetColumn(0, "nBussPrem") );		
//    DS_SAVEFAXINFO.setCellData(0, "sApplyType", wconv.mid(sPlanNo, 0, 1)); // 계약번호 
//    DS_SAVEFAXINFO.setCellData(0, "sApplyKind", wconv.mid(sPlanNo, 1, 4));
//    DS_SAVEFAXINFO.setCellData(0, "sApplySer", wconv.mid(sPlanNo, 5, 7));
DS_SAVEFAXINFO.setCellData(0, "sApplyType", wconv.substr(sPlanNo, 0, 1)); // 계약번호 
    DS_SAVEFAXINFO.setCellData(0, "sApplyKind", wconv.substr(sPlanNo, 1, 4));
    DS_SAVEFAXINFO.setCellData(0, "sApplySer", wconv.substr(sPlanNo, 5, 7));
    DS_SAVEFAXINFO.setCellData(0, "nEndorseNo", ""); // 계약변경번호 
    // 가입설계서일체
    if (rdo_sDmcode == "L164") {
        if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "3") {
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "B870_Rep");
            DS_SAVEFAXINFO.setCellData(0, "sContents", "재물보험 대면 가입설계서일체");
        } else if (DsWs_getCrInfo.getCellData(0, "sBunsMetdDocuClsfCd") == "7") {
            //연금 edit_by_sk
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "C400_Rep");
            DS_SAVEFAXINFO.setCellData(0, "sContents", "가입설계서일체");
        } else {
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "B400_Rep");
            DS_SAVEFAXINFO.setCellData(0, "sContents", "가입설계서일체");
        }
    } else if (rdo_sDmcode == "L140") {
        DS_SAVEFAXINFO.setCellData(0, "sFileName", "B741_Rep");
        DS_SAVEFAXINFO.setCellData(0, "sContents", "가입설계동의서"); //대면 알릴의무
    } else if (rdo_sDmcode == "L006") {
        if (wconv.substr(DsWs_getCrInfo.getCellData(0, "sGdCd"), 0, 2) == "21") {
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "B744_Rep");
            DS_SAVEFAXINFO.setCellData(0, "sContents", "알릴의무"); //DS_SAVEFAXINFO.SetColumn( 0  , "copies", 2);//인쇄매수설정
        } else {
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "B680_Rep");
            DS_SAVEFAXINFO.setCellData(0, "sContents", "알릴의무"); //DS_SAVEFAXINFO.SetColumn( 0  , "copies", 2);//인쇄매수설정
        }
    } else if (rdo_sDmcode == "L224") {
        if (DsWs_getCrInfo.getCellData(0, "sInsurStrtdate") < sStdDate201701) {
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "B999_Rep_20170101");
        } else {
            DS_SAVEFAXINFO.setCellData(0, "sFileName", "B999_Rep");
        }
        DS_SAVEFAXINFO.setCellData(0, "sContents", "상품설명서"); //대면 알릴의무
    }

    console.log("Fax =================>\n"
    		+"sContents:"+DS_SAVEFAXINFO.getCellData(0, "sContents")+"\n"
    		+"sFileName:"+DS_SAVEFAXINFO.getCellData(0, "sFileName")+"\n"
    		+"sDmCode:"+DS_SAVEFAXINFO.getCellData(0, "sDmCode")+"\n"
    	//	+"sDmCode:"+DS_SAVEEMAILINFO.getCellData(0, "sDmCode")+"\n"
    );
    //trace("FAXINFO==>222222222222===>"+DS_SAVEFAXINFO.SaveXML());


	var repoService = scwin.getRepoServiceNm(DS_SAVEFAXINFO.getCellData(0, "sFileName")).split("^");

	taskValue = repoService[0];
	actionValue = repoService[1];

	DS_SAVEFAXINFO.setCellData( 0 , "sTaskValue" ,  taskValue );
	DS_SAVEFAXINFO.setCellData( 0 , "sActionValue" , actionValue );
	DS_SAVEFAXINFO.setCellData( 0 , "sRptName" ,  "longterm/"+ DS_SAVEFAXINFO.getCellData(0, "sFileName"));//오즈명
	    console.log("Fax =================>\n"
    		+"sContents:"+DS_SAVEFAXINFO.getCellData(0, "sContents")+"\n"
    		+"sFileName:"+DS_SAVEFAXINFO.getCellData(0, "sFileName")+"\n"
    		+"sDmCode:"+DS_SAVEFAXINFO.getCellData(0, "sDmCode")+"\n"
    		+"sTaskValue:"+DS_SAVEFAXINFO.getCellData(0, "sTaskValue")+"\n"
    		+"sActionValue:"+DS_SAVEFAXINFO.getCellData(0, "sActionValue")+"\n"
    		+"sRptName:"+DS_SAVEFAXINFO.getCellData(0, "sRptName")+"\n"
    	//	+"sDmCode:"+DS_SAVEEMAILINFO.getCellData(0, "sDmCode")+"\n"
    );
    return true;
};

